	 /**
     * 화면 초기화
     */	
	scwin.initPage = function(param){	
		if(com.getLoginInfo('roleCode').indexOf("1010001") > 0){
			cbx_mgrYn.setStyle("display", "");
		}
		
		 // dataMap 초기화
		com.initDataMap(dma_search);	
		com.initDataMap(dma_result);	
		
		//엔터키 이벤트
	   	com.setEnterKeyEvent(grp_reqEmpNo, scwin.btn_searchEmp_onclick);				//신청자
		com.setEnterKeyEvent(grp_accnt, scwin.btn_searchAccntNo_onclick);				//계정번호
		com.setEnterKeyEvent(grp_reschEmpNo, scwin.btn_searchReschEmp_onclick);	//기술이전 책임자
		com.setEnterKeyEvent(grp_adminWrkRspn, scwin.btn_searchAdminWrk_onclick);	//기술이전협약 담당자 지정 - 행정담당자
		com.setEnterKeyEvent(grp_negoRspns, scwin.btn_searchNegoRspns_onclick);		//기술이전협약 담당자 지정 - 협상 담당자
		
		dma_search.set("clsf", "RAQ001");
		// 산업기술분류(대분류) 조회 - 그리드 기술적용분야
     	com.execute(sbm_selectTecBigClsf, {
			successCallback : function(e) { // 성공 콜백
     			dlt_tecBigClsf.setJSON(e.responseJSON.resIndTeccVoList);
     			
     			// 공통코드
		     	// RFD 기술구분, RFE 지적재산권분류, RFC 계약형태, RTR 실시범위, RTS 진행상태, RZB 업체규모,PFA 단위(기술사양), RAH 6T
				var codeOptions = [ { code : "RFD", compID : "rad_mouClsf"}, 		 			// 기술구분
							 					{ code : "RFE", compID : "gridSmit:indst_right_clsf"}, 	// 그리드 관련지식재산권-지식재산권구분
											    { code : "RTR", compID : "rad_trnsScop"}, 					// 기술이전조건 - 실시범위
											    { code : "RZB", compID : "gridVend:cmpy_typ"}, 		// 그리드 예상수요업체&담당자정보-업체규모 
											    { code : "PFA", compID : "gridSpec:unit_cd"},			 	// 그리드 기술자료(사양포함) - 단위
											    { code : "RFC", compID : "cbx_transTyp"}, 					//  기술이전조건 - 계약 형태
											    { code : "RFL", compID : "sbx_techPrjClsf"}, 				//  기술구분 - selectBox
											    { code : "RAH", compID : "sbx_sixTClsf"}, 					//  기술차별성(타기술대비 특징/기술특징 및 비교 우위성) - 기술적용분야
											    { code : "RZC", compID : "gridPsn:init_cd"}, 				//  그리드 참여자현황 -  대상자구분
											];
				com.setCmnCd(codeOptions, function() {
	     		
				});		
			}
		});		
		
		com.biz.checkRole({ serviceId : scwin.data.serviceId, roleCodes : com.getLoginInfo('roleCode')}, function(e) {
			scwin.data.isRoleYn = e.isRoleYn;
				if(scwin.data.reqNo != ""){
					dma_search.set("src_flag", "infoReg");
					dma_search.set("req_no", scwin.data.reqNo);
					scwin.search();
				} else {
						console.log("scwin.data.isRoleYn=11====="+scwin.data.isRoleYn);
						if (!scwin.data.isRoleYn) { 
							ibx_reqNo.setValue(com.getLoginInfo("cn"));
							ibx_reqEmpno.setValue(com.getLoginInfo("sn"));
							scwin.data.inputReqPsn = com.getLoginInfo("sn");
						}
					
					ica_calendarReqYmd.setValue(scwin.data.today);
				}
		});		
	};
	
