	scwin.search = function() {
		dma_search.set("seq", "1");
		com.execute(sbm_selectResTrsBaseInfo, {
			successCallback : function(e) {
				console.log("elData--=="+JSON.stringify(e.responseJSON.elData.resTrnsRcptInfoVo));
				//console.log("elData--smitList=="+JSON.stringify(e.responseJSON.elData.smitList));
