				console.log("src_flag===="+dma_search.get("src_flag"));
				if(dma_search.get("src_flag") == "patInfo"){
					for(var i=0; i<dlt_smitList.getRowCount(); i++){
						dlt_smitList.setCellData(i, "cud_type", "C");	//.setRowStatus(i, "C");
					}
				}
				//console.log("====="+JSON.stringify(e.responseJSON.elData.resTrnsRcptInfoVo.ind_tech_clsf));
				
				//화면 초기 과제협약서 제약 조건으로 인한 셋팅
