				//기술성숙도 셋팅- as-is 와 퍼블이 달라서 변경
				var val1 = dma_result.get("tech_grow_level");
				console.log("val1===="+val1);
				// console.log("val1==="+typeof(val1));
				/**switch(val1) {
					case "1" :
