					break;
					case "9" :
						rad_growLevel9.setSelectedIndex(0);
					break;
					default:
						break;
				}**/
				
				// 기술이전조건 - 계약형태 selectBox 
				dma_result.set("trns_typ", dma_result.get("trns_typ").replaceAll(" ", ","));
				console.log("=======scwin.data.param3 ========="+scwin.data.param3 );
				//첨부파일 세팅
				if (scwin.data.param3 >= "XAD020") {
					scwin.data.vMode = "view";
				}				
				
				var attachFileNo_A = dma_result.get("attach_file_no"); // 운전면허증사본
				var attachFileNo_B = dma_result.get("attach_file_no_2"); // 차량등록증사본	
				var attachFileNo_C  = dma_result.get("attach_file_no_3"); // 파일첨부
				var attachFileNo_D  = dma_result.get("attach_file_no_4"); // 파일첨부
				
