						btn_temp_save.setStyle("display", "none");		//.hide();							//임시저장버튼
						
					}
				}
				
			
				if(scwin.data.param3 >= "XAD010"){	//결재 상태
					btn_aprPrint.setStyle("display", "");		//.show();
				} else {
					btn_aprPrint.setStyle("display", "none");		//hide()
				}
				
				scwin.setCompo();
				console.log("smitList Cnt=="+dlt_smitList.getRowCount());
				/**0trueY
 				1falseN
				2false**/
				for (var i = 0; i < dlt_smitList.getRowCount(); i++) {
					//dlt_smitList.setCellData(i, "indst_right_clsf_nm", dlt_smitList.getCellData(i, "indst_right_clsf"));
					dlt_smitList.setCellData(i, "indst_right_clsf_nm", gridSmit.getCellDisplayData(i, "indst_right_clsf"));
					//gridSmit radio 버튼 표시
					if(gridSmit.getCellData(i, "repr_yn") == "Y"){
						gridSmit.setCellChecked( i, "grid_radio", true);
						//gridSmit.(i, "grid_radio", true);
						console.log(i+"true"+gridSmit.getCellData(i, "repr_yn"));
					}else{
						gridSmit.setCellChecked( i, "grid_radio", false);
						console.log(i+"false"+gridSmit.getCellData(i, "repr_yn"));
					}
				}
				
				for (var i = 0; i < dlt_vendList.getRowCount(); i++) {
					//gridVend.setCellData(i, "cmpy_typ_desc", gridVend.getCellDisplayData(i, "cmpy_typ"));
