				}
				if(dma_result.get("agncy_vend_yn") == "Y"){ //rad_agncyVendYn.getValue() == "Y"){
					grpAgency.setStyle("display", "");		//기술이전 중개기관
				}else{
					grpAgency.setStyle("display", "none");	
				}
				
				scwin.data.inputAdmnWrkRspns = dma_result.get("admn_wrk_rspns");
