				}**/
				
			}
		});
	};
	
	/**
	 * 엑셀다운로드 클릭 이벤트
	 */	
	scwin.btn_fileD_A_onclick = function(e) {
		if(gridSmit.getRowCount() == "0"){
			com.alert("다운로드 할 데이터가 없습니다.");
			return false;
		}

		var option = {
				    fileName : "관련 지식재산권.xls"
				  	, type : "0"
					, startRowIndex : 0
					, startColumnIndex : 0
					, removeColumns : "0,1,4,18,19"
					, useSubTotal : "false"
			};
		com.downloadGridExcel(grid_rslt, option);
	};
	
	/**
	 * 관련지식재산권 + 버튼 클릭 이벤트
	 * 출원번호 검색 팝업 호출
	 */	
	scwin.btn_smitAppAdd_onclick = function(e) {
		//var node = "flag=Y&indst_right_clsf=RFE001";
		var data = {
					indst_right_clsf : "RFE001"
		 		}
		biz.pat.pop.open( biz.pat.pop.TYPE.SMITAPP_NO, data, function(ret){
			console.log("ret======="+JSON.stringify(ret));
			var rowCnt = dlt_smitList.getRowCount();
			var cnt=0;
			for(var i=0; i<ret.length; i++){
				var rowData = ret[i];
				
				if(rowCnt == 0){
					dlt_smitList.setRowJSON(i, rowData);
					
					gridSmit.setCellChecked(i, "grid_radio", true);
						
				}else{
					//if(scwin.checkDupData(rowData)){
						
						dlt_smitList.setRowJSON(rowCnt+cnt, rowData);
						cnt++;
						console.log("cnt===="+cnt);
						for(var j=rowCnt; j<cnt; j++){
							dlt_smitList.setCellData(i, "repr_yn", "N");
						}
						dlt_smitList.setCellData(i, "repr_yn", "Y");
					//}
				}
				dlt_smitList.setCellData(i, "cud_type", "C");
			}	//end for
		});
	};
	
	/**
	 * 관련지식재산권 - 버튼 클릭 이벤트
	 */		
	scwin.btn_smitAppMinus_onclick = function(e) {
		var row = gridSmit.getFocusedRowIndex();
						
						console.log("row=="+row);
		if (row < 0) {
			com.alert("삭제할 row를 선택하세요.");
			return;
		}
						
		var status = dlt_smitList.getRowStatus(row);
		console.log("status=="+status);
						
		if (status != "V") {
			dlt_smitList.setCellData(row, "cud_type", "D");
		}
		dlt_smitList.removeRow(row);
	};
	
	/**
	 * 관련지식재산권 - 그리드 클릭 이벤트
	 */		
	scwin.gridSmit_oncellclick = function(row,col,colId) {
		console.log("row=="+row+"col=="+col+"colId=="+colId);
		scwin.onclick(row,col);
	};
	
	/**
	 * 관련지식재산권 - onCellClick 이벤트 헨들러
	 */	
	scwin.onclick = function(row,col) {
		var colName = gridSmit.getColumnID(col);
			console.log("colName==="+colName);
		var handleColumnName ="radio1";
		for (var i = 0; i < gridSmit.getRowCount(); i++) {
			//gridSmit radio 버튼 표시
			gridSmit.setCellData( i ,  'repr_yn' , 'N' );
		}
		
		if ("radio1" == colName){
		//gridSmit.setColumnDefaultValue( "repr_yn" , "N" );
		// 라디오 버튼 체크
			
			if (gridSmit.getColumnVisible( handleColumnName)){
				gridSmit.setCellChecked( row, handleColumnName, true);
				gridSmit.setCellData( row ,  'repr_yn' , 'Y' );
			}
		}
	};
	
	/**
	 * 관련지식재산권 - 그리드 변경 이벤트
	 */		
	scwin.gridSmit_onviewchange = function(info) {
			scwin.onviewchange(info);
	};
	
	/**
	* 관련지식재산권 - onChange 이벤트 헨들러
	*/
	scwin.onviewchange = function(info) {
		console.log("info=="+JSON.stringify(info));
		var row = info.rowIndex;
		var col = info.colIndex;
		//var colName = gridSmit.getColumnID(col);
		for (var i = 0; i < gridSmit.getRowCount(); i++) {
			gridSmit.setReadOnly( "cell" , i , 1 , false );
		}
	};
	
