	/**
	 * 기술이미지1 upload value change 이벤트
	 */	
	scwin.uploadAttachFileImg1_onInputValueChange = function(fileName) {
		var fileExt = fileName.split(".").pop(); // 확장자 받아오는 함수
		fileExt = fileExt.trim();
		fileExt = fileExt.toUpperCase();
		
		if (fileExt != "") {
			// Report 이미지 업로드 시 Flash Player 미지원으로 BMP확장자는 업로드가 되지 않도록 함(2013.10.30) : 포시에스 확인 및 팀장님 지시
			if (fileExt != "JPG" && fileExt != "JPEG" && fileExt != "GIF" && fileExt != "PNG") {
				com.alert("이미지 첨부파일의 확장자는 'JPG, JPEG, GIF, PNG'이어야 합니다.\n(대소문자 관계없음)");
				upload.reset();
				return;
			}
		
			uploadAttachFileImg1.submit();
		
		} else {
			com.alert("첨부된 파일의 확장자가 불분명합니다.\n확인바랍니다.");
			image1.setSrc("");
		
			return;
		}
	};
	
	/*
     * 기술이미지1 업로드 후처리
     */	
	scwin.uploadAttachFileImg1_ondone = function(retXml) {
		var retXmlStr = (new XMLSerializer()).serializeToString(retXml);
		var attachFilePathPhoto =  xml.split("<key>")[1].split("</key>")[0]; 
		var attachFilePhoto = xml.split("<storedFileList>")[1].split("</storedFileList>")[0];
		var photoFilePathArr = attachFilePathPhoto.split("/");
		var attachFileImg1 = "/bizimages/tec/" + photoFilePathArr[photoFilePathArr.length-1] +"/"+ attachFilePhoto;
		
		image1.setSrc(attachFileImg1);
		scwin.data.attachImgFile1 = attachFileImg1;
		btn_photo1.setStyle("display", "");	//.show();
	};
	
