		}else if(mouClsf == "RFD010"){
			btn_smitAppAdd.setDisabled( false );
			groupPsn.setStyle("display", "none");		//.hide();
			//outputAccntNo.changeClass( "labelCP" , "labelC" );
			//outputAccntRspns.changeClass( "labelCP" , "labelC" );
		}else{
			btn_smitAppAdd.setDisabled( false );
			groupPsn.setStyle("display", "none");	
		}
	};	
	
 	/**
	 * 신청자 조회 버튼 클릭 이벤트 
	 */	 	
	scwin.btn_searchEmp_onclick = function(e) {
		var data = { empno : ibx_reqEmpno.getValue()
						//, nm : ibx_trnsReschNm.getValue()
						//, syspayno : scwin.data.inputTrnsReschRspns
						 , work_clsf : "HAG010"
						 ,  ref_yn : "N" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			console.log("ret======="+JSON.stringify(ret));
			ibx_reqEmpno.setValue(ret.empno);			//신청자 사원번호
			ibx_reqNm.setValue(ret.nm);					//신청자 성명
			scwin.data.inputReqPsn = ret.syspayno;		//신청자 개인번호
		});
	};
	
	/**
	 * 신청자 조회 신청자번호 onkeyup 이벤트 
	 */	 
	scwin.ibx_reqEmpno_onkeyup = function(e) {
		if ((e.keyCode != 9) && (e.keyCode != 229)) {
			ibx_reqEmpno.setValue("");
			ibx_reqNm.setValue("");
			scwin.data.inputReqPsn = "";
		}
	};
	
