	/**
	 * 계정번호 버튼 클릭 이벤트
	 */	
	scwin.btn_searchAccntNo_onclick = function(e) {
		if(rad_mouClsf.getValue() == ""){
			com.alert("기술구분을 선택하세요.");
			return;
		}

		var data={
			accnt_no : ibx_accntNo.getValue(),
			ref_yn : (ibx_accntNo.getValue() == "")? "Y" : "N"
		};
		
		com.pop.open(com.pop.TYPE.ACNNO, data, function(ret){
			console.log("ret======"+JSON.stringify(ret));
			scwin.setAccntRow(ret);
			//ibx_accntNo.setValue(ret.accnt_no);
			//ibx_accnt_no_nm.setValue(ret.accnt_no_nm);
		});
	};
	
	/**
	 * 계정팝업에서 선택한 Data를 세팅
	 */	
	scwin.setAccntRow = function(ret){
		console.log("ret.accnt_state===="+ret.accnt_state);
		
		ibx_accntNo.setValue(ret.accnt_no);
		ibx_accntRspnsNm.setValue(ret.accnt_rspns_nm);			//계정책임자 성명
		ibx_accntRspnsEmpno.setValue(ret.accnt_rspns_empno);	//계정책임자개인번호
		ibx_accntNoNm.setValue(ret.accnt_no_nm); 					//계정명
		scwin.data.inputAccntRspns = ret.accnt_rspns;				//계정책임자
		ibx_reschFund.setValue(ret.resch_fund);							//실 투입 연구비(원)
		
		var bsnsCdInout = ret.bsns_cd_inout;							//내외구분
		
		var bsnsCdOfic = ret.bsns_cd_ofic;								// 관계부서
		
		//if(/RAK041/.test(ret.accnt_state)){ // RAK041 집행가능		
		if(bsnsCdInout == "RHA001"){ //정부연구과제
			if(bsnsCdOfic == "022000000"){ //산업통상부 과제
				sbx_techPrjClsf.setValue("RFL001"); //정부수탁(산업부)
			}else if(bsnsCdOfic == "027000000"){ //미래부 과제
				sbx_techPrjClsf.setValue("RFL004"); //정부수탁(미래부)						
			}else{
				sbx_techPrjClsf.setValue("RFL002"); //정부수탁(기타)
			}
		}else{
			sbx_techPrjClsf.setValue("RFL003"); //일반과제
		}
		
		scwin.setCompo();
		
		if(rad_mouClsf.getValue() == "RFD001"){
			dma_search.set("src_flag", "patInfo");
			dma_search.set("accnt_no", ret.accnt_no);
			scwin.search();
		}
		
	};
	
	/**
	 * 기술자료(사양포함) - 그리드 클릭 이벤트
	 */		
	scwin.gridSpec_oncellclick = function(row,col,colId) {
		if (colId == "btn_attach") {  // 첨부파일
		var getAttachFile = dlt_techRefList.getCellData(row ,"attach_file_no");  //  첨부파일ID				
			var data = {
					attach_file_no : (getAttachFile.trim() =="" ? scwin.data.serviceId : getAttachFile.trim()),
					file_key : scwin.data.serviceId,
					mode : "edit",
				};			
				var flag="spec";		
				scwin.openAttachFile(colId,data,row, flag);
		}
	};
	
	/**
	* 첨부파일 공통 팝업 오픈
	*/
	scwin.openAttachFile = function(type, data, row, flag) {
		var attach_file_no = data.attach_file_no;
		com.pop.open(com.pop.TYPE.ATTACH,data,function(ret){
			debugger;
			if(ret){
				if (type == "attach_file_btn") {
					if (ret.attach_file_no == undefined) {
						if (attach_file_no != ""){
							attach_file_no = "";
						}
					} else {
						attach_file_no = ret.attach_file_no;
					}
					if(flag == "spec"){
						dlt_techRefList.setCellData(row, "attach_file_no", attach_file_no);
					} else if(flag == "agency"){
					 	dlt_agencyVendList.setCellData(row, "attach_file_no", attach_file_no);
					}
					//dma_save.setJSON(dlt_bgremtl.getRowJSON(row));
					scwin.saveAttacheFileData(data);
				}
			}
		});
	};

	/**
	* 첨부파일 데이터 변경 처리
	*/	
	scwin.saveAttacheFileData = function(data, row) {
		com.execute(sbm_saveAttacheFileData, {
			successCallback : function(e) {
				console.log(e.responseJSON);						
			}
		});		
	};
	
