			consultInfo_gb = "Y";
			moveId = "I721";
		}
		
		var data = {
				 consultInfo_gb : consultInfo_gb
				 
				 
//				,trns_resch_empno : inputTrnsReschEmpno.getValue()
//				,trns_resch_nm:  ibx_trnsReschNm.getValue() 
//				,nego_rspns_empno : ibx_negoRspnsEmpno.getValue()
