//				,admn_wrk_rspns_nm : ibx_admnWrkRspnsNm.getValue()	
//				,xml_id : scwin.data.serviceId 
//				,g_req_no : g_req_no
			};
		
		com.openTabMenu(moveId, data, function(retObj) {
				// 콜백
				if(retObj){
					scwin.search();	
				}
			});	 
	}
	
