			radioGrowLevel.setStyle("top", "2208px");
		}else{
			//group21.setSize(989,486);
			grp32.setStyle("display", "none"); //grp32.hide();
			//KitechAttach.action.hide('upload_C');
			radioGrowLevel.setStyle("top", "1828px");
		}
	};
	
