	/**
	 * 기술이전조건 - 공급가액 onblur  이벤트
	 */	
	scwin.ibx_fxdSuplyValue_onblur = function(e) {
		var fxdTechSuplyValue = iibx_fxdSuplyValue.getValue();
		var fxdTechTaxFee = Number(fxdTechSuplyValue)/10;
		var fxdTechFee = Number(fxdTechSuplyValue) + Number(fxdTechTaxFee);
		ibx_fxdTechTaxFee.setValue(fxdTechTaxFee);		//부가세
		ibx_fxdTechFee.setValue(fxdTechFee);				//총액
	};
	
	/**
	 * 기술이전조건 - 부가세 onblur  이벤트
	 */	
	scwin.ibx_fxdTechTaxFee_onblur = function(e) {
		var fxdTechSuplyValue = iibx_fxdSuplyValue.getValue();
		var fxdTechTaxFee = ibx_fxdTechTaxFee.getValue();
		var fxdTechFee = Number(fxdTechSuplyValue) + Number(fxdTechTaxFee);
		
		ibx_fxdTechFee.setValue(fxdTechFee); 
	};
	
	/**
	 * 기술이전조건 - 총액 onblur  이벤트
	 */		
	scwin.ibx_fxdTechFee_onblur = function(e) {
		var fxdTechFee = ibx_fxdTechFee.getValue();
		var fxdTechSuplyValue =  Math.floor(Number(fxdTechFee) * 10 / 11);
		var fxdTechTaxFee = Number(fxdTechFee) - Number(fxdTechSuplyValue);
		
		iibx_fxdSuplyValue.setValue(fxdTechSuplyValue);
		ibx_fxdTechTaxFee.setValue(fxdTechTaxFee);
	};
	
	/**
	 * 기술이전조건 - 중개기관 존재여부 radio 클릭 이벤트
	 */		
	scwin.rad_agncyVendYn_onradioclick = function(index,value) {
		console.log("this.value===="+value);
		if(value == "Y"){
			grpAgency.setStyle("display", "");	 	//group33
		}else{
			grpAgency.setStyle("display", "none");	
		}
	};
	
