	/**
	 * 기술이전 중개기관 - 그리드 onviewchange 이벤트
	 */		
	scwin.gridAgncy_onviewchange = function(info) {
		console.log("info=="+JSON.stringify(info));
		var row = info.rowIndex;
		var col = info.colIndex;

		var colId = gridAgncy.getColumnID(col);
		var suplyValue = Number(gridAgncy.getCellData(row, "suply_value"));
		var addTax = 0;
		if(colId == "suply_value"){
			addTax = suplyValue*0.1;
			gridAgncy.setCellData(row, "add_tax", addTax);
			gridAgncy.setCellData(row, "agncy_fee", (suplyValue + addTax));
		}else if(colId == "add_tax"){
			addTax = Number(gridAgncy.getCellData(row, "add_tax"));
			gridAgncy.setCellData(row, "agncy_fee", (suplyValue + addTax));
		}
	};
	
	/**
	 * 참여자현황 - 그리드 onclick 이벤트
	 */	
	scwin.gridPsn_oncellclick = function(row,col,colId) {
		//var getRow = row;
		//var colId = gridPsn.getColumnID(col);
		//var empNo = dlt_tecPareList.getCellData(row, "");
		if(	colId == "emp_btn"){
			 //HumComUserSel_Popup();
			 var data = { empno : ""
						//, nm : ibx_trnsReschNm.getValue()
						//, syspayno : scwin.data.inputTrnsReschRspns
						 , work_clsf : "HAG010"
						 ,  ref_yn : "N" }
			com.pop.open( com.pop.TYPE.HUM, data, function(ret){
				console.log("ret======="+JSON.stringify(ret));
				dlt_tecPareList.setCellData(row, "nm", ret.nm);				//성명
				dlt_tecPareList.setCellData(row, "empno", ret.empno);	//사원번호
				dlt_tecPareList.setCellData(row, "syspayno", ret.syspayno);	//개인번호
			});
		}
	};
	
	/**
	 * 참여자현황 - 그리드 onviewchange 이벤트
	 */
	scwin.gridPsn_onviewchange = function(info) {
		console.log("info=="+JSON.stringify(info));
		var row = info.rowIndex;
		var col = info.colIndex;
		scwin.data.getRow = row;
		var colId = gridPsn.getColumnID(col);
		if (colId == "nm") {
			//getRow = row;
			var getInputData = dlt_tecPareList.getCellData(row, "empno");//newData;
			var setSysPayNo = "";
			var setNm = "";
		
			if (getInputData.trim() != "") {
				 //숫자인지 체크(숫자면 개인번호, 아니면 성명)
				if (isNumber(getInputData)) {
					//  앞뒤로 % 붙임
					setSysPayNo = '%' + getInputData + '%';
				} else {
					// 앞뒤로 % 붙임
					setNm = '%' + getInputData + '%';
				}
		
				dma_search.set(syspayno, setSysPayNo);
				dma_search.set(nm, setNm);
				
				com.execute(sbm_searchHumBasicInfoNm, {
					successCallback : function(e) { // 성공 콜백
		     			dma_tecPare.setJSON(e.responseJSON.elData.ResTrnsTecPareVo);
		     			
		     			var cnt = dma_tecPare.get("cnt");
		     			var workClsf = dma_tecPare.get("work_clsf");
		     			var posiCd = dma_tecPare.get("posi_cd");
		     			
		     			if(cnt == "1"){
							gridPsn.setCellData(scwin.data.getRow, "empno", dma_tecPare.getKey("empno"));
							gridPsn.setCellData(scwin.data.getRow, "nm", dma_tecPare.getKey("nm"));
							gridPsn.setCellData(scwin.data.getRow, "syspayno",  dma_tecPare.getKey("syspayno"));
							gridPsn.setCellData(scwin.data.getRow, "dept_nm",  dma_tecPare.getKey("dept_nm"));
							gridPsn.setCellData(scwin.data.getRow, "posi_nm",  dma_tecPare.getKey("posi_nm"));
							//gridPsn.setCellData(getRow, "init_cd", initCd);
						
						if(workClsf == "HAG010"){
							if(posiCd == "351010" || posiCd == "354000" || posiCd == "350010" || posiCd == "330020"){ //연구생, 연수생, 학생연구원
								dlt_tecPareList.setCellData(scwin.data.getRow, "user_div", "5");
							}else{
								dlt_tecPareList.setCellData(scwin.data.getRow, "user_div", "1");
							}
						}else{
							dlt_tecPareList.setCellData(scwin.data.getRow, "user_div", "3");
						}
		     			
					} else {
						var data = { empno : ibx_reqEmpno.getValue()
						//, nm : ibx_trnsReschNm.getValue()
						//, syspayno : scwin.data.inputTrnsReschRspns
						 , work_clsf : "HAG010"
						 ,  ref_yn : "N" }
						com.pop.open( com.pop.TYPE.HUM, data, function(ret){
							console.log("ret======="+JSON.stringify(ret));
							dlt_tecPareList.setCellData(scwin.data.getRow, "empno", ret.empno);			// 사원번호
							dlt_tecPareList.setCellData(scwin.data.getRow, "nm", ret.nm);					// 성명
							dlt_tecPareList.setCellData(scwin.data.getRow, "syspayno", ret.syspayno);;		// 개인번호
						});
					}
				}
			});	
				
			}
		}
	};
	
	/**
	 * 기술이전협약 담당자 지정 - 행정담당자 버튼 클릭 이벤트
	 */	
	scwin.btn_searchAdminWrk_onclick = function(e) {
		var data = { empno : ibx_admnWrkRspnsEmpno.getValue()
						 , nm : ibx_admnWrkRspnsNm.getValue()
						 , syspayno : scwin.data.inputAdmnWrkRspns
						 , work_clsf : "HAG010"
						 ,  ref_yn : "N" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			console.log("ret======="+JSON.stringify(ret));
			ibx_admnWrkRspnsEmpno.setValue(ret.empno);		// 사원번호
			ibx_admnWrkRspnsNm.setValue(ret.nm); 					// 성명
			scwin.data.inputAdmnWrkRspns = ret.syspayno;		// 개인번호
		});
	};
	
	/**
	 * 기술이전협약 담당자 지정 - 행정담당자 onkeyup 이벤트
	 */	
	scwin.ibx_admnWrkRspnsEmpno_onkeyup = function(e) {
		if ((e.keyCode != 9) && (e.keyCode != 229)) {
			ibx_admnWrkRspnsEmpno.setValue("");
			ibx_admnWrkRspnsNm.setValue("");
			scwin.data.inputAdmnWrkRspns = "";
		}
	};
	
	/**
	 * 기술이전협약 담당자 지정 - 협상담당자 버튼 클릭 이벤트
	 */	
	scwin.btn_searchNegoRspns_onclick = function(e) {
		var data = { empno : ibx_negoRspnsEmpno.getValue()
						 , nm : ibx_negoRspnsNm.getValue()
						 , syspayno : scwin.data.inputNegoRspns
						 , work_clsf : "HAG010"
						 ,  ref_yn : "N" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
