			ibx_negoRspnsEmpno.setValue(ret.empno);		// 사원번호
			ibx_negoRspnsNm.setValue(ret.nm); 				// 성명
			scwin.data.inputNegoRspns = ret.syspayno;		// 개인번호
		});
	};
	
	/**
	 * 기술이전협약 담당자 지정 - 협상담당자 onkeyup 이벤트
	 */		
	scwin.ibx_negoRspnsEmpno_onkeyup = function(e) {
		if ((e.keyCode != 9) && (e.keyCode != 229)) {
			ibx_negoRspnsEmpno.setValue("");
			ibx_negoRspnsNm.setValue("");
			scwin.data.inputNegoRspns = "";
		}
