	/**
	 * 기술이전협약 담당자 지정 저장 버튼 클릭 이벤트
	 */	
	scwin.btn_trnsEmps_onclick = function(e) {
		scwin.data.btnClsf = "temp"; 
		scwin.data.saveFlag = "temp";
		if(scwin.data.inputAdmnWrkRspns == ""){
			com.alert("행정담당자를 입력하세요.");
			return;
		}
		if(scwin.data.inputNegoRspns == ""){
			com.alert("협상담당자를 입력하세요.");
			return;
		} 
		
		dma_result.set("admn_wrk_rspns", scwin.data.inputAdmnWrkRspns);
		dma_result.set("nego_rspns", scwin.data.inputNegoRspns);
		dma_result.set("proc_state", "RTS030");
		dma_result.set("syspayno", com.getLoginInfo("sn"));
		dma_result.set("save_clsf", scwin.data.saveClsf);
		dma_result.set("scrn_clsf", "trnsInfo");
		
		scwin.save();
		//gfnExecuteSubmission("setSaveData");
	};
	
