	/**
	 * 목록 버튼 클릭 이벤트
	 */
	scwin.btn_pre_onclick = function(e) {
			if(scwin.data.param4 == "apr"){
				//TO-DO 링크가 어디로 가라는건지??
				//"http://apr.kitech.re.kr/processparticipant/worklist/kitech_workList_saved.jsp?currentPage=1";
			} else {
				var data = {
						 args : com.getParameter("args")
						,trns_resch_empno : scwin.data.p_trns_resch_empno
						,trns_resch_nm:  scwin.data.p_trns_resch_nm
						,nego_rspns_empno : scwin.data.p_nego_rspns_empno
						,nego_rspns_nm : scwin.data.p_nego_rspns_nm
						,admn_wrk_rspns_empno : scwin.data.p_admn_wrk_rspns_empno
						,admn_wrk_rspns_nm : scwin.data.p_admn_wrk_rspns_nm
						,xml_id : scwin.data.serviceId 
						,his_back : "Y"
					};
				
				com.moveList("J770", data, function(retObj) {
						// 콜백
						if(retObj){
							scwin.search();	
						}
					});	 
				
				// 목록 페이지로 이동
				//com.moveList("H533");
		}			
	};
	
	/**
	 * 기술자료(사양포함) - 버튼 클릭 이벤트
	 */		
	scwin.btn_gridSpecMinus_onclick = function(e) {
		var row =  gridSpec.getFocusedRowIndex();
		if (row < 0) {
			com.alert("삭제할 row를 선택하세요.");
			return;
		}
		
		var status = dlt_techRefList.getRowStatus(row);
		console.log("status=="+status);
						
		if (status != "V") {
			dlt_techRefList.setCellData(row, "cud_type", "D");
		}
		dlt_techRefList.removeRow(row);		
	};
	
	/**
	 * 예상수요업체&담당자정보 + 버튼 클릭 이벤트
