	/**
	 * 임시저장 버튼 클릭 이벤트
	 */	
	scwin.btn_temp_save_onclick = function(e) {
		scwin.data.btnClsf = "temp";
		scwin.data.saveFlag = "temp";
		scwin.data.aprState = "XAD005";
		dma_result.set("apr_state", scwin.data.aprState); 
		scwin.save();
	};
	
	/**
	 * 삭제 버튼 클릭 이벤트
	 */	
	scwin.btn_del_onclick = function(e) {
		if(scwin.data.aprState >= "XAD020"){
			com.alert("신청서가 접수완료되었으므로 삭제할 수 없습니다.");
			return;
		}
		
		dma_result.set("prcs_clsf", "infoReg");
		// 삭제 실행
		com.confirm("신청서를 삭제하시겠습니까?"/*삭제 하시겠습니까?*/, function(ret) {
			if (ret) {
					com.execute(sbm_delResTrs, {
						successCallback : function(e) { // 성공 콜백
							com.alert("신청서를 삭제하였습니다.", function() {
								com.moveList("J770");
							});
						}
					});
				}
			});			
	};
	
	/**
	 * 저장 버튼 클릭 이벤트
	 */	
	scwin.btn_save_onclick = function(e) {
		scwin.data.btnClsf = "";
		scwin.data.saveFlag = "save";
		dma_result.set("apr_state", "XAD005"); 
		scwin.data.aprState = "XAD005";
		
		
;		//dma_result.set("tech_grow_level", techGrowLevel);
		dma_result.set("trns_typ", dma_result.get("trns_typ").replaceAll(",", " ")); 
		
		if (!scwin.preSave(scwin.data.btnClsf)) return;
		// 2015.3.20 ssy. 이지희 연구원 요청
		com.confirm("※ 기술이전 신청서 처리완료까지 최소한달 이상이 소요됩니다.\n 저장하시겠습니까?", function(ret) {
			if (ret) {
				scwin.save();
			}
		});		
	};
	
	/**
	 * 저장전 실행
	 */	
	scwin.preSave = function(state) { 	
		var contrctStartYmd = ica_calendarContrctStartYmd.getValue();
		var contrctClsYmd = ica_calendarContrctClsYmd.getValue();
		var imgFileCnt = 0;

		if(contrctStartYmd == "" || contrctStartYmd ==null){
			com.alert("희망 계약 시작일자를 기입하세요");
			ica_calendarContrctStartYmd.focus();
			return false;
		}

		if(contrctClsYmd == "" || contrctClsYmd ==null){
			com.alert("희망 계약 종료일자를 기입하세요");
			ica_calendarContrctClsYmd.focus();
			return false;
		}

		if(scwin.data.inputReqPsn == ""){
			com.alert("신청자는 필수입력사항입니다.");
			return false;
		}

		var transKrnNm = ibx_trnsKrnNm.getValue().trim();
		if(transKrnNm == ""){
			com.alert("이전기술명(국문)을 기입해 주십시오");
			ibx_trnsKrnNm.focus();
			return false;
		}
		if(transKrnNm.length > 120){
			com.alert("이전기술명(국문)을 400자를 초과 할 수 없습니다.");
			return false;
		}

		var transEngNm = ibx_transEngNm.getValue();
		if(transEngNm.length > 400){
			com.alert("이전기술명(영문)을 400자를 초과 할 수 없습니다.");
			return false;
		}

		if(scwin.data.inputTrnsReschRspns == ""){
			com.alert("기술이전 연구 책임자는 필수입력사항입니다.");
			ibx_trnsReschEmpno.focus();
			return false;
		}
		if(ibx_trnsRspnsDeptCd.getValue() == ""){
			com.alert("이전 연구 책임자의 부서명은 필수입력사항입니다.");
			return false;
		}

		if(ibx_reschFund.getValue() == "" && ibx_accntNo.getValue() != ""){
			com.alert("실 투입 연구비는 필수입력사항입니다.");
			ibx_reschFund.focus();
			return false;
		}

		var mouClsf = dma_result.get("mou_clsf");	 //data:dma_result.mou_clsf rad_mouClsf.getValue();
		if(mouClsf == ""){
			com.alert("기술구분은 필수항목입니다.");
			rad_mouClsf.focus();
			return false;
		}

		if(ibx_accntNo.getValue() == "" && rad_mouClsf.getValue() != "RFD010"){
			com.alert("계정번호는 필수입력사항입니다.");
			ibx_accntNo.focus();
			return false;
		}
			
		var techPrjClsf = sbx_techPrjClsf.getValue();
		if(mouClsf == "RFD001"){ //지식재산권
			if(gridSmit.getRowCount() == 0){
				com.alert("관련특허를 입력하세요.");
				return false;
			}else{
				var gridRadioYn = "N";
				for(var i=0; i<dlt_smitList.getRowCount(); i++){
					var indstRightClsf = dlt_smitList.getCellData(i, "indst_right_clsf");
					if(gridSmit.getCellChecked( i , "grid_radio" )== true){
						gridRadioYn = "Y";
					}
					
					if(indstRightClsf == ""){
						com.alert("지식재산권구분을 선택하세요.");
						return false;
					}
				}
				
				if(gridRadioYn =="N"){
					com.alert("관련 지식재산권에 대표를 선택하세요.");
					return false;
				}
			}
		}else if(mouClsf == "RFD002" && techPrjClsf == "RFL003"){ // 노하우, 일반과제일경우, 참여자현황 필수기재사항(20150617 이지희) 
			if(dlt_tecPareList.getRowCount() < 1){
				com.alert("기술구분이 Know-How일 경우, 참여자현황은 필수입력입니다.");
				return;
			}
		}
		
		//2016.07.18.mouClsf - RFD007(정부연구과제(노하우등)), RFD008(정부연구과제(지재권))삭제
		//연구관리시스템의 정부수탁(산업부), 정부수탁(기타)에 의한 분리
		if(scwin.data.inputAccntRspns == ""  && radioMouClsf.getValue() != "RFD010"){
			com.alert("계정책임자가 누락되었습니다. 확인바랍니다.");
			return false;
		}
				
		if(txa_techOutline.getValue() == ""){
			com.alert("기술개요는 필수입력사항입니다.");
			txa_techOutline.focus();
			return false;
		}
				
		if(scwin.data.attachImgFile1 != "") {
			imgFileCnt++;
		}
				
				/**if(attachImgFile1.getValue() != "") {
					imgFileCnt++;
				}
				
				if(attachImgFile1.getValue() != "") {
					imgFileCnt++;
				}**/				
				
		if (imgFileCnt < 1) {
			com.alert("최소 1개의 이미지파일은 첨부되어야 합니다.");
			return false;				
		}
								
		if(sbx_sixTClsf.getValue() == ""){
			com.alert("기술적용분야는 필수입력사항입니다.");
			sbx_sixTClsf.focus();
			return false;
		}
								
		if(sbx_bigClsf.getValue() == ""){
			com.alert("산업기술분류코드는 필수입력사항입니다.");
			sbx_bigClsf.focus();
			return false;
		}
								
		if(ica_devLeadTimeSt.getValue() == ""){
			com.alert("총 기술개발소요기간 시작일자는 필수입력사항입니다.");
			ica_devLeadTimeSt.focus();
			return false;
		}
				
		if(ica_devLeadTimeEd.getValue() == ""){
			com.alert("총 기술개발소요기간 종료일자는 필수입력사항입니다.");
			ica_devLeadTimeEd.focus();
			return false;
		}				
								
		if(ibx_expectEffect.getValue() == ""){
			com.alert("기대효과는 필수입력사항입니다.");
			ibx_expectEffect.focus();
			return false;
		}
								
		var radio1 = dma_result.get("tech_grow_level"); 	//dma_result.get$('input[name=radio1]').val();
		console.log("radio1===="+radio1);
		if(radio1 == ""){
			com.alert("기술성숙도는 필수입력사항입니다.");
			radioGrowLevel.focus();// radioGrowLevel.focus();
			return false;
		}				

		if(scwin.data.btnClsf != "temp"){
			if(techPrjClsf == "RFL001" || techPrjClsf == "RFL002" || techPrjClsf == "RFL004"){ //정부 연구관제 (미래부 추가)
			
				if (1 > com.file.action.getTotalFileCount(upload_C.getID())) {
					com.alert("과제협약서를 첨부하세요");
					return false;
				}
			}
			
			if(dma_result.get("mou_clsf") != "RFD010"){	//radioMouClsf.getValue()
				if (1 > com.file.action.getTotalFileCount(upload_A.getID())) {
					com.alert("청렴유지서약서를 첨부하세요");
					return false;
				}
			}
				
			
			if (1 > com.file.action.getTotalFileCount(upload_D.getID())) {
				com.alert("비밀유지협약서를 첨부하세요");
				return false;
			}	
			if(dma_result.get("trns_typ")  == ""){	// cbx_transTyp.getValue()
				com.alert("계약형태를 기입해 주십시오.");
				return false;
			}

			if(dma_result.get("trns_scop")  == ""){ // rad_trnsScop.getValue()
				com.alert("실시범위를 기입해 주십시오.");
				return false;
			}

			var etcCnd = txa_etcCnd.getValue().trim();
			if(etcCnd.length >500){
				com.alert("기타 기술이전 조건은 500자를 초과 할 수 없습니다.");
				return false;
			}
					
			//2017.09-18 한진희 요청  기술이전 노하우 업로드 없으면 금지 
			// 노하우 일때만.
			console.log("mouClsf :"+mouClsf);
			if(mouClsf == "RFD002" ){
				console.log("gridSpec.getRowCount() :"+dlt_techRefList.getRowCount());
				if(dlt_techRefList.getRowCount() == 0 ){
					com.alert("기술이전 노하우 기술자료를 첨부 하셔야 합니다.");
					gridSpec.focus();
					return false;
				}
						
			//참여자현황
			var psnCnt = dlt_tecPareList.getRowCount();
			var contrbtRt = 0;
			
			for(var i=0; i < psnCnt; i++){
				contrbtRt += Number(dlt_tecPareList.getCellData(i, "contrbt_rt"));
				
			}
			
			if(contrbtRt != 100){
				com.alert("기여율이 100% 가 아닙니다.");
				return false;
			}						
		}

//					var runRyalty = inputRunRyalty.getValue();
//					if(runRyalty == ""){
//						alert("경상기술료를 기입해 주십시오.");
//						return false;
//					}

		var techFeeContnt = txa_techFeeContnt.getValue().trim();
		if(techFeeContnt.length > 120){
			com.alert("협상조건은 120자를 초과 할 수 없습니다.");
			return false;
		}

		// 수요기업존재여부 
		var dmndVendYn = dma_result.get("dmnd_vend_yn"); //rad_dmndVendYn.getValue();	//data:dma_result.dmnd_vend_yn
		if(dmndVendYn == ""){
			com.alert("수요기업 존재 여부를 기입해 주십시오.");
			return false;
		}
		//예상수요업체
		var vendCnt = dlt_vendList.getRowCount();
		if (dmndVendYn == "Y") {
			if (vendCnt == 0) {
				com.alert("예상수요업체가 입력되지 않았습니다.");
				return false;
			}
		}
			
		if (dmndVendYn == "Y") {
			if (1 > com.file.action.getTotalFileCount(upload_B.getID())) {
				com.alert("사업자등록증을 첨부하여주세요");
				upload_B.focus();		//wframe2.focus();
				return false;
			}
		}
		if (dmndVendYn == "Y") {
			for(var i=0; i<vendCnt; i++){
				if(dlt_vendList.getCellData(i, "vend_cd").trim() == ""){
					com.alert("업체코드가 입력되지 않았습니다. 확인바랍니다.");
					return false;
				}
				
				/*
				if(gridVend.getCellData(i, "bsns_psn_regst_no").trim() == ""){
					alert("사업자등록번호가 입력되지 않았습니다. 확인바랍니다.");
					return false;
				}
				*/

				if(dlt_vendList.getCellData(i, "cmpy_typ") == ""){
					com.alert("업체규모가 입력되지 않았습니다. 확인바랍니다.");
					return false;
				}

				if(dlt_vendList.getCellData(i, "charg") == ""){
					com.alert("담당자성명이 입력되지 않았습니다. 확인바랍니다.");
					return false;
				}

				if(dlt_vendList.getCellData(i, "charg_tel") == ""){
					com.alert("연락처가 입력되지 않았습니다. 확인바랍니다.");
					return false;
				}

				if(dlt_vendList.getCellData(i, "charg_email").trim() == ""){
					com.alert("이메일이 입력되지 않았습니다. 확인바랍니다.");
					return false;
				}
				if(dlt_vendList.getCellData(i,"charg_dept") == ""){
					com.alert("수요업체 담당부서가 입력되지 않았습니다. 확인바랍니다.");
					return false;
				}
			}
		}
		var agncyYn = dma_result.get("agncy_vend_yn"); //rad_agncyVendYn.getValue();
		if(agncyYn == ""){
			com.alert("중개기관 존재여부를 선택하세요.");
			rad_agncyVendYn.focus();
			return false;
		}
		var agncyCnt = gridAgncy.getRowCount();
		/**if(agncyCnt > 0 && agncyYn == "N"){
			rad_agncyVendYn.setSelectedIndex(0);//set  ..setValue("Y");
			agncyYn = "Y";
		}**/
		if( agncyYn == "Y"){
			if(agncyCnt == 0){
				com.alert("중개기관을 입력하세요.");
				return false;
			}
						
			for(var i=0; i<agncyCnt; i++){
				if(dlt_agencyVendList.getCellData(i, "bsns_psn_regst_no") == ""){
					com.alert("중개기관의 사업자번호가 누락되었습니다.");
					return false;
				}
								// 20161010 수수료율 및 금액 검증 제거요청(이지희)
	//							if(WebSquare.util.getNumber(gridAgncy.getCellData(i, "agncy_fee_rt")) == 0){
	//								alert("중개수수료율이 누락되었습니다.");
	//								return false;
	//							}
	//							if(WebSquare.util.getNumber(gridAgncy.getCellData(i, "suply_value")) == 0){
	//								alert("중개기관의 금액이 누락되었습니다.");
	//								return false;
	//							} 
	
				if(dlt_agencyVendList.getCellData(i, "charg") == ""){
					com.alert("중개기관의 담당자가 누락되었습니다.");
					return false;
				}
				if(dlt_agencyVendList.getCellData(i, "charg_tel") == ""){
					com.alert("중개기관의 담당자연락처가 누락되었습니다.");
					return false;
				}
				if(dlt_agencyVendList.getCellData(i, "charg_email") == ""){
					com.alert("중개기관의 담당자 이메일이 누락되었습니다.");
					return false;
				}
			}// end for
		}// end agncyYn == "Y"
	}
	
	return true;
};

	/**
	 * 결재신청버튼 클릭 이벤트
	 */
	scwin.btn_apr_onclick = function(e) {
		scwin.data.btnClsf = "";
		scwin.data.saveFlag = "apr";
		var radio1 = dma_result.get("tech_grow_level"); //scwin.setRadio();
		
		if(radio1 == "1" || radio1 == "2"){
			dma_result.set("grow_level_nm", "기초연구단계");
		} else if(radio1 == "3" || radio1 == "4"){
			dma_result.set("grow_level_nm", "실험단계");
		} else if(radio1 == "5" || radio1 == "6"){
			dma_result.set("grow_level_nm", "시작품단계");
		} else if(radio1 == "7" || radio1 == "8"){
			dma_result.set("grow_level_nm", "실용화단계");
		} else if(radio1 == "9"){
			dma_result.set("grow_level_nm", "사업화단계");
		}
		
		var reqPsnNm = dma_result.get("req_nm") + "{" + dma_result.get("req_empno") + ")";
		var contrctYmd = dma_result.get("contrct_start_ymd") + " ~ " + dma_result.get("contrct_cls_ymd");
		var sixTNm = sbx_sixTClsf.getText();
		
		dma_result.set("req_psn_nm", reqPsnNm);
		dma_result.set("contrct_ymd", contrctYmd);
		dma_result.set("six_t_nm", sixTNm);
		dma_result.set("approval", "true");
		dma_result.set("apr_state", "XAD010"); 
		scwin.data.aprState = "XAD010";
		scwin.save();
	};
	
	/**
	 * 저장
	 */
	scwin.save = function() {
		if(scwin.data.saveFlag == "temp" || scwin.data.saveFlag == "save"){
			//2.관련 지식재산권
			//gridSmit
			if(dlt_smitList.getRowCount()>0){
				for(var i=0; i<dlt_smitList.getRowCount(); i++){
					var status = dlt_smitList.getRowStatus(i);
					if(status == "U"){
						if(scwin.data.saveClsf == "insert"){
							dlt_smitList.setCellData(i, "cud_type", "C");
						} else {
							dlt_smitList.setCellData(i, "cud_type", "U");
						}
					}
				}
			}
			// 4.기술자료	
			//gridSpec
			if(dlt_techRefList.getRowCount()>0){
				for(var i=0; i<dlt_techRefList.getRowCount(); i++){
					var status = dlt_techRefList.getRowStatus(i);
					if(status == "U"){
						if(scwin.data.saveClsf == "insert"){
							dlt_techRefList.setCellData(i, "cud_type", "C");
						} else {
							dlt_techRefList.setCellData(i, "cud_type", "U");
						}
					}
				}
			}
			// 3.신청업체&담당자정보
			//gridVend
			if(dlt_vendList.getRowCount()>0){
				for(var i=0; i<dlt_vendList.getRowCount(); i++){
					var status = dlt_vendList.getRowStatus(i);
					if(status == "U"){
						if(scwin.data.saveClsf == "insert"){
							dlt_vendList.setCellData(i, "cud_type", "C");
						} else {
							dlt_vendList.setCellData(i, "cud_type", "U");
						}
					}
				}
			}
				
			// 6.중개기관&담당자정보
			//gridAgncy
			if(dma_result.get("agncy_vend_yn") == "Y"){
				if(dlt_agencyVendList.getRowCount()>0){
					for(var i=0; i<dlt_agencyVendList.getRowCount(); i++){
						var status = dlt_agencyVendList.getRowStatus(i);
						if(status == "U"){
							if(scwin.data.saveClsf == "insert"){
								dlt_agencyVendList.setCellData(i, "cud_type", "C");
							} else {
								dlt_agencyVendList.setCellData(i, "cud_type", "U");
							}
						}
					}
				}
			}
				
			// 5.참여자현황
			//gridPsn	
			if(dlt_tecPareList.getRowCount()>0){
				for(var i=0; i<dlt_tecPareList.getRowCount(); i++){
					var status = dlt_tecPareList.getRowStatus(i);
					if(status == "U"){
						if(scwin.data.saveClsf == "insert"){
							dlt_tecPareList.setCellData(i, "cud_type", "C");
						} else {
							dlt_tecPareList.setCellData(i, "cud_type", "U");
						}
					}
				}
			}
		}
		
		com.file.transport(function(attachObj){
			dma_result.set("attach_file_no", attachObj.upload_A);
			dma_result.set("attach_file_no_2", attachObj.upload_B);
			dma_result.set("attach_file_no_3", attachObj.upload_C);
			dma_result.set("attach_file_no_4", attachObj.upload_D);
				
			var attaches = [];
			if (attachObj.upload_A !="") {
				attaches.push(attachObj.upload_A);
			}
			if (attachObj.upload_B !="") {
				attaches.push(attachObj.upload_B);
			}
			if (attachObj.upload_C !="") {
				attaches.push(attachObj.upload_C);
			}
			if (attachObj.upload_D !="") {
				attaches.push(attachObj.upload_D);
			}
			dma_result.set("validAttachNo", attaches.join(","));	
			
			var infoCd = rad_mouClsf.getText() + "/" + ibx_trnsKrnNm.getValue() + "/" + ibx_trnsReschNm.getValue() + "/";
			//radioMouClsf.getText() + "/" + inputTrnsKrnNm.getValue() + "/" + inputTrnsReschNm.getValue() + "/";
			dma_result.set("scrn_clsf", "trnsInfo");
			dma_result.set("service_id", scwin.data.serviceId);
			dma_result.set("trns_resch_rspns", scwin.data.inputTrnsReschRspns);
			dma_result.set("info_cd", infoCd);
			dma_result.set("req_psn_region", scwin.data.inputRegionCd);
			
			com.execute(sbm_saveResTrs, {
				successCallback : function(e) { // 성공 콜백
					scwin.data.saveClsf = "update";
					
					if(scwin.data.btnClsf != "temp"){
						console.log("scwin.data.aprState====="+scwin.data.aprState);
						if(scwin.data.aprState == "XAD005" || scwin.data.aprState == "XAD013" || scwin.data.aprState == "XAD015"){
							com.confirm("저장하였습니다. 결재신청을 하시겠습니까?", function(ret) {
								if (ret) {
									scwin.btn_apr_onclick();//scwin.approval();
								}else{
									com.alert("저장하였습니다");
									scwin.search();
								}							
							});
						}else{
							//	결재신청 후에는 결재진행함으로 이동
							com.movePageMenu("A001", { "reqNo" :  dma_result.get("req_no")});	
							//com.alert("저장하였습니다");
							//scwin.search();
						}
					}else{
						com.alert("저장하였습니다.");
						scwin.search();
					}	
				} 
			});
		});		
	};
	
