head.script:
        
	scwin.data={
		typeCode : "G310",
		allowExtension : "jpg,pptx,tiff,jpeg,hwp,hwpx,bmp,gif,mht,pdf,doc,rtf,tif,ppt,html,docx,txt,png"
	};
		
	/*
	*  날짜 설정
	 */
	   
	var now = new Date();
	var defalutday = gcm.date.formatDateTime(now, "yyyyMMddHHmm"); // yyyyMMddHHmm
	var ymd = gcm.date.formatDateTime(now); // yyyyMMdd
	var year = ymd.substr(0, 4); // yyyyMM 
	var yearMm = ymd.substr(0, 6); // yyyyMM
	var Month = ymd.substr(4, 2); // yyyyMM
	var session = com.getLoginInfo();	     
	var aprYn = ""; 
	
	scwin.onpageload = function() {
		com.file.init();
		
		com.setEnterKeyEvent(grp_accnt_rspns, scwin.btn_charger_onclick);//계정책임자조회
		
		grp_rvew.setStyle("display", "none");
		grp_title.hide();
		inp_relatno.setStyle("display", "none");
		
		var param = com.getParameter();
				
		if(param.param4 == "apr"){
			dma_search.set("req_no", param.req_no);		
		}else{
			dma_search.set("req_no", param.req_no);	
		}
		
		// 공통코드
		var codeOptions = [ { code : "HCF", compID :"sel_req_region"}, 		
							{ code : "RBH", compID :"sbx_rvew_rst_cd"},
							{ code : "RDK", compID :"rad_intgr_mngmt_unit"}
												
		                ];
		com.setCmnCd(codeOptions, function() {
			rad_intgr_mngmt_unit.setValue("RDK003");										
		});
		
		//debugger;	
		if(dma_search.get("req_no") != ""){
			scwin.search();
			dma_result.set("cud_type", "U");
			//radioSliptype.setDisabled(true);
			//radioSliptype.setValue(param.slip_typ);
				
		} else {
			
			dma_result.set("nm", session.cn);
			dma_result.set("empno", session.empno);
			dma_result.set("req_psn", session.sn);
			dma_result.set("req_ymd", ymd);
			dma_result.set("cud_type", "C");
			btn_del.setDisabled(true); //최초 저장 전 버튼 disabled
			com.file.create(upload_A.getID(), {fileKey : scwin.data.typeCode, mode: 'edit'});	
			com.file.create(upload_B.getID(), {fileKey : scwin.data.typeCode, mode: 'edit'});
			
			dma_result.set("accnt_no", "FF210002");
			grp_accnt_rspns.setDisabled(true);	
		}
	};
	
	scwin.onpageunload = function() {
		
	};
	
	//목록
	scwin.btn_move_onclick = function(e) {
		com.moveList("G309");
	};
	
	/*
     * 조회
     */
	scwin.search = function(){
		com.execute(sbm_selectData, {
			successCallback : function(e){ 				
				// 결재상태에 따른 버튼 제어
				com.biz.disabledButtons(dma_result.get("apr_state"), dma_result.get("req_no"));
				if(dma_result.get("req_no") != ""){
					if(dma_result.get("intgr_mngmt_unit") == "RDK003"){
						grp_accnt_rspns.setDisabled(true);		
					}
					if(dma_result.get("apr_state") > "XAD020"){						
						btn_save.setDisabled(true);
						screen.setDisabled(true); //전체화면 disable
						btn_move.setDisabled(false);
						btn_print.setDisabled(false);
						// 1010102 시스템관리자M, 1010001 시스템관리자A, 1015119  장비통합관리자
						if(/1010102|1010001|1015119/.test(com.getLoginInfo("roleCode"))) {
							btn_save.setDisabled(false);	
							//grp_rvew.show();
							grp_rvew.setStyle("display", "");			
							grp_title.show();
							grp_rvew.setDisabled(false);			
						}						
						btn_apr.setDisabled(true);
						btn_del.setDisabled(true);
					}else {
						if(dma_result.get("req_psn") != session.sn){
							btn_save.setDisabled(true);
							btn_apr.setDisabled(true);
							btn_del.setDisabled(true);
						}else{
							btn_save.setDisabled(false);
							btn_apr.setDisabled(false);
							btn_del.setDisabled(false);	
						}						
					}											
							
				}
				var attach_file_no = dma_result.get("attach_file_no");				
				var rst_attach_file_no = dma_result.get("rst_attach_file_no");
				com.file.create(upload_A.getID(), {fileKey : com.isEmpty(attach_file_no) ? scwin.data.typeCode : attach_file_no, mode : 'view' });
				com.file.create(upload_B.getID(), {fileKey : com.isEmpty(rst_attach_file_no) ? scwin.data.typeCode : rst_attach_file_no, mode : 'view' });
				
				scwin.chkbox_diabled(dma_result.get("expns_cd"), "R");
			}
		});
	};
	
	//오즈 인쇄
	scwin.btn_print_onclick = function(e) {
		var node = "req_no=" + dma_result.get("req_no");
		
		if(dma_result.get("req_no") != ""){
			com.oz.openPopup({
				ozrCode : "A030",
				param: node
			});
		}else{
			com.alert("데이터 저장 이후 출력 가능합니다.");
			return;
		}
	};
	
	/*삭제*/
	scwin.btn_del_onclick = function(e) {
		com.confirm("message.xom.wq.022"/*삭제하시겠습니까?*/, function(ret){
			if(ret){
				com.file.deleteAll(function(){
						com.execute(sbm_deleteData, {
							successCallback : function(e) {
								com.moveList("G309", {});
							}
						});
				});								
			}
		});
	};
	
	/*저장*/
	scwin.btn_save_onclick = function(e) {
		
		//저장전 validate 체크
		if(!scwin.preSave("SAVE")) return;

		com.file.transport( function(attachObj){
			dma_result.set("attach_file_no", attachObj.upload_A);
			dma_result.set("rst_attach_file_no", attachObj.upload_B);
			
			com.execute(sbm_saveData, {
				successCallback : function(e) {
					com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {			
						dma_result.set("cud_type", "U");								
						if(aprYn == "Y"){
							com.execute(sbm_SaveAprData, {
								successCallback : function(e){							
									com.movePageMenu("A001", { "reqNo" :  dma_result.get("req_no")});									
								}
							});
						}else{							
							scwin.search();
						}
												
					});
				}
			});
		});
	};
	
	
	/**
	* 저장전 필수 체크
	*/ 
	scwin.preSave = function(flag) {
				
		var validData = [
			{dataObj : dma_result
			   , valInfoArr :[
				    { id : "intgr_mngmt_unit", mandatory : true}
				   ,{ id : "accnt_no", mandatory : true } 
				   ,{ id : "enfrc_ncsst", mandatory : true } 
				   ,{ id : "enfrc_use", mandatory : true } 
				   ,{ id : "aset_no", mandatory : true }
				   ,{ id : "expns_cd", mandatory : true }
				   ,{ id : "req_amt", mandatory : true }
				   ,{ id : "req_region", mandatory : true } 
				   ,{ id : "enfrc_chk", mandatory : true }					   				  
				]
			  , isCheckModified : false
			}
		];
			
		if(!com.validateData(validData)) return false;
		
		if(dma_result.get("intgr_mngmt_unit") == "RDK001" && dma_result.get("accnt_rspns") == ""){
			alert("통합관리단위가 연구책임자인경우 계정책임자 입력사항입니다.");
			return;
		}
			
		if(com.file.action.getTotalFileCount(upload_A.getID()) < 1){
			com.alert("첨부파일은 필수등록 항목입니다.");
			return false;	
		}		
		
		if(dma_result.get("enfrc_chk") == "" && dma_result.get("enfrc_chk2") == ""){
			alert("집행체크리스트 중 1개는 필수 선택사항입니다.");
			return;	
		} 
				
		return true;
	}

	scwin.rad_intgr_mngmt_unit_onviewchange = function(info) { //debugger;
		if(info.value == "RDK003"){ 
			dma_result.set("accnt_no", "FF210002");
			dma_result.set("accnt_rspns", "");
			dma_result.set("accnt_rspns_nm", "");
			dma_result.set("accnt_rspns_empno", "");
			grp_accnt_rspns.setDisabled(true);	
			
		}else{
			dma_result.set("accnt_no", "FF210001"); 
			grp_accnt_rspns.setDisabled(false);	
		}
	};


	//계정책임자 찾기
	scwin.btn_charger_onclick = function(e) {
		var data = { empno : ibx_empno.getValue()
					, work_clsf : "HAG010"
					, ref_yn : (ibx_empno.getValue() == "")?"Y" : "N"
					};
		
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			ibx_empno.setValue(ret.empno);
			ibx_nm.setValue(ret.nm);
			
			dma_result.set("accnt_rspns", ret.syspayno); //debugger;
			dma_search.set("syspayno", ret.syspayno);
			dma_search.set("bugt_yr", year);
			
			
			com.execute(sbm_selectAccntData, {
				successCallback : function(e){ 
					if(dma_accntNo.get("accnt_no") == ""){
						alert("보유한 연구장비통합관리 계정이 없습니다.");
						dma_result.set("accnt_no", "");
						dma_result.set("accnt_rspns_empno", "");
						dma_result.set("accnt_rspns", "");
						inp_relatno.setStyle("display", "none");
						return;	
					}else{
						inp_relatno.setStyle("display", "");
						dma_result.set("accnt_no", dma_accntNo.get("accnt_no"));
						inp_relatno.setValue(dma_accntNo.get("relat_no"));	
						dma_accntNo.setEmptyValue();					
					}					
				}
			});
		});
	};
	//계정책임자 초기화
	scwin.ibx_empno_onchange = function(info) {
		if(info.newValue == "") {
			ibx_nm.setValue("");
			dma_result.set("accnt_rspns", "");
		}
	};

	scwin.btn_asset_onclick = function(e) {
		com.pop.open(com.pop.TYPE.ZEUS, "", function(ret){		//debugger;	
			dma_result.set("aset_no", ret.aset_no);
			dma_result.set("aset_kor_nm", ret.aset_nm);
			dma_result.set("depthed_nm", ret.nm +"("+ ret.empno +")");
			dma_result.set("ntis_reg_no", ret.ntis_reg_no);
			dma_result.set("intro_amt", ret.intro_amt);
			dma_result.set("intro_ymd", ret.intro_ymd);
			dma_result.set("user_nm", ret.user_nm);
			dma_result.set("aset_eng_nm", ret.aset_eng_nm);
			dma_result.set("posi_addr", ret.posi_addr);
			inp_fincc.setValue(ret.finnc);
		});
	};
	
	//사용용도 이벤트
	scwin.rad_expns_cd_onviewchange = function(info) {
		scwin.chkbox_diabled(info.value, "C");
	};
	
	scwin.chkbox_diabled = function(expns_cd, type){
		chk_abc007.setDisabled(true);
		chk_abc006.setDisabled(true);			
		chk_abc005.setDisabled(true);
		chk_abc004.setDisabled(true);
		chk_abc003.setDisabled(true);
		chk_abc002.setDisabled(true);
		chk_abc001.setDisabled(true);
		
		if(type == "C"){
			dma_result.set("enfrc_chk2", "");
			dma_result.set("enfrc_chk", "");
		}
		
		if(expns_cd == "431"){
			dma_result.set("enfrc_chk", "ACB007");			
		}else if(expns_cd == "262"){
			dma_result.set("enfrc_chk", "ACB006");					
		}else{			
			chk_abc007.setDisabled(true);
			chk_abc006.setDisabled(true);
			if(dma_result.get("apr_state") < "XAD020"){
				chk_abc005.setDisabled(false);
				chk_abc004.setDisabled(false);
				chk_abc003.setDisabled(false);
				chk_abc002.setDisabled(false);
				chk_abc001.setDisabled(false);
			}
		}
		
	};
	//결재신청
	scwin.btn_apr_onclick = function(e) {
		aprYn = "Y";
		scwin.btn_save_onclick();			
	};
