head.script:
	/****************************************************
	 * 화면 설명 : 직인인감신청
	 * 변경 이력 : 2023-08-14	lsh	최초 작성
	 * serviceId : C200
	 ****************************************************/	
	
	// 전역변수로 사용하기 위해 scwin 객체안에 선언  
	scwin.data={};
        
	scwin.onpageload = function() {
		// dataMap 초기화
		com.initDataMap(dma_resultMap);
		
		var session = com.getLoginInfo();
		var param = com.getParameter();
		
		// parameter 설정
		dma_search.setJSON(com.getParameter());
		
		// 영역내의 컴포넌트에 엔터키 이벤트 발생시 조회 수행
		com.setEnterKeyEvent(grp_hum_pop, scwin.openHumUserPop);
		com.setEnterKeyEvent(grp_dept_pop, scwin.selectDept);
		
		scwin.data.cudType = "C"; // 저장구분
		scwin.data.status = "";// 결재신청상태구분
		dma_resultMap.set("cud_type", "C"); // 저장상태구분셋팅
		dma_resultMap.set("seal_clsf", "CGA001"); // 인장종류셋팅
		
		// 결재데이터용 변수 선언(전역)
		scwin.data.apr_emp_no = "";
		scwin.data.apr_dept_nm = "";
		scwin.data.apr_nm = "";
		scwin.data.apr_posi = "";
		scwin.data.apr_use_orign_ymd = "";
		scwin.data.apr_use_cls_ymd = "";
		scwin.data.apr_mngmt_respn_psn_dept_nm = "";
		
		// 기간 역전 체크 이벤트 바인딩
		com.checkPeriod(inputCalendarUseOrignYmd, inputCalendarUseClsYmd);
		
		if(dma_search.get("req_no")){
			scwin.data.cudType = "U";
			
			scwin.search();
		}else{ // 최초작성 일때
			// 신청일자 셋팅
			dma_resultMap.set("req_ymd", com.getServerDateTime("yyyyMMdd"));
			
			// 사용시작일자 셋팅
			inputCalendarUseOrignYmd.setValue(com.getServerDateTime("yyyyMMdd"));
			
			// 버튼제어
			com.biz.disabledButtons(dma_resultMap.get("apr_state"), dma_resultMap.get("req_no"));
		}
	};
	
	scwin.onpageunload = function() {
		
	};
	
	scwin.setDisable = function(flag) {
		inputStampChar.setReadOnly(flag);
   		selectboxStampStd.setDisabled(flag);
   		inputSize.setReadOnly(flag);
   		inputDeptCd.setReadOnly(flag);
   		inputUseDeptNm.setReadOnly(flag);
   		inputMngmtRespnEmpNo.setReadOnly(flag);
   		inputMngmtRespnSyspayNm.setReadOnly(flag);
   		inputUseUsg.setReadOnly(flag);
   		inputStampReqResn.setReadOnly(flag);
   		inputCalendarUseOrignYmd.setReadOnly(flag);
   		inputCalendarUseClsYmd.setReadOnly(flag);
   		inputSealMatrl.setReadOnly(flag);
   		inputSpclabItem.setReadOnly(flag);
   		
   		btn_dpet_search.setDisabled(flag);
   		btn_hum_search.setDisabled(flag);
	}
	
	scwin.search = function() {
		com.execute(sbm_selectSealUse, {
			successCallback : function(e) {
				var aprStateCode = dma_resultMap.get("apr_state");
				var reqSyspayno = dma_resultMap.get("regst_psn_syspayno");

				if(reqSyspayno == com.getLoginInfo("sn") && (aprStateCode != "XAD005" && aprStateCode != "XAD010" && aprStateCode != "XAD013" && aprStateCode != "XAD015")){
					scwin.setDisable(true);
				}else if(reqSyspayno != com.getLoginInfo("sn")){
					scwin.setDisable(true);
				}
				
				// 결재상태에 따른 버튼제어
				com.biz.disabledButtons(dma_resultMap.get("apr_state"), dma_resultMap.get("req_no"));
				
				var useOrignYmd = dma_resultMap.get("use_orign_ymd");
	        	var useOrignYYYY = useOrignYmd.substring(0,4);
	        	var useOrignMM = useOrignYmd.substring(4,6);
	        	var useOrignDD = useOrignYmd.substring(6,8);
	        	
	        	useOrignYmd = useOrignYYYY+"-"+useOrignMM+"-"+useOrignDD;
	        	
	        	var useClsYmd = dma_resultMap.get("use_cls_ymd");
	        	var useClsYYYY = useClsYmd.substring(0,4);
	        	var useClsMM = useClsYmd.substring(4,6);
	        	var useClsDD = useClsYmd.substring(6,8);
	        	
	        	useClsYmd = useClsYYYY+"-"+useClsMM+"-"+useClsDD;
				
				// 결재용데이터 셋팅
				scwin.data.apr_emp_no = "(" + dma_resultMap.get("req_emp_no") + ")";
				scwin.data.apr_dept_nm = dma_resultMap.get("req_dept_nm");
				scwin.data.apr_nm = dma_resultMap.get("req_psn_nm");
				scwin.data.apr_posi = dma_resultMap.get("req_posi_nm");
				scwin.data.apr_use_orign_ymd = useOrignYmd;
				scwin.data.apr_use_cls_ymd = useClsYmd;
				scwin.data.apr_mngmt_respn_psn_dept_nm = dma_resultMap.get("mngmt_respn_psn_dept_nm");
				
			}
		});
	};
	
	scwin.save = function() {
		if(scwin.data.status == "apr"){
			// 결재여부셋팅
			dma_resultMap.set("approval", true); // 결재여부
			
			// 결재용 데이터 셋팅
			dma_resultMap.set("apr_emp_no", scwin.data.apr_emp_no); 
			dma_resultMap.set("apr_dept_nm", scwin.data.apr_dept_nm); 
			dma_resultMap.set("apr_nm", scwin.data.apr_nm); 
			dma_resultMap.set("apr_posi", scwin.data.apr_posi); 
			dma_resultMap.set("apr_use_orign_ymd", scwin.data.apr_use_orign_ymd); 
			dma_resultMap.set("apr_use_cls_ymd", scwin.data.apr_use_cls_ymd); 
			dma_resultMap.set("apr_mngmt_respn_psn_dept_nm", scwin.data.apr_mngmt_respn_psn_dept_nm);
		}else{
			dma_resultMap.set("approval", false); // 결재여부
		}
		
		dma_resultMap.set("cud_type", scwin.data.cudType); // 저장상태구분셋팅
		dma_resultMap.set("seal_clsf_nm", radioSealClsf.getText()); // 인장구분명 셋팅
		
		com.execute(sbm_saveSealUse, {
			successCallback : function(e) { // 성공 콜백
				if(dma_resultMap.get("approval")){
					// 결재화면 이동
					com.movePageMenu("A001", { "reqNo" :  dma_search.get("req_no")});
				}else{
					com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
						scwin.search();
					});	
				}	
			}
		});
	}
	
	/**
	 * 필수값 체크
	 */
	scwin.validation = function() {
		return com.validateData(dma_resultMap, [
					{ id : "seal_clsf", mandatory : true},
					{ id : "stamp_char", mandatory : true},
					{ id : "stamp_std", mandatory : true},
					{ id : "size", mandatory : true},
					{ id : "mngmt_respn_psn_nm", mandatory : true},
					{ id : "use_dept_nm", mandatory : true},
					{ id : "use_usg", mandatory : true},
					{ id : "stamp_req_resn", mandatory : true},
					{ id : "use_orign_ymd", mandatory : true},
					{ id : "use_cls_ymd", mandatory : true},
					{ id : "seal_matrl", mandatory : true}
			   ], false);
	}	
	
	/**
	 * 저장 버튼 클릭 이벤트
	 */
	scwin.btn_save_onclick = function(e) {
		if(!scwin.validation()) return;
		
		scwin.data.status = "save";
		
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret) {
			if (ret) {
				scwin.save(true);
			}
		});
	};
	
	
	/**
	 * 목록 버튼 클릭시 이벤트
	 */
	scwin.btn_pre_onclick = function(e) {
		var menuId = com.getParameter("menuID") || "C201";
		com.moveList(menuId);
	};
	
	/**
	 * 삭제 버튼 클릭시 이벤트
	 */
	scwin.btn_del_onclick = function(e) {
		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function( ret) {
			if (ret) {
				com.execute(sbm_deleteSealUse, {
					successCallback : function(e) { // 성공 콜백
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							com.moveList(com.getParameter("menuID"), {mod :"delete"});
						});
					}
				});
			}
		});
	};
	
	/**
	 * 결재신청 버튼 클릭시 이벤트
	 */
	scwin.btn_apr_onclick = function(e) {
		if(!scwin.validation()) return;
		scwin.data.status = "apr";
		com.confirm("결재신청을 하시려면 [확인]을 클릭하세요", function( ret) {
			scwin.save(true);
		});
	};
	
	/**
	 * 부서 검색 버튼 클릭 이벤트
	 */
	scwin.btn_dpet_search_onclick = function(e) {
		scwin.selectDept();
	};
	
	/**
	 * 부서 조회 팝업
	 */	
	scwin.selectDept = function(data) {
		// 버튼(img)으로 팝업함수 바로 호출 처음 변수에 아무것도 넣지 않으면 최초 서브미션도 실행 안함.
		data == "img"  ? data = {dept_nm : ""} : com.isKorean(inputUseDeptNm.getValue()) ? data = { dept_nm : inputUseDeptNm.getValue()} : data = { dept_cd : inputDeptCd.getValue()};
		
		com.pop.open( com.pop.TYPE.DEPT, data, function(ret){
			inputDeptCd.setValue(ret.dept_cd);
			inputUseDeptNm.setValue(ret.dept_nm);
			dma_resultMap.set("use_dept_new_ymd", ret.dept_new_ymd);
		});
	};
	
	/**
	 * 사용자 검색 버튼 클릭 이벤트
	 */	
	scwin.btn_hum_search_onclick = function(e) {
		scwin.openHumUserPop();
	};
	
	/**
	 * 사용자검색 팝업 실행
	 */
	scwin.openHumUserPop = function(){
		var vEmpno = inputMngmtRespnEmpNo.getValue(); 
		var data = { empno : vEmpno, work_clsf : "HAG010" };
		
		com.pop.open(com.pop.TYPE.HUM, data, function(ret){
			inputMngmtRespnSyspayNm.setValue(ret.nm);
			inputMngmtRespnEmpNo.setValue(ret.empno);
			outputMngmtRespnPsnDept.setValue(ret.dept_nm);
			
			dma_resultMap.set("mngmt_respn_psn_dept_cd", ret.dept_cd);
			dma_resultMap.set("mngmt_respn_psn_dept_new_ymd", ret.dept_new_ymd);
			dma_resultMap.set("mngmt_respn_psn_syspayno", ret.syspayno);
		});
	}
	
	/**
	 * 각인모양 셀렉트박스 이벤트 
	 */	
	scwin.selectboxStampStd_onchange = function(info) {
		if(selectboxStampStd.getValue() == "원"){				
			outputExpresstion.setValue("(지름으로 표기)");
		}else if(selectboxStampStd.getValue() == "사각형"){
			outputExpresstion.setValue("(가로 X 세로 로 표기)");
		}else{
			outputExpresstion.setValue("");
		}
	};

	/**
	 * 사용시작일 캘린더 이벤트 
	 */	
	scwin.inputCalendarUseOrignYmd_onviewchange = function(info) {
		var toDay = com.getServerDateTime("yyyyMMdd");
		if(inputCalendarUseOrignYmd.getValue() < toDay){
			com.alert("현재일 이전 날짜는 신청하실 수 없습니다.", function(){
				inputCalendarUseOrignYmd.setValue(toDay);
			});
		}
	};
