head.script:


	scwin.onpageload = function() {

		var codeOptions = [ { code : "CGK", compID :"area_list_sel"}];
		com.setCmnCd(codeOptions, function() {

			var use_orign_ymd = com.getParameter("use_orign_ymd");
			var use_orign_time = com.getParameter("use_orign_time");
			var use_cls_ymd = com.getParameter("use_cls_ymd");
			var use_cls_time = com.getParameter("use_cls_time");
			var req_seq = com.getParameter("req_seq");
			var syspano = com.getLoginInfo("sn");
			var req_psn_dept = com.getLoginInfo('deptName');
			var req_psn_dept_new_ymd = com.getLoginInfo('deptNewYmd');
			var req_psn_mail_addr = com.getLoginInfo('email');

			dma_carUse.set("use_orign_ymd", use_orign_ymd);
			dma_carUse.set("use_orign_time", use_orign_time);
			dma_carUse.set("use_cls_ymd", use_cls_ymd);
			dma_carUse.set("use_cls_time", use_cls_time);
			dma_carUse.set("req_seq", req_seq);
			dma_carUse.set("req_psn_syspayno", syspano);
			dma_carUse.set("regst_psn_syspayno", syspano);
			dma_carUse.set("req_psn_dept", req_psn_dept);
			dma_carUse.set("req_psn_dept_new_ymd", req_psn_dept_new_ymd);
			dma_carUse.set("req_psn_mail_addr", req_psn_mail_addr);

			selectboxUseOrignYmd.setDisabled(true);
			selectboxUseOrignTime.setDisabled(true);
			selectboxUseClsYmd.setDisabled(true);
			selectboxUseClsTime.setDisabled(true);
			area_list_sel.setDisabled(true);
			selectboxCarNm.setDisabled(true);

			scwin.searchCar();
			scwin.searchCgsDetail();

		});


	};

	scwin.onpageunload = function() {

	};
	//날짜설정관련 
	var now = new Date();
	var year = now.getFullYear();
	var month = now.getMonth();
	var date = now.getDate();
	var firstDate = new Date(year, month , 1);
	var lastDate = new Date(year, month + 1, 0);
	var toDate = new Date(year, month , date);

	//차량조회
	scwin.searchCar = function(info) {

		dlt_carList.removeAll();
		com.execute(sbm_selectCarList, {
			successCallback : function(e) {

				dlt_carList.setJSON(e.responseJSON.elData.cgsCarUseVoList);
			}
		});
	};

	//지역변경후차량조회
	scwin.searchCar_change = function() {

		var region = dma_carUse.get("req_region_cd"); 

		dma_carListSearch.set("region_cd",region);

		 scwin.searchCar();

	};

	//배차조회
	scwin.searchCgsDetail = function() {

		com.execute(sbm_selectCgsDetail, {
			successCallback : function(e) {
				var bizwrkPrityRank = dma_carUse.get("bizwrk_prity_rank");

				cData = bizwrkPrityRank.split(";");
				checkbox1.setValue(cData[0]);
				checkbox2.setValue(cData[1]);
				checkbox3.setValue(cData[2]);
				checkbox4.setValue(cData[3]);
				checkbox5.setValue(cData[4]);
				checkbox6.setValue(cData[5]);
				checkbox7.setValue(cData[6]);
				checkbox8.setValue(cData[7]);

				var req_psn_syspayno = dma_carUse.get("req_psn_syspayno");
				var sn = com.getLoginInfo('sn');
				var charg_syspayno = dma_carUse.get("charg_syspayno");

				if ( sn != charg_syspayno ) {
					btn_rcpt.hide();
					btn_return.hide();
					btn_carRpt.hide();
					btn_saveCgs.hide();
					btn_delCgs.hide();
					btn_reject.hide();
				}

				if ( sn != req_psn_syspayno && sn != charg_syspayno ) {
					grp_contentAll.setDisabled(true);
					btn_close.setDisabled(false);
					btn_print.setDisabled(false);
				}
			}
		});
	};

	//저장클릭이벤트
	scwin.btn_saveCgs_onclick = function() {
		var bizwrkPrityRank = checkbox1.getValue() + ";" + checkbox2.getValue() + ";" + checkbox3.getValue() + ";" + checkbox4.getValue() + ";" + checkbox5.getValue() + ";"
						+ checkbox6.getValue() + ";" + checkbox7.getValue() + ";" + checkbox8.getValue();

		dma_carUse.set("bizwrk_prity_rank",bizwrkPrityRank);
		if(scwin.vaildCheck() == "N"){
			return;
		} else {
			scwin.saveCgs();	
		}
	};

	//저장
	scwin.saveCgs = function() {
			com.execute(sbm_saveCgs, {

				successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								scwin.closeActionFlag = true; // 삭제 후 닫을때는 변경분 체크 안하도록
								com.closePopup({}); // 팝업 닫기 (콜백 수행을 위해 빈 객체 넘김)
							});
						}
			});
	};

	//삭제클릭이벤트
	scwin.btn_deleteCgs_onclick = function() {
		scwin.deleteCgs();
	};

	//삭제
	scwin.deleteCgs = function() {

			com.execute(sbm_deleteCgs, {

				successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								scwin.closeActionFlag = true; // 삭제 후 닫을때는 변경분 체크 안하도록
								com.closePopup({}); // 팝업 닫기 (콜백 수행을 위해 빈 객체 넘김)
									});
								}
				});

	};




	scwin.vaildCheck = function() {
			if (dma_carUse.get("charg_syspayno") == com.getLoginInfo("sn") && dma_carUse.get("req_state") == "CGO004") return;

			var sYmd = selectboxUseOrignYmd.getValue();
			var eYmd = selectboxUseClsYmd.getValue();
			var sTime = selectboxUseOrignTime.getValue();
			var eTime = selectboxUseClsTime.getValue();
			sTime = sTime.replace(':', '');
			eTime = eTime.replace(':', '');
		   	sTime = Number(sTime);
			eTime = Number(eTime);


			if (sYmd < getDateStr(toDate) || eYmd < getDateStr(toDate)) {
				alert("지난 날짜는 예약하실 수 없습니다.");
				return "N";
			}

			if (sYmd > eYmd) {
				alert("시작일은 종료일보다 커야합니다.");
				return "N";
			}

			if (sYmd == eYmd) {
				if(sTime >= eTime){
					alert("시작시간은 종료시간보다 커야합니다.");
					return "N";
				}
			}

			if(area_list_sel.getValue() == null || area_list_sel.getValue() == '')
			{
				alert("신청 지역은 반드시 입력해야 합니다.\n  확인해 주세요!!");	
				return "N";
			}

			if(inputDestin.getValue() == null || inputDestin.getValue() == '')
			{
				alert("행선지는 반드시 입력해야 합니다.\n  확인해 주세요!!");	
				return "N";
			}

			if(selectboxCarNm.getValue() == null || selectboxCarNm.getValue() == '')
			{
				alert("차량은 반드시 입력 해야 합니다.\n  확인해 주세요!!");	
				return "N";
			}

			if(inputCont.getValue() == null || inputCont.getValue() == '')
			{
				alert("사업 목적은 반드시 입력해야 합니다.\n  확인해 주세요!!");	
				return "N";
			}

	}

	scwin.btn_close_onclick = function() {
		com.closePopup({}); // 팝업 닫기 (콜백 수행을 위해 빈 객체 넘김)
	};	

	//날짜구하기
	function getDateStr(date) {
	    var sYear = date.getFullYear();
	    var sMonth = date.getMonth() + 1;
	    var sDate = date.getDate();

	    sMonth = sMonth > 9 ? sMonth : "0" + sMonth;
	    sDate  = sDate > 9 ? sDate : "0" + sDate;
	    return String(sYear) + String(sMonth) + String(sDate);
	}

	scwin.btn_return_onclick = function(e) {
		if (!selectboxUseRtnYmd.getValue()) {
			com.alert("반납일시는 필수값입니다.");
			return;	
		} else {
			scwin.saveCgs();
		}
	};

	scwin.btn_rcpt_onclick = function(e) {
		if (!selectboxUseRcvYmd.getValue()) {
			com.alert("수령일시는 필수값입니다.");
			return;	
		} else {
			scwin.saveCgs();
		}
	};

