	};

	scwin.btn_carRpt_onclick = function(e) {
		var vusage_dt_fr = selectboxUseOrignYmd.getValue().substr(0,6) + '%';
		var vcar_no = dma_carUse.get('car_info');

		if (vcar_no == '') {
			com.alert('차량을 선택하세요.');	
			return;
		} else {
			var tmp = vcar_no.split(':');
			var tmpCarNo = "";

			if (tmp[0].length > 10) {
				tmpCarNo = tmp[0].substr(0,7);
			} else {
				tmpCarNo = tmp[0].substr(2,7);
			}

			tmpCarNo = tmp[0];

			var node = "use_orign_ymd=" + vusage_dt_fr + "&car_no=" + tmpCarNo.trim();
			var reportObj = {
				ozrCode : "C010",
				param : node
			};

			com.oz.openPopup(reportObj);
		}
	};

	scwin.getTotalAmt = function() {
		var actualAmt =  Number(dma_carUse.get("arriv_meter")) - Number(dma_carUse.get("start_meter"));

		dma_carUse.set("mvmnt_dist",actualAmt);
	};

	scwin.start_mt_onviewchange = function(info) {
		scwin.getTotalAmt();
	};

	scwin.end_mt_onviewchange = function(info) {
		scwin.getTotalAmt();
	};

	scwin.btn_reject_onclick = function(e) {
		dma_carUse.set("req_state", "CGO005");
		scwin.saveCgs();
	};

	/**
	 * 출발유량 공백 입력시 0으로 치환
	 */
	scwin.start_flow_onchange = function(info) {
		if(start_flow.getValue() == ""){
			start_flow.setValue(0);
		}
	};

	/**
	 * 도착유량 공백 입력시 0으로 치환
	 */
	scwin.arriv_flow_onchange = function(info) {
		if(arriv_flow.getValue() == ""){
			arriv_flow.setValue(0);
		}
	};
