	scwin.onpageload = function() {

		var now = new Date();
		var year = now.getFullYear();
		var month = now.getMonth();
		var date = now.getDate();
		var toDate = new Date(year, month , date);
		var toMonth = new Date(year, month , date);

		scwin.show_start_form();
		rdo_styClsf.setValue("S");
		group_l.setStyle( "display", "none");
		jeju_detail.setStyle( "display", "none");
		inputCalendarUseYmd.setValue(getDateStr(toDate));
		inputCalendarUseYm.setValue(getDateStr(toMonth));

		dma_user.set("use_psn_empno", com.getLoginInfo("empno"));
		dma_user.set("use_psn_nm", com.getLoginInfo("cn"));
		dma_user.set("use_psn_syspayno", com.getLoginInfo("sn"));
		dma_user.set("use_posi_cd", com.getLoginInfo("posiCode"));

		scwin.init_region_status();

		groupRFe.setStyle( "display", "none");
		reg_area.setStyle( "display", "block");



	};

	scwin.onpageunload = function() {

	};

	scwin.show_start_form = function(e) {

		//날짜설정관련 
		var now = new Date();
		var year = now.getFullYear();
		var month = now.getMonth();
		var date = now.getDate();
		var toDate = new Date(year, month , date);

		var login_syspayno = com.getLoginInfo('sn');
		var today = getDateStr(toDate);

		dma_dormReqSearch.set("syspayno", login_syspayno);
		dma_dormReqSearch.set("ymd", today);

		com.execute(sbm_selectDormReqList, {
			successCallback : function(e) {

			}
		});
	};

