	/** 포맷을 지정한 날짜를 가져온다. */
	function gfnGetDateFormat(sDate, split) {
		if (typeof split == "undefined" || split == "" || split == null)
			split = "-";

		sDate = sDate.replace(/[^0-9]/g, "");

		var len = sDate.length;

		if (len <= 4)
			return sDate;
		if (len <= 6)
			return sDate.substring(0, 4) + split + sDate.substring(4, len);
		if (len <= 8)
			return sDate.substring(0, 4) + split + sDate.substring(4, 6) + split + sDate.substring(6, len);
	}


	scwin.selectListCgsDormApprovDate = function() {
			com.execute(sbm_selectListCgsDormApprovDate, {
				successCallback : function(e) {

					var numDays = dlt_approvDate.getTotalRow();
					scwin.gvar.consecutiveDate = 0;
					scwin.gvar.approvedDates = [];

					for(var i = 0 ; i < numDays ; i++){
						if (e.responseJSON.elData.cgsDormReqVoList.at(i).req_state_nm == "승인" && e.responseJSON.elData.cgsDormReqVoList.at(i).stay_clsf_nm == "단기" && e.responseJSON.elData.cgsDormReqVoList.at(i).region_nm == "제주") {
						    scwin.gvar.approvedDates.push(e.responseJSON.elData.cgsDormReqVoList.at(i).use_from_ymd);
						}
					}

					scwin.gvar.approvedDates.sort();

					if( scwin.gvar.approvedDates.length > 0 ) {
						if( (scwin.gvar.approvedDates.indexOf(gcm.date.addDate(gfnGetDateFormat(scwin.gvar.approvedDates[0]), 9)) > -1 || scwin.gvar.approvedDates.indexOf(gcm.date.addDate(gfnGetDateFormat(scwin.gvar.approvedDates[0]), -9)) > -1 ) && scwin.gvar.approvedDates.length > 9 ){
							scwin.gvar.consecutiveDate = 1;
						}
					}

					ipsel_jejudate.removeAll();

					for (var i = 0 ; i < 30 ; i++) {
						ipsel_jejudate.addItem( i+1, i+1+"일");
					}

					ipsel_jejudate.setSelectedIndex(0);					
				}
			});

	};

	scwin.insertCgsDorm = function() {
		com.execute(sbm_insertCgsDorm, {
			successCallback : function(e) {

					if (rdo_styClsf.getValue() == "L") { // 신청 숙박구분이 장기인 경우

						if (selectboxUseYmOpt.getValue() == "1") { // 2개월 일괄신청이면

							var base_yyyy = inputCalendarUseYm.getValue().substr(0, 4);
							var base_month = parseInt(inputCalendarUseYm.getValue().substr(4, 2), 10) - 1; // zero-based month value
							var use_from_ymd = WebSquare.date.getFormattedDate(new Date(base_yyyy, base_month + 1, 1), "yyyyMMdd");
							var use_to_ymd = WebSquare.date.getFormattedDate(new Date(base_yyyy, base_month + 2, 0), "yyyyMMdd");

							dma_dormReq.set("use_from_ymd", use_from_ymd);
							dma_dormReq.set("use_to_ymd", use_to_ymd);

							com.execute(sbm_insertCgsDorm, {
								successCallback : function(e) {
									alert("기숙사 신청이 완료 되었습니다");
									//gcm.win.isRefreshing = true;
									//$p.reinitialize();
									scwin.btn_list_onclick();

								}
							});

						} else if (selectboxUseYmOpt.getValue() == "2") { // 3개월 일괄신청이면

							var base_yyyy = inputCalendarUseYm.getValue().substr(0, 4);
							var base_month = parseInt(inputCalendarUseYm.getValue().substr(4, 2), 10) - 1; // zero-based month value
							var use_from_ymd = WebSquare.date.getFormattedDate(new Date(base_yyyy, base_month + 1, 1), "yyyyMMdd");
							var use_to_ymd = WebSquare.date.getFormattedDate(new Date(base_yyyy, base_month + 2, 0), "yyyyMMdd");

							dma_dormReq.set("use_from_ymd", use_from_ymd);
							dma_dormReq.set("use_to_ymd", use_to_ymd);

							com.execute(sbm_insertCgsDorm, {
								successCallback : function(e) {

									var base_yyyy = inputCalendarUseYm.getValue().substr(0, 4);
									var base_month = parseInt(inputCalendarUseYm.getValue().substr(4, 2), 10) - 1; // zero-based month value
									var use_from_ymd = WebSquare.date.getFormattedDate(new Date(base_yyyy, base_month + 2, 1), "yyyyMMdd");
									var use_to_ymd = WebSquare.date.getFormattedDate(new Date(base_yyyy, base_month + 3, 0), "yyyyMMdd");

									dma_dormReq.set("use_from_ymd", use_from_ymd);
									dma_dormReq.set("use_to_ymd", use_to_ymd);

									com.execute(sbm_insertCgsDorm, {
										successCallback : function(e) {
											alert("기숙사 신청이 완료 되었습니다");
											//gcm.win.isRefreshing = true;
											//$p.reinitialize();
										scwin.btn_list_onclick();

										}
									});

								}
							});

						}else{
							alert("기숙사 신청이 완료 되었습니다");
								//gcm.win.isRefreshing = true;
								//$p.reinitialize();
								scwin.btn_list_onclick();
						}
					}else{
						alert("기숙사 신청이 완료 되었습니다");
						//gcm.win.isRefreshing = true;
						//$p.reinitialize();
						scwin.btn_list_onclick();
					}



			}
		});			
	};

