	scwin.selectListHoliday = function() {
		console.log("!!!!!!!!selectListHoliday");
			com.execute(sbm_selectListHoliday, {
				successCallback : function(e) {
					scwin.gvar.holidayList = [];
					var numDays = dlt_holiyDay.getTotalRow();

					for(var i = 0 ; i < numDays ; i++){
						if(e.responseJSON.elData.cgsDormReqVoList.at(i).off_yn == "Y"){

							scwin.gvar.holidayList.push(e.responseJSON.elData.cgsDormReqVoList.at(i).ymd);
						}
					}
					scwin.gvar.holidayList.sort();


					for (var i = 1; i < 7; i++) {
					scwin.gvar.holidayCompBList.push(gcm.date.addDate(gfnGetDateFormat(inputCalendarUseYmd.getValue()), -i));
					scwin.gvar.holidayCompAList.push(gcm.date.addDate(gfnGetDateFormat(inputCalendarUseYmd.getValue()), i));
					}

					// scwin.gvar.packageDates 초기화안됨
					scwin.gvar.packageDates.push(inputCalendarUseYmd.getValue());
					//신청날이 휴일일때
					if (scwin.gvar.holidayList.indexOf(inputCalendarUseYmd.getValue()) > -1) {
				        // 앞 비교
				        for (var i = 0; i < scwin.gvar.holidayCompAList.length; i++) {
				            if (scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompAList[i]) > -1) {
				                scwin.gvar.packageDates.push(scwin.gvar.holidayList[scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompAList[i])]);
				            }else{break;}
				        }
				        // 뒤 비교
				        for (var i = 0; i < scwin.gvar.holidayCompBList.length; i++) {
				            if (scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompBList[i]) > -1) {

				                scwin.gvar.packageDates.push(scwin.gvar.holidayList[scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompBList[i])]);
				            }else{
				            	scwin.gvar.packageDates.push( scwin.gvar.holidayCompBList[i] );
				            	break; 
				            }
				        }
				    }

			        //신청날이 평일이고 다음날이 휴일일때
			        if (scwin.gvar.holidayList.indexOf( gfnGetDateFormat(inputCalendarUseYmd.getValue()) ) < 0 && scwin.gvar.holidayList.indexOf(gcm.date.addDate(gfnGetDateFormat(inputCalendarUseYmd.getValue()), 1)) > -1) {
			            // 앞 비교
			            for (var i = 0; i < scwin.gvar.holidayCompAList.length; i++) {
			                if (scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompAList[i]) > -1) {
			                    scwin.gvar.packageDates.push(scwin.gvar.holidayList[scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompAList[i])]);
			                }else{break;}
			            }
			        }

					var uniqueArr = scwin.gvar.packageDates.filter(function(element, index){
					    return scwin.gvar.packageDates.indexOf(element) === index;
					});
					scwin.gvar.packageDates = uniqueArr;
					scwin.gvar.packageDates.sort();

					var gapDateList = [];
					scwin.gvar.holidayCompAList = [];	
					var gap = ipsel_jejudate.getValue() - scwin.gvar.packageDates.length;

					if(gap > 0){
						for (var i = 1 ; i < gap+1 ; i++) {
							gapDateList.push(gcm.date.addDate(gfnGetDateFormat(scwin.gvar.packageDates[scwin.gvar.packageDates.length-1]), i));
						}

						for (var i = 1; i < 7; i++) {
							scwin.gvar.holidayCompAList.push(gcm.date.addDate(gfnGetDateFormat(gapDateList[gapDateList.length-1]), i));
						}

						//끝나는 날이 휴일일때
						if (scwin.gvar.holidayList.indexOf(gapDateList[gapDateList.length-1]) > -1) {
					        // 앞 비교
					        for (var i = 0; i < scwin.gvar.holidayCompAList.length; i++) {
					            if (scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompAList[i]) > -1) {
					                gapDateList.push(scwin.gvar.holidayList[scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompAList[i])]);
					            }else{break;}
					        }
					    }

					    //끝나는 날이 평일이고 다음날이 휴일일때
					    if (scwin.gvar.holidayList.indexOf( gfnGetDateFormat(gapDateList[gapDateList.length-1]) ) < 0 && scwin.gvar.holidayList.indexOf(gcm.date.addDate(gfnGetDateFormat(gapDateList[gapDateList.length-1]), 1)) > -1) {
					    	// 앞 비교
				            for (var i = 0; i < scwin.gvar.holidayCompAList.length; i++) {
				                if (scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompAList[i]) > -1) {
				                    gapDateList.push(scwin.gvar.holidayList[scwin.gvar.holidayList.indexOf(scwin.gvar.holidayCompAList[i])]);
				                }else{break;}
				            }
					    }
					    gapDateList.sort();
					    gapDateList.forEach(function(a){
					    	scwin.gvar.packageDates.push(a);
					    });
					    scwin.gvar.packageDates.sort();
				    }

					var enterymd = gcm.date.formatDate(scwin.gvar.packageDates[0]);
					var enterday = gcm.date.getDay(scwin.gvar.packageDates[0]);

					var exitymd = "";
					var exitday = "";					



					entranceYmd.setValue(enterymd+"("+enterday+")");
					if(scwin.gvar.packageDates.length < 2){
						 ymd =gcm.date.addDate(gfnGetDateFormat(scwin.gvar.packageDates[0]), 1);						

						 exitymd = gcm.date.formatDate(ymd);
						 exitday = gcm.date.getDay(ymd);

						exitYmd.setValue(exitymd+"("+exitday+")");					
					}else{
						ymd =gcm.date.addDate(gfnGetDateFormat(scwin.gvar.packageDates[scwin.gvar.packageDates.length-1]), 1)			

						exitymd = gcm.date.formatDate(ymd);
						 exitday = gcm.date.getDay(ymd);

						exitYmd.setValue(exitymd+"("+exitday+")");
					}

					scwin.init_region_status();
				}
			});

	};

	scwin.selectListCgsDorm = function() {
			com.execute(sbm_selectDormList, {
				successCallback : function(e) {
				 dlt_dormCa.removeAll();
				 dlt_dormKw.removeAll();
				 dlt_dormDk.removeAll();
				 dlt_dormWs.removeAll();
				 dlt_dormJinj.removeAll();
				 dlt_dormBs.removeAll();
				 dlt_dormYj.removeAll();
				 dlt_dormKs.removeAll();
				 dlt_dormJej.removeAll();
  					for (var i = 0; i < dlt_dorm.getTotalRow(); i++) {
						if (dlt_dorm.getRowJSON(i).region_cd == "CAB001") {

						dlt_dormCa.setRowJSON(dlt_dormCa.getTotalRow(),dlt_dorm.getRowJSON(i));

						}else if (dlt_dorm.getRowJSON(i).region_cd == "CAB002") {

						dlt_dormKw.setRowJSON(dlt_dormKw.getTotalRow(),dlt_dorm.getRowJSON(i));

						}else if (dlt_dorm.getRowJSON(i).region_cd == "CAB003") {

						dlt_dormDk.setRowJSON(dlt_dormDk.getTotalRow(),dlt_dorm.getRowJSON(i));

						}else if (dlt_dorm.getRowJSON(i).region_cd == "CAB004") {

						dlt_dormWs.setRowJSON(dlt_dormWs.getTotalRow(),dlt_dorm.getRowJSON(i));

						}else if (dlt_dorm.getRowJSON(i).region_cd == "CAB005") {

						dlt_dormJinj.setRowJSON(dlt_dormJinj.getTotalRow(),dlt_dorm.getRowJSON(i));

						}else if (dlt_dorm.getRowJSON(i).region_cd == "CAB006") {

						dlt_dormBs.setRowJSON(dlt_dormBs.getTotalRow(),dlt_dorm.getRowJSON(i));

						}else if (dlt_dorm.getRowJSON(i).region_cd == "CAB007") {

						dlt_dormYj.setRowJSON(dlt_dormYj.getTotalRow(),dlt_dorm.getRowJSON(i));

						}else if (dlt_dorm.getRowJSON(i).region_cd == "CAB008") {

						dlt_dormKs.setRowJSON(dlt_dormKs.getTotalRow(),dlt_dorm.getRowJSON(i));

						}else if (dlt_dorm.getRowJSON(i).region_cd == "CAB009") {

						dlt_dormJej.setRowJSON(dlt_dormJej.getTotalRow(),dlt_dorm.getRowJSON(i));

						}
					}

				}
			});

	};

