	scwin.checkbox1_onchange = function(info) {

		dlt_dormNo.removeAll();

		var check = info.newValue;

		if(check == 'Y'){
			jeju_detail.setStyle( "display", "inline");

			group_jej.setStyle( "display", "block");
			group_ca.setStyle( "display", "none");
			group_kw.setStyle( "display", "none");
			group_dk.setStyle( "display", "none");
			group_ws.setStyle( "display", "none");
			group_jinj.setStyle( "display", "none");
			group_bs.setStyle( "display", "none");
			group_yj.setStyle( "display", "none");
			group_ks.setStyle( "display", "none");
		}else{
			jeju_detail.setStyle( "display", "none");

			group_jej.setStyle( "display", "none");
			group_ca.setStyle( "display", "block");
			group_kw.setStyle( "display", "block");
			group_dk.setStyle( "display", "block");
			group_ws.setStyle( "display", "block");
			group_jinj.setStyle( "display", "block");
			group_bs.setStyle( "display", "block");
			group_yj.setStyle( "display", "block");
			group_ks.setStyle( "display", "block");
		}


		if (rdo_styClsf.getValue() == "S") {

			scwin.gvar.packageDates = [];
			scwin.gvar.holidayCompBList = [];
			scwin.gvar.holidayCompAList = [];

			dma_holiyDayListSearch.set("req_fr_ymd",gcm.date.addDate(gfnGetDateFormat(inputCalendarUseYmd.getValue()), -15));
			dma_holiyDayListSearch.set("req_to_ymd",gcm.date.addDate(gfnGetDateFormat(inputCalendarUseYmd.getValue()), 15));

			scwin.selectListHoliday();
		}

	};


