	scwin.btn_search_onclick = function(e) {
		console.log("!!!!btn_search_onclick");

		if (rdo_styClsf.getValue() == "S") {
			var jeju =  cbx_checkbox1.getValue();



			if(jeju == 'Y'){
				group_jej.setStyle( "display", "block");
				group_ca.setStyle( "display", "none");
				group_kw.setStyle( "display", "none");
				group_dk.setStyle( "display", "none");
				group_ws.setStyle( "display", "none");
				group_jinj.setStyle( "display", "none");
				group_bs.setStyle( "display", "none");
				group_yj.setStyle( "display", "none");
				group_ks.setStyle( "display", "none");
			}else{
				group_jej.setStyle( "display", "none");
				group_ca.setStyle( "display", "block");
				group_kw.setStyle( "display", "block");
				group_dk.setStyle( "display", "block");
				group_ws.setStyle( "display", "block");
				group_jinj.setStyle( "display", "block");
				group_bs.setStyle( "display", "block");
				group_yj.setStyle( "display", "block");
				group_ks.setStyle( "display", "block");
			}
			dma_holiday.set("ymd", inputCalendarUseYmd.getValue());

			com.execute(sbm_selectHoliyday, {
				successCallback : function(e) {
					var offYn = dma_holiday.get("off_yn");
					if(offYn == 'Y'){
						if (!confirm("휴일 단기기숙사 신청은 전날 사용에 따른 청소 및 정돈이 되어있지 않을 수 있습니다. 그래도 계속 신청하시겠습니까?")) {
								return;
						}
					}
					scwin.init_region_status();
				}
			});

		} else {

			group_jej.setStyle( "display", "block");
			group_ca.setStyle( "display", "block");
			group_kw.setStyle( "display", "block");
			group_dk.setStyle( "display", "block");
			group_ws.setStyle( "display", "block");
			group_jinj.setStyle( "display", "block");
			group_bs.setStyle( "display", "block");
			group_yj.setStyle( "display", "block");
			group_ks.setStyle( "display", "block");



			scwin.init_region_status();
		}
	};

	/** 일자선택에 따른 가용선택호실정보 초기 조회 */
	scwin.init_region_status = function(e) {
		btn_save.hide(); // 저장 버튼 숨김
		dlt_dormNo.removeAll();
		console.log("!!!!!!init_region_status");
		scwin.reg_region_status();


		//grid_roomList.initGrid();


	};

	scwin.reg_region_status = function(e) {
	console.log("!!!!!!reg_region_status");
				if (tab_inOut.getSelectedTabIndex() == 1) { // 사용자-외부
					if (use_psn_nm2.getValue() == "") {
						alert("성명을 입력하세요.");
						use_psn_nm2.focus();
						return;
					}
					if (use_psn_dept_nm.getValue() == "") {
						alert("소속기관/업체명을 입력하세요.");
						use_psn_dept_nm.focus();
						return;
					}
					if (use_psn_tel.getValue() == "") {
						alert("연락처(HP)를 입력하세요.");
						use_psn_tel.focus();
						return;
					}
					if (use_psn_mail.getValue() == "") {
						alert("이메일을 입력하세요.");
						use_psn_mail.focus();
						return;
					}
					if (req_resn.getValue() == "") {
						alert("신청사유를 입력하세요.");
						req_resn.focus();
						return;
					}
					dma_dormSearch.set("use_psn_sex", radio_gender.getValue());
				}

				if (rdo_styClsf.getValue() == "S") { // 숙박 구분이 단기인 경우 :: 이대훈 변경
					if (inputCalendarUseYmd.getValue() == "") {
						alert("일자를 입력하세요.");
						inputCalendarUseYmd.focus();
						return;
					}
					var use_from_ymd = inputCalendarUseYmd.getValue();

					if( scwin.gvar.packageDates.length > 1 && cbx_checkbox1.getValue() == "Y" ){
						use_from_ymd = scwin.gvar.packageDates[0];
						var use_to_ymd = scwin.gvar.packageDates[scwin.gvar.packageDates.length-1];
					}else{
						var use_to_ymd = inputCalendarUseYmd.getValue();
					}
				} else if (rdo_styClsf.getValue() == "L") { // 숙박 구분이 장기인 경우
					if (inputCalendarUseYm.getValue() == "") {
						alert("년월을 입력하세요.");
						inputCalendarUseYm.focus();
						return;
					}
					var use_from_ymd = inputCalendarUseYm.getValue() + "01";
					var use_to_yyyy = use_from_ymd.substr(0, 4);
					var use_to_mm = parseInt(use_from_ymd.substr(4, 2), 10) + parseInt(selectboxUseYmOpt.getValue());
					var use_to_ymd = WebSquare.date.getFormattedDate(new Date(use_to_yyyy, use_to_mm, 0), "yyyyMMdd");
				}

				group_list.show();
				dma_dormSearch.set("use_psn_syspayno", dma_user.get("use_psn_syspayno"));
				dma_dormSearch.set("use_from_ymd", use_from_ymd);
				dma_dormSearch.set("use_to_ymd", use_to_ymd);
				dma_dormSearch.set("stay_clsf", rdo_styClsf.getValue());

				scwin.selectListCgsDorm();
	};



