		grid_ws.outFocusedCell();
		grid_jinj.outFocusedCell();
		grid_bs.outFocusedCell();

		grid_ks.outFocusedCell();
		grid_kj.outFocusedCell();
		grid_junj.outFocusedCell();
		grid_jej.outFocusedCell();

		for (var i = 0; i < grid_ca.getRowCount(); i++) grid_ca.setCellChecked(i, grid_ca.getColumnIndex("colRadio"), false);
		for (var i = 0; i < grid_kw.getRowCount(); i++) grid_kw.setCellChecked(i, grid_kw.getColumnIndex("colRadio"), false);
		for (var i = 0; i < grid_dk.getRowCount(); i++) grid_dk.setCellChecked(i, grid_dk.getColumnIndex("colRadio"), false);
		for (var i = 0; i < grid_ws.getRowCount(); i++) grid_ws.setCellChecked(i, grid_ws.getColumnIndex("colRadio"), false);
		for (var i = 0; i < grid_jinj.getRowCount(); i++) grid_jinj.setCellChecked(i, grid_jinj.getColumnIndex("colRadio"), false);
		for (var i = 0; i < grid_bs.getRowCount(); i++) grid_bs.setCellChecked(i, grid_bs.getColumnIndex("colRadio"), false);

		for (var i = 0; i < grid_ks.getRowCount(); i++) grid_ks.setCellChecked(i, grid_ks.getColumnIndex("colRadio"), false);
		for (var i = 0; i < grid_kj.getRowCount(); i++) grid_kj.setCellChecked(i, grid_kj.getColumnIndex("colRadio"), false);
		for (var i = 0; i < grid_junj.getRowCount(); i++) grid_junj.setCellChecked(i, grid_junj.getColumnIndex("colRadio"), false);
