	scwin.getRoomStatus = function (gridObj) {
				// 지역 및 호실 선택
				var rowIndex = gridObj.getFocusedRowIndex();
				if (rowIndex == -1) {
					alert("방구분을 선택하세요.");
					return;
				}
				btn_save.show();

				var use_psn_syspayno = dma_user.get("use_psn_syspayno");
				var use_psn_sex = dma_dormSearch.get("use_psn_sex");
				var use_from_ymd = dma_dormSearch.get("use_from_ymd");
				var use_to_ymd = dma_dormSearch.get("use_to_ymd");
				var stay_clsf = rdo_styClsf.getValue();

				var region_cd = gridObj.getCellData(rowIndex, "region_cd");

				var dorm_clsf = gridObj.getCellData(rowIndex, "dorm_clsf");

				var room_clsf = gridObj.getCellData(rowIndex, "room_clsf");

				dma_dormNoSearch.set("use_psn_syspayno",use_psn_syspayno);
				dma_dormNoSearch.set("use_psn_sex",use_psn_sex);
				dma_dormNoSearch.set("use_from_ymd",use_from_ymd);
				dma_dormNoSearch.set("use_to_ymd",use_to_ymd);
				dma_dormNoSearch.set("stay_clsf",stay_clsf);

				dma_dormNoSearch.set("region_cd",region_cd);
				dma_dormNoSearch.set("dorm_clsf",dorm_clsf);
				dma_dormNoSearch.set("room_clsf",room_clsf);

				com.execute(sbm_selectListCgsDormNo, {
					successCallback : function(e) {

					}
				});

			}

	scwin.btn_userSearch_onclick = function(e) {
		var data={empno : ipt_psn_empno.getValue()};

		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			dma_user.set("use_psn_empno", ret.empno);
			dma_user.set("use_psn_nm", ret.nm);
			dma_user.set("use_psn_syspayno", ret.syspayno);
			dma_user.set("use_posi_cd", ret.posi_cd);
		});
	};

	scwin.btn_save_onclick = function(e) {
		var now = new Date();
	    var time = now.getHours();
	    var sn = dma_user.get("use_psn_syspayno");

   		var now = new Date();
		var year = now.getFullYear();
		var month = now.getMonth();
		var date = now.getDate();
		var toDate = new Date(year, month , date);
		var today = getDateStr(toDate);

	    var area = com.getLoginInfo("areaCode");

		if (rdo_styClsf.getValue() == "S" // 단기신청은 주로 당일 신청이므로
				&& (time < "10" || time >= "19") // 10시~16시 신청허용 시간대 아니면
				&& scwin.gvar.roleCode.indexOf("1006257") == -1 // 그리고 기숙사담당이 아니고
				&& sn != "20130576"
				&& sn != "99999963") { // 전산관리자가 아니면 신청 차단
			alert("기숙사 (단기)신청가능 시간은 오전 10시부터 오후 4시까지입니다.");
		} else {
			var regionCd = dma_dormNoSearch.get("region_cd");
			var useFromYmd =dma_dormSearch.get("use_from_ymd"); // 조회된 사용일자
			var dayCnt = WebSquare.date.dateDiff(today, useFromYmd); // 날짜차이일수

			if (regionCd == "CAB001" && rdo_styClsf.getValue() == "S" && dayCnt > 0 && scwin.gvar.roleCode.indexOf("1006257") == -1) { //천안지역본부는 단기일 경우 당일 신청 가능

				// 금요일이고 토/일 까지 신청하여 2일 이하인 경우 예외 처리
				if(new Date().getDay() == 5 && dayCnt < 3) {
					// ignore
				} else {
					alert("단기 기숙사 신청은 당일에만 신청이 가능합니다.");
					return;
				}
			}

			if (regionCd == "CAB002" && dayCnt > 14) { // 강원지역본부는 14일 전부터 신청 가능
				alert("강원지역본부 기숙사 운영내규에 따라 사용일 기준 14일 전부터 신청가능합니다.");
				return;
			}
			if (regionCd == "CAB009" && dayCnt > 29 && rdo_styClsf.getValue() == "S" && scwin.gvar.roleCode.indexOf("1006257") == -1) { // 제주지역본부는 30일 전부터 신청 가능
				alert("제주본부 기숙사 운영내규에 따라 사용일 기준 30일 전부터 신청가능합니다.");
				return;
			}


			if( regionCd == "CAB009" && rdo_styClsf.getValue() == "S" && (scwin.gvar.approvedDates.length + scwin.gvar.packageDates.length) > 10 ){ // 제주지역본부 단기일때 10일초과 연박 불가, 담당자만 신청 가능(민관웅 선임사무원님 요청, 20230628)
				if( area != "HBX040" || scwin.gvar.roleCode.indexOf("1006257") == -1 ){		// 제주지역본부 기숙사 담당자만 허용
					alert("제주본부 기숙사 단기 최대 연박은 10일까지만 가능합니다.");
					return;
				}

			}

			if(regionCd == "CAB009" && rdo_styClsf.getValue() == "S" && scwin.gvar.packageDates.length > 1){
				if (!confirm("제주본부 단기 기숙사 신청시\n신청날짜 전후로 휴일이면 휴일 포함 패키지로 신청됩니다.\n사용요금은 실제 사용일만 부과되며,\n이 경우 기숙사 경비분께 실제 사용일을 알려주셔야 반영됩니다.\n현재 선택된 날짜는\n\n" + scwin.gvar.packageDates[0] + "~" + scwin.gvar.packageDates[scwin.gvar.packageDates.length-1] + " 입니다.\n\n계속하시겠습니까?")) {
					return;
				}
			}

			// 정규직 직급 조회 
			com.execute(sbm_selectListPosi, {
				successCallback : function(e) {

					var use_posi_cd = dma_user.get("use_posi_cd");
					var dorm_clsf = dma_dormNoSearch.get("dorm_clsf");
					var room_clsf = dma_dormNoSearch.get("room_clsf");

					var use_psn_syspayno = dma_user.get("use_psn_syspayno");			// 2023.10.20. 김현진(150639)사무원 요청 - XC2202302720 

					var isNormalWorker = dlt_posi.getAllJSON().some( function(index){
						var tmpPosiCd = index.posi_cd;
						return (tmpPosiCd == use_posi_cd);
					});

					if(																	// 2023.10.20. 김현진(150639)사무원 요청 - XC2202302720
						use_psn_syspayno != "20230495" &&
						use_psn_syspayno != "20231191" &&
						use_psn_syspayno != "20231192" &&
						use_psn_syspayno != "20231134" &&
						use_psn_syspayno != "20230411" &&
						use_psn_syspayno != "20221012" &&
						use_psn_syspayno != "20230410" &&
						use_psn_syspayno != "20220990" &&
						use_psn_syspayno != "20230402" &&
						use_psn_syspayno != "20220359" &&
						use_psn_syspayno != "20211053" &&
						use_psn_syspayno != "20181021" &&
						use_psn_syspayno != "20220357" &&
						use_psn_syspayno != "20210404" &&
						use_psn_syspayno != "20210437" &&
						use_psn_syspayno != "20230267" &&
						use_psn_syspayno != "20180980" &&
						use_psn_syspayno != "20220446" &&
						use_psn_syspayno != "20220424" &&
						use_psn_syspayno != "20210323" &&
						use_psn_syspayno != "20220421" &&
						use_psn_syspayno != "20220423" &&
						use_psn_syspayno != "20210293" &&
						use_psn_syspayno != "20221005" &&
						use_psn_syspayno != "20180953" &&
						use_psn_syspayno != "20230422" &&
						use_psn_syspayno != "20220489" &&
						use_psn_syspayno != "20231170" &&
						use_psn_syspayno != "20220443" &&
						use_psn_syspayno != "20230407"

															){

							if(isNormalWorker == false && dorm_clsf == "CAC002" && room_clsf != "FCP070") {
								alert("일반 상근 직원이 아니면 신기숙사(게스트룸 제외)를 사용하실 수 없습니다.");
							} else {
								scwin.applyRoom();
							}

					}else {							// 2023.10.20. 김현진(150639)사무원 요청 - XC2202302720
						scwin.applyRoom();			// 2023.10.20. 김현진(150639)사무원 요청 - XC2202302720	
					}								// 2023.10.20. 김현진(150639)사무원 요청 - XC2202302720


				}
			});
		}
	};

	scwin.applyRoom = function(){



		g_region_cd = "";
		g_dorm_clsf = "";
		g_req_room_clsf = "";
		g_req_room_cd = "";

		var rowCount = grid_roomList.getRowCount(); // 호실목록-개수
		var rowIndex = grid_roomList.getFocusedRowIndex(); // 호실목록-선택한 행번호
		console.log("rowCount    "+rowCount);
		console.log("rowIndex    "+rowIndex);
		if (rowCount == 0) { // 숙박대기 신청
			if (confirm("숙박대기 신청하시겠습니까?")) {
				scwin.gvar.standbyReqYn = "Y"; // 기숙사 숙박대기 신청
				var region_cd = dma_dormNoSearch.get("region_cd"); // 지역코드
				var dorm_clsf = dma_dormNoSearch.get("dorm_clsf"); // 기숙사구분
				var req_room_clsf = dma_dormNoSearch.get("room_clsf"); // 신청방구분
				var req_room_cd = ""; // 신청호실코드

				console.log("region_cd     :"+region_cd);
				console.log("dorm_clsf     :"+dorm_clsf);
				console.log("req_room_clsf     :"+req_room_clsf);



			} else {
				alert("취소하셨습니다.");
				return;
			}
		} else if (rowIndex == -1) { // 자동승인 우선 호실로 신청
			if (confirm("자동승인 우선 호실로 신청하시겠습니까?")) {
				scwin.gvar.standbyReqYn = "N"; // 자동승인 우선 호실로 신청 또는 선택호실 신청
				var region_cd = dma_dormNoSearch.get("region_cd"); // 지역코드
				var dorm_clsf = dma_dormNoSearch.get("dorm_clsf"); // 기숙사구분
				var req_room_clsf = dma_dormNoSearch.get("room_clsf"); // 신청방구분
				var req_room_cd = ""; // 신청호실코드
			} else {
				alert("취소하셨습니다.");
				return;
			}
		} else if (rowIndex >= 0) { // 선택한 호실로 신청
			var reqRoomNo = grid_roomList.getCellData(rowIndex, "room_no"); // 신청호실번호
			if (confirm(reqRoomNo+" 호실로 신청하시겠습니까?")) {
				// debugger;
				scwin.gvar.standbyReqYn = "N"; // 자동승인 우선 호실로 신청 또는 선택호실 신청
				var region_cd = grid_roomList.getCellData(rowIndex, "region_cd"); // 지역코드
				var dorm_clsf = grid_roomList.getCellData(rowIndex, "dorm_clsf"); // 기숙사구분
				var req_room_clsf = grid_roomList.getCellData(rowIndex, "room_clsf"); // 신청방구분
				var req_room_cd = grid_roomList.getCellData(rowIndex, "room_cd"); // 신청호실코드
				if(grid_roomList.getCellData(rowIndex, "auto_approval_yn_nm") == "불가능") scwin.gvar.standbyReqYn = "Y"; // 자동승인 불가능일때 standbyReqYn값 Y 세팅
			} else {
				alert("취소하셨습니다.");
				return;
			}
		}

		console.log("region_cd    "+region_cd);
		console.log("dorm_clsf    "+dorm_clsf);
		console.log("req_room_clsf    "+req_room_clsf);
		console.log("req_room_cd    "+req_room_cd);

		g_region_cd = region_cd;
		g_dorm_clsf = dorm_clsf;
		g_req_room_clsf = req_room_clsf;
		g_req_room_cd = req_room_cd;
		dma_dormReq.set("region_cd", region_cd);
		dma_dormReq.set("dorm_clsf", dorm_clsf);
		dma_dormReq.set("req_room_clsf", req_room_clsf);
		dma_dormReq.set("req_room_cd", req_room_cd);

		// 게스트룸 신청이고 신청사유가 없으면
		if (req_room_clsf == "FCP070" && (dma_dormReq.get("req_resn") == "" && dma_user.get("req_resn") == "")) { 
			alert("게스트룸 신청 시 신청사유 항목에 사용인원 및 세부사유를 반드시 입력하십시오.");
			return;
		}

		scwin.genNoSeq();



	}

	scwin.genNoSeq = function(){

			console.log("scwin.gvar.roleCode"+scwin.gvar.roleCode);
			console.log("scwin.gvar.sn   :"+scwin.gvar.sn);

			if (tab_inOut.getSelectedTabIndex() == 0){ 
			 var _use_psn_nm = ipt_psn_empno.getValue();  // 내부-사용자명
			}else if (tab_inOut.getSelectedTabIndex() == 1){
			 var _use_psn_nm = use_psn_nm2.getValue(); // 외부-사용자명
			}

			// 숙박 구분이 단기인 경우
			if (rdo_styClsf.getValue() == "S") {
				var use_from_ymd = inputCalendarUseYmd.getValue();
				var use_to_ymd = inputCalendarUseYmd.getValue();
			// 숙박 구분이 장기인 경우
			} else if (rdo_styClsf.getValue() == "L") {
				var use_from_ymd = inputCalendarUseYm.getValue() + "01";
				var use_to_ymd = inputCalendarUseYm.getValue() + new Date(use_from_ymd.substr(0, 4), use_from_ymd.substr(4, 2), 0).getDate();
			}

			console.log("use_from_ymd"+use_from_ymd);
			console.log("use_to_ymd"+use_to_ymd);

			//dma_dormReq.set("_origin_req_no", );
			//dma_dormReq.set("req_no", );

			dma_dormReq.set("req_psn_syspayno", scwin.gvar.sn);
			dma_dormReq.set("use_psn_syspayno", dma_user.get("use_psn_syspayno"));
			dma_dormReq.set("use_psn_nm", dma_user.get("use_psn_nm"));
			dma_dormReq.set("use_psn_sex", dma_user.get("use_psn_sex"));
			dma_dormReq.set("use_psn_dept_nm", dma_user.get("use_psn_dept_nm"));
			dma_dormReq.set("use_psn_tel", dma_user.get("use_psn_tel"));
			dma_dormReq.set("use_from_ymd", use_from_ymd);
			dma_dormReq.set("use_to_ymd", use_to_ymd);

			if(dma_user.get("use_psn_mail") == "" || dma_user.get("use_psn_mail") == null){			
			 	dma_user.set("use_psn_mail", com.getLoginInfo("email"));
			}

			dma_dormReq.set("use_psn_mail", dma_user.get("use_psn_mail"));

			console.log("insert start");
			//제주도 다중 인서트 - 이대훈 추가
			if (rdo_styClsf.getValue() == "S" && cbx_checkbox1.getValue() == "Y") {
				console.log("insert jeju");

				for (var i = 0 ; i < scwin.gvar.packageDates.length ; i++) {
					console.log("insert jeju2");
					dma_dormReq.set("use_from_ymd", scwin.gvar.packageDates[i]);
					dma_dormReq.set("use_to_ymd", scwin.gvar.packageDates[i]);

					console.log("insert jeju3");

					dlt_dormReqj.setRowJSON(i, dma_dormReq.getJSON());

				}
				console.log(scwin.gvar.packageDates);
				console.log(dlt_dormReqj.getAllJSON());
					if (scwin.gvar.standbyReqYn == "Y") { // if 기숙사 숙박대기 신청인 경우
						com.execute(sbm_insertCgsDormStandj, {
							successCallback : function(e) {
								dlt_dormReqj.removeAll();
									//gcm.win.isRefreshing = true;
									//$p.reinitialize();
									scwin.btn_list_onclick();
									console.log(dlt_dormReqj.getAllJSON());
									console.log(scwin.gvar.packageDates);
							}
						});		
					} else { // 자동승인 우선 호실로 신청 또는 선택호실 신청인 경우
					console.log("insert jeju3");
						com.execute(sbm_insertCgsDormj, {
							successCallback : function(e) {
								dlt_dormReqj.removeAll();
									//gcm.win.isRefreshing = true;
									//$p.reinitialize();
									scwin.btn_list_onclick();
								console.log(scwin.gvar.packageDates);	
								console.log(dlt_dormReqj.getAllJSON());

							}
						});		
					}
			}else{
				// debugger;
				console.log("insert normal");
				if (scwin.gvar.standbyReqYn == "Y") { // if 기숙사 숙박대기 신청인 경우
				console.log("insert normal start");
					scwin.insertCgsDormStand();
				} else { // 자동승인 우선 호실로 신청 또는 선택호실 신청인 경우
					scwin.insertCgsDorm();
				}
			}
	}

	scwin.tab_inOut_ontabclick = function(tabID,idx) {
		if(tab_inOut.getSelectedTabIndex() == 0){

			com.initDataMap(dma_user);
			dma_user.set("use_psn_empno", com.getLoginInfo("empno"));
			dma_user.set("use_psn_nm", com.getLoginInfo("cn"));
			dma_user.set("use_psn_syspayno", com.getLoginInfo("sn"));
			dma_user.set("use_posi_cd", com.getLoginInfo("posiCode"));

			group_list.hide();

		}else{

			com.initDataMap(dma_user);
			group_list.hide();

		}
	}

	scwin.grd_gridView1_oncellclick = function(row,col,colId) {
		if (colId == "column_cancel") {	
			var now = new Date();
		    var time = now.getHours();

		    alert(time + " : 시간");		   

			if ("신청" == this.getCellData(row, "req_state_nm")){
				if ("제주" == this.getCellData(row, "region_nm") && "Y" == this.getCellData(row, "off_yn") && scwin.gvar.roleCode.indexOf("1006257") == -1 && scwin.gvar.sn != "20130576") {
					alert("패키지로 신청된 건에 대해서는 취소가 불가능 합니다. \n실제 사용일만 요금 부과를 원하실 경우 실제 사용일을 해당일 기숙사 근무 경비분께 말씀해 주시기 바랍니다.");
					return;
				} else {
					if (!confirm("신청을 취소하고 신청내용을 삭제하시겠습니까?")) return;
				}	
			} else if ("승인" == this.getCellData(row, "req_state_nm") && "Y" == this.getCellData(row, "auto_approval_yn")){

				if (time < "10" || time > "16") { // 10시~16시 신청허용 시간대 아니면
					alert("기숙사 (단기)취소가능 시간은 오전 10시부터 오후 4시까지입니다.");
					return;
				} else {
					if (!confirm("자동 승인을 취소하고 승인내용을 삭제하시겠습니까?")) return;
				}
			}

			dma_dormReq.set("req_no", dlt_dormReq.getCellData(row, "req_no"));

			com.execute(sbm_deleteCgsDorm, {
					successCallback : function(e) {

						alert("기숙사 신청이 취소 되었습니다");
						group_list.hide();

						//gcm.win.isRefreshing = true;
						//$p.reinitialize();
						scwin.btn_list_onclick();
					}
			});

		}
	};
