	scwin.btn_saveCgs_onclick = function() {
		var tripKnd = dma_insert.get("biztrip_clsf");
		var sbxExpns = dma_insert.get("biztrip_expns_clsf");
		if(tripKnd == "FCA001" && sbxExpns == "FAR001") {
			if(inputBiztripFee.getValue() < "0"){
				alert("[알림] 주중금액은 0과 같거나 커야 합니다.");
				inputBiztripFee.focus();
			}else if(inputWeekBiztripFee.getValue() < "0"){
				alert("[알림] 주말금액은 0과 같거나 커야 합니다.");
  				inputWeekBiztripFee.focus();
			}
		}

		scwin.saveCgs();	
	};

