head.script:
    /****************************************************
	 * 화면 설명 : 국내출장신청서
	 * 변경 이력 : 2022-06-07	이영희	최초 작성
	 * serviceId : C320
	 ****************************************************/    
	scwin.data = {
		isApproval : false,
		serviceId : "C320"
	}; 
	/**
	 * 페이지 로딩 이벤트
	 */
	scwin.onpageload = function() {
		// parameter
		var param = com.getParameter();
		var param1 = com.getParameter("serviceId");
		console.log("param1 : " + param1);

		if(param.param4 != "apr"){
			scwin.data.serviceId = param.menuObj.serviceID;
		}else{
			scwin.data.serviceId = param.serviceId;
		}
		dma_search.setJSON(param);

		if(!com.isEmpty(param.openerTabId)){
			scwin.data.openerTabId = param.openerTabId;
		}

		// 첨부파일 컴포넌트 초기화
		com.file.init();

		// 초기화
		btn_path_del.hide();
		grp_pool.hide();
		grp_holiday.hide();
		spnChgExpnsDesc.hide();
		grp_pay_info.hide();
		grp_vst_plc.hide();
		btn_mngSave.hide();

		// 오늘 날짜
		scwin.data.today = com.getServerDateTime();

		// session
		var session = com.getLoginInfo();
		scwin.data.sn = session.sn;

		// 기간 역전 체크
		com.checkPeriod(ica_start_dt, ica_end_dt);			

		// enter key event
		com.setEnterKeyEvent(grp_biztrip_psn, scwin.btn_biztrip_psn_onclick);
		com.setEnterKeyEvent(grp_agent, scwin.btn_agent_onclick);	

		// 변경사항 체크
		scwin.checkList = [dma_trip, dma_biztripPsn, dlt_compn, dlt_path, dlt_schd];	

		// 공통코드
		var codeOptions = [ { code : "FBG", compID :"sbx_posi_clsf,grd_compn:posi_clsf" } // 직급구분
		                   ,{ code : "FDO", compID :"sbx_car_use" }     // 원차량목록
		                   ,{ code : "FBV", compID :"sbx_goal_clsf" }	// 출장목적
		                   ,{ code : "FBH", compID :"grd_compn:biztrip_psn_clsf" }	// 동반자구분		                   
		                   ,{ code : "CTD", compID :"grd_holiday:rest_cd" }	// 근무구분
		                   ,{ code : "FAU", compID :"grd_path:start_region,grd_path:arriv_region" }	// 출발지 및 도착지
						   ,{ code : "CTC"}
		                ];
		// (구)국내출장 또는 기업현장 출장 조회시 화면 비활성화, 지급내역 보여줌
		if(dma_search.get("req_no") != ""){
			grd_path.setColumnVisible("biztrip_fee", false);

			codeOptions.push({code : "FAA", compID :"grd_pay:bank_cd"});

			if(dma_search.get("req_no").substring(0, 3) != "C86"){
				grp_page.setDisabled(true);
				btn_pre.setDisabled(false);
				btn_print.setDisabled(false);
			}
		}

		com.setCmnCd(codeOptions, function() {
			//TODO TEST
			//dma_search.set("req_no", "C86202203604");			

			if(dma_search.get("req_no") != ""){
				scwin.search();	
			}else{
				// 첨부파일
				com.file.create(upload_A.getID(), {fileKey: "C320", mode: 'edit'});	

				if(scwin.data.serviceId == "S951"){		
					sbx_goal_clsf.setDisabled(true);		
					scwin.setSpt(true);
				}else{
					scwin.setSpt(false);
				}

				// 차량사융(사용안함)
				sbx_car_use.setValue("FDO001");
				// 운임구분
				sbx_biztrip_way.setValue("train");

				// 출장 시작일자
				ica_start_dt.setValue(scwin.data.today);		

				// 작성자 및 출장자
				dma_trip.set("cud_type", "C");
				dma_trip.set("req_ymd", scwin.data.today);

				if(session.sn.substring(0, 4) != "9999"){
					com.pop.open(com.pop.TYPE.HUM, {empno : session.empno}, function(ret) {
						// 작성자
						dma_trip.set("req_psn_syspayno", ret.syspayno);
						dma_trip.set("req_psn_nm", ret.nm);
						dma_trip.set("req_psn_dept_cd", ret.dept_cd);
						dma_trip.set("req_psn_dept_new_ymd", ret.dept_new_ymd);						
						dma_trip.set("req_dept_nm", ret.dept_nm);

						scwin.data.fomat_unit = ret.fomat_unit;						

						// 출장자	
						scwin.setBizTripPsn("Biz", ret);

						// 변경사항 초기화
						dma_trip.initRowStatus();
						dma_biztripPsn.initRowStatus();
						dlt_compn.modifyAllStatus("R");
						dlt_path.modifyAllStatus("R");
						dlt_schd.modifyAllStatus("R");
					});
				}
			}
		});		

	};

	/**
	 * 현장출장신청 셋팅
	 */
	scwin.setSpt = function(flag){
		if(flag){
			sbx_goal_clsf.addItem("FBV920", "현장출장(기술)", 0, true);
			sbx_goal_clsf.setValue("FBV920");
			sbx_goal_clsf.setDisabled(true);

			grp_vst_plc.show("");
			spn_accnt_msg.show("");
		}else{
			grp_vst_plc.hide();
			spn_accnt_msg.hide();
		}
	}

	scwin.onpageunload = function() {

	};

	/**
	 * 출장정보 조회
	 */
	scwin.search = function(){
		com.execute(sbm_selectData, {
			successCallback : function(e){
				// 출장자 셋팅
				dma_biztripPsn.setJSON(dlt_compn.getRowJSON(0));
				dlt_compn.removeRow(0);
				dlt_compn.reform();
				tbx_compn_cnt.setValue(dlt_compn.getRowCount());

				// 출장복사일 경우
				if(scwin.data.isCopied){
					dma_trip.set("req_no", "");
					dma_trip.set("biztrip_cls_ymd", "");
					dma_trip.set("biztrip_start_ymd", scwin.data.today);					
					dma_trip.set("biztrip_nght", "");
					dma_trip.set("biztrip_day", "");
					dma_trip.set("attach_file_no", "");

					dma_trip.set("cud_type", "C");

					// 계정내역 신청번호 초기화
					for(var i=0; i<dlt_accnt.getRowCount(); i++){
						dlt_accnt.setCellData(i, "cud_type", "C");
						dlt_accnt.setCellData(i, "req_no", "");
					}
				}		

				if(dma_trip.get("biztrip_goal_clsf_nm") == "현장출장"){
					// 현장출장신청 셋팅
					scwin.setSpt(true); 
				}

				// 과세계정일 경우 
				for(var i=0; i<dlt_accnt.getRowCount(); i++){
					grd_accnt.setCellColor(i, "tax_cd_nm", (dlt_accnt.getCellData(i, "tax_cd_nm") == "과세")? "red" : "#333");	
				}				

				// (구)국내출장 또는 출장자 중에 이공계인턴쉽이 있는 경우 지급내역 보여줌
				if(dlt_pay.getRowCount() > 0){
					grp_pay_info.show("");
				}

				// 조회 후 셋팅
				scwin.setForm();	

				// 휴일내역 조회
				scwin.selectHoliday();			
			}
		});
	};

	/**
	 * 출장정보 조회 후 셋팅
	 */
	scwin.setForm = function(){
		// 결재상태 갱신 및 버튼컨트롤: 출장 복사의 경우 버튼 컨트롤 하지 않고 결재상태를 강제로 저장중으로 세팅
		if(scwin.data.isCopied){
			dma_trip.set("apr_state", "");
		}
		scwin.data.aprState = dma_trip.get("apr_state");

		// 버튼 제어
		com.biz.disabledButtons(scwin.data.aprState, dma_trip.get("req_no"));		

		if(scwin.data.isCopied) scwin.data.isCopied = false;

		// 업무담당자 수정권한 부여(버튼제어)
		var vMode = "edit";
		if(dma_trip.get("apr_state") >= "XAD020"){
			grp_page.setDisabled(true);
			grp_search.setDisabled(false);

			// 출장담당자일 경우 첨부파일 업로드 가능하게 기능 추가
			// 1006206 출장담당자, 1006205 총무담당자, 1006273 외부강의담당자
			if (/1006206|1006205|1006273/.test(com.getLoginInfo('roleCode'))) {
				btn_mngSave.show("");	// 담당자저장
				btn_mngSave.setDisabled(false);

				ica_start_dt.setDisabled(false);			// 출장일자(시작일)
				ica_end_dt.setDisabled(false);				// 출장일자(종료일)
				sbx_car_use.setDisabled(false);				// 원차량사용
				sbx_biztrip_way.setDisabled(false);			// 운임구분 
				sbx_goal_clsf.setDisabled(false);			// 출장목적
				txa_biztrip_goal.setDisabled(false);		// 출장목적 상세 
				ibx_biztrip_region.setDisabled(false);		// 방문처			
				rad_is_internal_region.setDisabled(false);	// 원내출장(원내 or 원외)
				cbx_notebook_out.setDisabled(false);		// 정보기기반출(노트북 반출)
				cbx_usb_out.setDisabled(false);					// 정보기기반출(휴대용 저장매체 반출)

				btn_schd_plus.setDisabled(false);		// 활동내역(+)
				btn_schd_minus.setDisabled(false);		// 활동내역(-)
				grd_schd.setDisabled(false);			// 활동내역
			}else{
				vMode = "view";	

				if(chk_accntNoUse.getValue() != "Y"){
					scwin.setTripExpns();
				}	
			}
		}else{
			if(scwin.data.serviceId == "S951"){		
				grp_page.setDisabled(false);
				sbx_goal_clsf.setDisabled(true);		
			}else{
				grp_page.setDisabled(false);
			}

		}

		// 첨부파일
		if(dma_trip.get("attach_file_no") != ""){					
			com.file.create(upload_A.getID(), {fileKey: dma_trip.get("attach_file_no"), mode: vMode});	
		}else{
			com.file.create(upload_A.getID(), {fileKey: "C320", mode: vMode})
		}

		// 출장경로 버튼 및 그리드 컨트롤		
		scwin.gridPathView();

		// 이지바로 과제일 경우 학술대회 보여줌
		if(dma_trip.get("biztrip_goal_clsf") == "FBV110"){
//			grp_pool.show("");
			grp_pool.hide();
		}		

		// 활동내역이 없으면 생성
		if(dlt_schd.getRowCount() == 0){
			btn_schd_plus.trigger("click");
		}
	}

	/**
	 * 이지바로 과제 여부 조회
	 */
	scwin.getEzbrYn = function(){
		// 이지바로과제 여부 체크
		var arr = dlt_accnt.getMatchedIndex("card_use_ex", "FAJ007", true);
		return (arr.length >0)? "Y":"N";
	};

	/**
	 * 목록 버튼 클릭 이벤트
	 */
	scwin.btn_pre_onclick = function(e) {
		// 현장출장일 경우
		if(dma_trip.get("biztrip_goal_clsf") == "FBV920"){
			com.moveList("S950");
		}else{
			com.moveList("C306");
		}

	};	

	/**
	 * 출장복사 버튼 클릭 이벤트
	 */
	scwin.btn_copy_onclick = function(e) {
		scwin.data.isCopied = true;
		var data = {
			flag : "DomLoad",
			type : "Read",
			ref_yn : "Y"
		};

		com.pop.open(com.pop.TYPE.TRIP, data, function(ret){
			dma_search.set("req_no", ret.req_no);
			scwin.search();
		});
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 */
	scwin.btn_save_onclick = function(e) {	
		scwin.data.isApproval = false;
		dma_trip.set("approval", "false");

		if(!scwin.preSave()) return;

		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				scwin.save();
			}
		});
	};

	/**
	 * 저장 실행
	 */
	scwin.save = function(){
		if(dma_trip.get("biztrip_nght") == '0' && dma_trip.get("biztrip_day") == '1'){
			dma_trip.set("slip_prcs_state", "FBM010");
		}

		com.file.transport( function(attachObj){
			dma_trip.set("attach_file_no", attachObj.upload_A);
			dma_trip.set("validAttachNo", attachObj.upload_A);
			dma_trip.set("start_region", dlt_path.getCellData(0,"start_region"));
			dma_trip.set("arriv_region", dlt_path.getCellData(dlt_path.getRowCount()-1,"start_region"));

			com.execute(sbm_saveDomTrip, {
				successCallback : function(e) { // 성공 콜백
					if(scwin.data.isApproval){
						// 결재화면 이동
						com.movePageMenu("A001", { "reqNo" :  dma_trip.get("req_no")});
					}else{
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function(){
							// 조회
							scwin.search();
						});
					}
				}
			});
		});	
	};

	/**
	 * 저장전 체크
	 */
	scwin.preSave = function(){
		var validData = [
			{dataObj : dma_trip
			   , valInfoArr :[
			        { id : "biztrip_start_ymd", mandatory : true}
			       ,{ id : "biztrip_cls_ymd", mandatory : true}
				   ,{ id : "car_use_yn", mandatory : true } 
				   ,{ id : "biztrip_goal_clsf", mandatory : true } 
				   ,{ id : "biztrip_goal", mandatory : true } 
				   ,{ id : "biztrip_region", mandatory : true }
				   ,{ id : "is_internal_region", mandatory : true }
				   ,{ id : "depart_base", mandatory : true }
				   ,{ id : "card_meal", mandatory : true }
				]
			  , isCheckModified : false
			},
			{dataObj : dma_biztripPsn
			  , valInfoArr :[
				    { id : "biztrip_psn_syspayno", mandatory : true}
				   ,{ id : "agent_psn_syspayno", mandatory : true } 
			    ]
			   , isCheckModified : false
			},
			{dataObj : dlt_path
			  , valInfoArr :[
				    { id : "arriv_region", mandatory : true}
			    ]
			   , isCheckModified : false
			},			
			{dataObj : dlt_schd
			  , valInfoArr :[
				    { id : "major_intrv_psn", mandatory : true}
				   ,{ id : "major_intrv_tel", mandatory : true }
				   ,{ id : "agncy_nm", mandatory : true } 
				   ,{ id : "actn_detls", mandatory : true } 
			    ]
			   , isCheckModified : false
			},
		];

		if(dlt_compn.getRowCount() > 0){
			var data = {dataObj : dlt_compn
						  , valInfoArr :[
							    { id : "biztrip_psn_syspayno", mandatory : true}
							   ,{ id : "agent_psn_syspayno", mandatory : true } 
						    ]
						   , isCheckModified : false
						};
			validData.push(data);
		}

		// 현장출장일 경우
		if(dma_trip.get("biztrip_goal_clsf") == "FBV920"){
			var data = {dataObj : dlt_vst
					  , valInfoArr :[
						    { id : "cmpy_nm_krchar", mandatory : true}
						   ,{ id : "bsns_psn_regst_no", mandatory : true }
						   ,{ id : "reprs_psn", mandatory : true } 
						   ,{ id : "cmpy_addr", mandatory : true } 
						   ,{ id : "cust_no", mandatory : true } 
						   ,{ id : "cunsl_rcpt_no", valFunc : function(value) {
									if(com.isEmpty(value)) {
										return "message.xom.ui.ccs.002"; //기업현장출장신청은 기술지도 등록이 사전에 등록되어야 합니다.\n관련문의는 기업협력지원실로 부탁드립니다.
									} 
								} 
							}
					    ]
					 , isCheckModified : false
				};
			validData.push(data);	
		}

		if(cbx_no_expns.getValue() != "Y"){
			validData.push({dataObj : dlt_accnt, valInfoArr :[
				 { id : "accnt_no", mandatory : true},
				 { id : "expns_cd", mandatory : true},
				 { id : "accnt_cd", mandatory : true}
				], isCheckModified : false
			});
		}		

		if(!com.validateData(validData)) return false;

		//방문처
//		for(var i=0; i<dlt_vst.getRowCount(); i++){
//			if(dlt_vst.getCellData(i,"cunsl_rcpt_no") == ""){
//				com.alert("message.xom.ui.ccs.002"); //기업현장출장신청은 기술지도 등록이 사전에 등록되어야 합니다.\n관련문의는 기업협력지원실로 부탁드립니다.
//				return false;	
//			}
//		}

		// 원차량 출장비 체크(FBV150 외부강의등)
		if(dma_trip.get("car_use_yn") != "FDO001" && dma_trip.get("biztrip_goal_clsf") !="FBV150" && dma_trip.get("no_trvel_expns") == "Y"){
			com.alert("message.xom.ui.ccs.016"); //원 차량 이용시 출장비는 차량관리비 계정(BG)에 흡수되므로, 출장비를 반드시 입력하시기 바랍니다.
			return false;
		}	

		if(dma_trip.get("car_use_yn") != "FDO001" && dma_trip.get("biztrip_way") == ""){
			com.alert(com.getMsg("message.xom.co.com.0003", "운임구분"));
			return false;
		}

		// 계정 중복 체크
		var accnts = dlt_accnt.getColData("accnt_no");
		if (accnts.some( function(a){ return accnts.indexOf(a) !== accnts.lastIndexOf(a);})) {
			com.alert("message.xom.ui.ccs.017");
			return false;
		}

		// 계정내역의 출장경비가 0일 경우 출장비없음에 체크
		var totExpnsSum = 0;
		for(var i=0; i<dlt_accnt.getRowCount(); i++){
			totExpnsSum += Number(dlt_accnt.getCellData(i, "tot_expns"));
		}

		if(totExpnsSum == 0){
			cbx_no_expns.setValue("Y");			
			// 출장경비 초기화
			scwin.initAmtRmkExpns();

			// 출장경비 비활성화
			grp_expns.setReadOnly(true);
		}

		// 비상근직원의 출장기간 유효성 검증
		// 2022.10.06.  비상근 직급 1박 2일 출장상신이 가능하도록 요청(이재상)에 의해 체크로직 주석처리
		//if(!scwin.checkPeriodPartTimer()) return false;

		// 휴무일내역 유효성 검증
		for(var i = 0; i < dlt_holiday.getRowCount(); i++){
			if( dlt_holiday.getCellData( i, "rest_cd") == "CTD010"){
				if (dlt_holiday.getCellData( i, "holiday_alt_day") == "" && dlt_holiday.getCellData( i, "day_no") != "6"){
					com.alert(com.getMsg("message.xom.co.com.0003", "대체휴가일"));
					grd_holiday.setFocusedCell( i, "holiday_alt_day" , true );
					return false;
				}
				if (dlt_holiday.getCellData( i, "detls_scheads") == ""){
					com.alert(com.getMsg("message.xom.co.com.0009", "활동내역"));
					grd_holiday.setFocusedCell( i, "detls_scheads" , true );
					return false;
				}
			}
		}

		return true;
	};	

	scwin.checkPeriodPartTimer = function(ymd){	
		if (dma_trip.get("biztrip_day") > 0){
			var arrs = new Array({ occuType: dma_biztripPsn.get("occutyp_cd"), posiType: dma_biztripPsn.get("posi_cd")});
			for (var i=0; i < dlt_compn.getRowCount(); i++){
				arrs.push({
					  occuType: dlt_compn.getCellData( i, "occutyp_cd"),
					  posiType: dlt_compn.getCellData( i, "posi_cd")
				});
			}
			// 비상근직군 중 임원(비상근)=감사(비상임)의 경우는 제외
			if (arrs.some( function(obj){ return "310000" === obj.occuType && "310010" !== obj.posiType; })){
//				ica_start_dt.setValue(ymd ? ymd : "");
//				ica_end_dt.setValue(ymd ? ymd : "");
//				dma_trip.set("biztrip_nght",0);
//				dma_trip.set("biztrip_day", 1);

//				com.alert( "단시간근로자(비상근)의 경우 2일 이상의 출장신청이 불가함을 안내드립니다.\n"
//					 + "■ 관련\n"
//					 + "? 단시간(비상근) 근로자 채용계약서(3.근로시간 및 휴게시간)\n"
//					 + "? 근로기준법 제18조(단시간근로자의 근로조건)\n"
//					 + "※ 단시간(비상근)근로자 소정근로시간 : 1주 15시간 이상 / 월 60시간 이상 초과 활용 불가");

				// 2022.10.26. 알람창을 띄우고 1박이상 출장신청은 가능하도록 수정
				com.alert( "단시간 근로자(비상근)의 경우, 출장 신청 이후 EIP- 근무시간관리에서 본인의 출장시간을 조정하여주시기 바랍니다");					 
				//return false;
			}
		}
		return true;
	}

	/**
	 * 학술대회 버튼 클릭 이벤트
	 */
	scwin.btn_confrHis_onclick = function(e) {
		if(dma_trip.get("req_no") == "") return;

		// 학술대회이력
		var data = {
			req_no : dma_trip.get("req_no"),
			ezbr_yn : scwin.ezbrYn(),
			ref_yn : "Y"
		};

		com.pop.open(com.pop.TYPE.CONFPOOL, data, function(ret) {
			dma_trip.set("pool_conf_nm", ret);
		});
	};

	/**
	 * 이지바로 과제 여부
	 */
	scwin.ezbrYn = function(){
		var accntArr = dlt_accnt.getMatchedIndex("card_use_ex", "FAJ007");
		return (accntArr.length > 0)? "Y":"N";
	};

	/**
	 * 출장목적 변경 이벤트
	 */
	scwin.sbx_goal_clsf_onviewchange = function(info) {
		if(this.getValue() == "FBV110" && dma_trip.get("req_no") != ""){ // 학회참석(논문발표)
//			grp_pool.show("");
			grp_pool.hide();
		}else{
			grp_pool.hide();
		}
	};

	/**
	 *출장비없음 변경 이벤트
	 */
	scwin.cbx_no_expns_onviewchange = function(info) {
		if(this.getValue() == "Y"){ // 출장비 없음
			grp_accnt.hide();

			// 출장경비 초기화
			scwin.initAmtRmkExpns();

	 		//총액 재계산 및 계정내역 금액 계산
			scwin.setAccntTotExpns();

			// 출장경비 비활성화
			grp_expns.setReadOnly(true);

		}else{
			grp_accnt.show("");

			// 출장경비: 운임 비고 생성
			scwin.makeFareFeeRmk();

			// 기타 출장 경비 생성
			scwin.setTripExpns();

			grp_expns.setReadOnly(false);
		}
	};

	/**
	 * 운임경비 및 비고 계산
	 */
	scwin.makeFareFeeRmk = function(){
		if(cbx_no_expns.getValue() == "Y") return;

		// 총 운임비 및 비고 생성
		var totFareFee = 0;
		var rmks = [];
		for(var i=0; i<dlt_path.getRowCount(); i++){
			// 도착지가 설정되어있지 않으면 생성하지 않음
			if(dlt_path.getCellData(i, "arriv_region") == ""){
				return;	
			}

			totFareFee += Number(dlt_path.getCellData(i, "biztrip_fee"));
			rmks.push(dlt_path.getCellData(i, "rmk"));
		}

		// 출장인원수
		var psnCnt = dlt_compn.getRowCount() +1;

		if(/exprss|vessel/.test(sbx_biztrip_way.getValue())){ // 항공, 선박
			dma_trip.set("fare_fee", 0);
			var way = (sbx_biztrip_way.getValue() == "exprss")?"항공":"선박";
			dma_trip.set("fare_fee_rmk", "= 실비정산(" + way + ") X 2(왕복) X "  + psnCnt + "명");
		}else{ // 열차/버스 등
			var ngt = Number(dma_trip.get("biztrip_nght"));
			var day = Number(dma_trip.get("biztrip_day"));
			// 무박 1일 초과면 운임비 X 일수
			dma_trip.set("fare_fee", totFareFee * psnCnt * ((ngt==0 && day >1)? day : 1));
			dma_trip.set("fare_fee_rmk", "=( "+ rmks.join(" + ") +" ) X "+ psnCnt +"(명)" + ((ngt ==0 && day>1)? " X " + day + "일" : ""));
		}
	}

	/**
	 * 출장비 없음 체크시 출장경비 초기화
	 */
	scwin.initAmtRmkExpns = function(){
		dma_trip.set("fare_fee", 0);
		dma_trip.set("ymd_fee", 0);
		dma_trip.set("lodg_fee", 0);
		dma_trip.set("meal_fee", 0);
		dma_trip.set("card_meal", 0);

		dma_trip.set("fare_fee_rmk", "");
		dma_trip.set("ymd_fee_rmk", "");
		dma_trip.set("lodg_fee_rmk", "");
		dma_trip.set("meal_fee_rmk", "");
	};


	/**
	 * 출장직급과 인사정보 직급 바인딩
	 */
	scwin.setPosiInfo = function(type, row){
		var posiNm = "";
		if(type == "Biz"){
			posiNm = dma_biztripPsn.get("posi_nm");
		}else{
			posiNm = dlt_compn.getCellData(row, "posi_nm");
		}

		var posiClsf = "FBG090";
		if(/임원/.test(posiNm)){
			posiClsf = "FBG010";
		}else if(/수석|연구위원/.test(posiNm)){
			posiClsf = "FBG020";
		}else if(/선임|주임/.test(posiNm)){
			posiClsf = "FBG030";
		}

		if(type == "Biz"){
			dma_biztripPsn.set("posi_clsf", posiClsf);
			if(scwin.data.posi_clsf != "" && scwin.data.posi_clsf != posiClsf){
				// 직급이 변경되면  기타경비 재조회
				scwin.getEtcFee();
			}
		}else{
			dlt_compn.setCellData(row, "posi_clsf", posiClsf);
		}
	};

	/**
	 * 출장자 검색 버튼 클릭 이벤트
	 */
	scwin.btn_biztrip_psn_onclick = function(e) {
		scwin.getEmpInfo("Biz", idx_biztrip_psn_empno.getValue());
	};

	/**
	 * 사원검색 및 팝업호출
	 */
	scwin.getEmpInfo = function(type, empNo){
		var data = {empno : empNo,
					work_clsf : "HAG010"
		};
		if(empNo == ""){
			data.ref_yn = "Y";	
		}

		com.pop.open(com.pop.TYPE.HUM, data, function(ret) {
			scwin.setBizTripPsn(type, ret);
		});
	}

	/**
	 * 출장자 정보 셋팅
	 * type : Biz-출장자, Compn - 동반자
	 */
	scwin.setBizTripPsn = function(type, data){
		if(type == "Biz"){ // 출장자
			var arr = dlt_compn.getMatchedIndex("biztrip_psn_syspayno", data.syspayno, true);
			if(arr.length > 0){
				com.alert(com.getMsg("message.xom.ui.ccs.007", data.nm));
				arr.reverse().forEach( function(index){
					dlt_compn.removeRow( index);					
				});
				return;
			}

			scwin.data.posi_clsf = dma_biztripPsn.get("posi_clsf");

			dma_biztripPsn.set("biztrip_psn_clsf", "FBH010");
			dma_biztripPsn.set("biztrip_psn_syspayno", data.syspayno);
			dma_biztripPsn.set("biztrip_psn_empno", data.empno);
			dma_biztripPsn.set("biztrip_psn_nm", data.nm);
			dma_biztripPsn.set("biztrip_psn_dept_cd", data.dept_cd);
			dma_biztripPsn.set("biztrip_psn_dept_new_ymd", data.dept_new_ymd);						
			dma_biztripPsn.set("biztrip_dept_nm", data.dept_nm);
			dma_biztripPsn.set("posi_nm", data.posi_nm);
			dma_biztripPsn.set("posi_cd", data.posi_cd);
			dma_biztripPsn.set("occutyp_cd", data.occutyp_cd);

			scwin.data.work_land = data.work_land;
			// 직급구분
			scwin.setPosiInfo(type);		

			// 출장 경로 초기화
			scwin.gridPathInit();

			// 출장자 중에 이공계인턴쉽이 있는경우 (posi_cd = 353010)
			if(data.posi_cd == "353010"){
				dma_search.set("biztrip_psn_syspayno", data.syspayno);
				dma_search.set("from_ymd", ica_start_dt.getValue());
				com.execute(sbm_searchPayInfoPosi, {
					successCallback : function(e){						
						dlt_pay.setJSON(e.responseJSON.ctrDomPayVoList);

						if(dlt_pay.getRowCount() > 0){
							grp_pay_info.show("");	
						}
					}
				});
			}

		}else if(type == "Compn"){ // 동반자
			var row = grd_compn.getFocusedRowIndex();
			if(data.syspayno == dma_biztripPsn.get("biztrip_psn_syspayno")){
				com.alert("message.xom.ui.ccs.005");
				return;
			}

			var arr = dlt_compn.getMatchedIndex("biztrip_psn_syspayno", data.syspayno, true);
			if(arr.length > 0){				
				com.alert(com.getMsg("message.xom.ui.ccs.006", data.nm));
				return;
			}
			dlt_compn.setCellData(row, "biztrip_psn_syspayno", data.syspayno);
			dlt_compn.setCellData(row, "biztrip_psn_empno", data.empno);
			dlt_compn.setCellData(row, "biztrip_psn_nm", data.nm);
			dlt_compn.setCellData(row, "biztrip_psn_dept_cd", data.dept_cd);
			dlt_compn.setCellData(row, "biztrip_psn_dept_new_ymd", data.dept_new_ymd);
			dlt_compn.setCellData(row, "biztrip_dept_nm", data.dept_nm);
			dlt_compn.setCellData(row, "posi_nm", data.posi_nm);
			dlt_compn.setCellData(row, "posi_cd", data.posi_cd);
			dlt_compn.setCellData(row, "occutyp_cd", data.occutyp_cd);

			scwin.setPosiInfo(type, row);

			// 휴일 내역에 동반자 셋팅
			if(dlt_holiday.getRowCount() > 0){
				scwin.gridHolidayAppend()
			}

			// 출장경비: 운임 비고 생성
			scwin.makeFareFeeRmk();	

			// 기타경비 계산
			scwin.setTripExpns();

			// 출장자 중에 이공계인턴쉽이 있는경우 (posi_cd = 353010)
			if(data.posi_cd == "353010"){
				dma_search.set("biztrip_psn_syspayno", data.syspayno);
				dma_search.set("from_ymd", ica_start_dt.getValue());
				com.execute(sbm_searchPayInfoPosi, {
					successCallback : function(e){
						dlt_pay.setJSON(e.responseJSON.ctrDomPayVoList);
						if(dlt_pay.getRowCount() > 0){
							grp_pay_info.show("");	
						}
					}
				});
			}

		}else if(type == "Agent"){ // 동반자 그리드 직무대행자
			var row = grd_compn.getFocusedRowIndex();
			dlt_compn.setCellData(row, "agent_psn_syspayno", data.syspayno);
			dlt_compn.setCellData(row, "agent_empno", data.empno);
			dlt_compn.setCellData(row, "agent_nm", data.nm);

		}else if(type == "BizAgent"){ // 출장자 직무대행자
			dma_biztripPsn.set("agent_psn_syspayno", data.syspayno);
			dma_biztripPsn.set("agent_empno", data.empno);
			dma_biztripPsn.set("agent_nm", data.nm);

		}else if(type == "Schd"){ // 활동내역 그리드 면담자
			var row = grd_schd.getFocusedRowIndex();
			dlt_schd.setCellData(row, "major_intrv_psn", data.nm);
			dlt_schd.setCellData(row, "major_intrv_tel", data.mobile);			
		}	
	};

	scwin.gridPathInit = function(){
		dlt_path.removeAll();
		dlt_path.reform();

		dlt_path.insertRow(0);
		var cmnList = gcm.cmnCdCache['dlt_cmnCd_CTC'].filter(function(elt, i) {
			return elt.mngmtItem1 == scwin.data.work_land;
		});	

		dlt_path.setCellData(0, "start_region", cmnList[0].mngmtItem3);
		grd_path.setCellDisabled(0, "chk", true);
	};

	/**
	 * 동반자 그리드 textimage 클릭 이벤트
	 */
	scwin.grd_compn_ontextimageclick = function(row,col) {		
		if(col == grd_compn.getColumnIndex("biztrip_psn_empno")){
			if(dma_biztripPsn.get("biztrip_psn_syspayno") == ""){
				com.alert(com.getMsg("message.xom.co.com.0001","출장자"));	
				dlt_compn.setCellData(row, "biztrip_psn_empno", "");
				return;
			}

			scwin.getEmpInfo("Compn", dlt_compn.getCellData(row, "biztrip_psn_empno"));

		}else if(col == grd_compn.getColumnIndex("agent_empno")){
			scwin.getEmpInfo("Agent", dlt_compn.getCellData(row, "agent_empno"));
		}
	};

	/**
	 * 동반자 그리드 변경 이벤트
	 */
	scwin.grd_compn_onviewchange = function(info) {
		var row = info.rowIndex;
		if(info.colIndex == grd_compn.getColumnIndex("biztrip_psn_empno")){
			if(dlt_compn.getCellData(row, "biztrip_psn_empno") == ""){
				dlt_compn.setCellData(row, "biztrip_psn_syspayno", "");
				dlt_compn.setCellData(row, "biztrip_psn_nm", "");
				dlt_compn.setCellData(row, "biztrip_psn_resid_no", "");
				dlt_compn.setCellData(row, "occutyp_cd", "");
				dlt_compn.setCellData(row, "posi_cd", "");
				dlt_compn.setCellData(row, "posi_clsf", "");
				dlt_compn.setCellData(row, "biztrip_dept_nm", "");
				dlt_compn.setCellData(row, "biztrip_psn_dept_cd", "");
			}else{
				if(dma_biztripPsn.get("biztrip_psn_syspayno") == ""){
					com.alert(com.getMsg("message.xom.co.com.0001","출장자"));	
					dlt_compn.setCellData(row, "biztrip_psn_empno", "");
					return;
				}	

				scwin.getEmpInfo("Compn", dlt_compn.getCellData(row, "biztrip_psn_empno"));
			}			
		}else if(info.colIndex == grd_compn.getColumnIndex("agent_empno")){
			if(dlt_compn.getCellData(row, "agent_empno") == ""){
				dlt_compn.setCellData(row, "agent_nm", "");
				dlt_compn.setCellData(row, "agent_psn_syspayno", "");
			}else{
				scwin.getEmpInfo("Agent", dlt_compn.getCellData(row, "agent_empno"));
			}
		}		
	};

