	};

	/**
	 * 행추가
	 */
	scwin.addRow = function(type){
		if(type == "Compn"){ // 동반자
			var idx = com.addRow(grd_compn);
			dlt_compn.setCellData(idx, "biztrip_psn_clsf", "FBH020");

		}else if(type == "Schd"){ // 활동내역
			var idx = com.addRow(grd_schd);
			dlt_schd.setCellData(idx, "start_ymd", ica_start_dt.getValue());
			dlt_schd.setCellData(idx, "cls_ymd", ica_end_dt.getValue());
			if(idx > 0){
				dlt_schd.setCellData(idx, "agncy_nm", dlt_schd.getCellData(idx-1, "agncy_nm"));
				dlt_schd.setCellData(idx, "actn_detls", dlt_schd.getCellData(idx-1, "actn_detls"));
			}
		}else if(type == "Accnt"){ // 계정내역
			var idx = com.addRow(grd_accnt);
			dlt_accnt.setCellData(idx, "expns_cd", "271");
			dlt_accnt.setCellData(idx, "cud_type", "C");
		}
	}

	/**
	 * 방문처 추가 버튼 클릭 이벤트
