	};

	/**
	 * 계정내역 추가 버튼 클릭 이벤트
	 */
	scwin.btn_accnt_plus_onclick = function(e) {
		scwin.addRow("Accnt");
	};	

	scwin.removeGridRows = function(gridObj, type){
		var rows = gridObj.getCheckedIndex("chk");
		if(rows.length == 0){
			com.alert(com.getMsg("message.xom.co.com.0003", "삭제할 행"));
			return;
		}
		rows.reverse().forEach(function(rowIndex){
			if(type == "Compn"){ // 동반자
				dlt_compn.removeRow(rowIndex);
			}else if(type == "Schd"){ // 활동내역				
				dlt_schd.removeRow(rowIndex);	
			}else if(type == "Path"){ // 경로				
				var rowIndexes = grd_path.getCheckedIndex("chk");
				rowIndexes.reverse();
				rowIndexes.forEach(function(idx) {
					dlt_path.setCellData(idx+1, "start_region", dlt_path.getCellData(idx-1, "arriv_region"));
					dlt_path.removeRow(idx);
				});

			}else if(type == "Accnt"){ // 계정내역
				dlt_accnt.setCellData(rowIndex, "cud_type", "D");
				dlt_accnt.removeRow(rowIndex);
			}
		});

		if(type == "Compn"){ // 동반자
			dlt_compn.reform();	

			// 출장경비: 운임 비고 생성
			scwin.makeFareFeeRmk();	

			// 기타경비 계산
			scwin.setTripExpns();
		}else if(type == "Schd"){ // 활동내역		
			dlt_schd.reform();			
		}else if(type == "Path"){ // 경로		
			scwin.getFareFee();
		}
	}

	/**
	 * 동반자 삭제 버튼 클릭 이벤트
	 */
	scwin.btn_compn_minus_onclick = function(e) {
