	};

	/**
	 *예산조회 버튼 클릭 이벤트
	 */
	scwin.btn_bugt_onclick = function(e) {
		var rows = grd_accnt.getRowCount();
		if(rows > 0){
			var row = (rows == 1)? 0 : grd_accnt.getFocusedRowIndex();
			if(row <0){
				 com.alert(com.getMsg("message.xom.co.com.0003", "계정번호"));
				 return;
			}
			com.pop.open(com.pop.TYPE.BUGT, {accnt_no:dlt_accnt.getCellData(row, "accnt_no")}, function(ret) {				
			});
		}
	};

	/**
	 * 계정내역 그리드 계정명 textimage 클릭 이벤트
	 */
	scwin.grd_accnt_ontextimageclick = function(row,col) {
		if(ica_end_dt.getValue() == ""){
			com.alert(com.getMsg("message.xom.co.com.0007", "출장종료일"));
			return;
		}

		if(grd_accnt.getColumnID(col) == "accnt_no"){
			var data = {
				accnt_no : dlt_accnt.getCellData(row, "accnt_no"),
				cls_ymd : ica_end_dt.getValue(),
				ref_yn : "Y"
			};			

			com.pop.open(com.pop.TYPE.ACNNO, data, function(ret) {
				scwin.setAccntRow(row, ret);
			});
		}
	};

	/**
	 * 계정내역 그리드 사용계정버튼 클릭 이벤트
	 */
	scwin.grd_accnt_oncellclick = function(row,col,colId) {
		if(grd_accnt.getColumnID(col) == "btn_accnt_use"){
			if(ica_end_dt.getValue() == ""){
				com.alert(com.getMsg("message.xom.co.com.0007", "출장종료일"));
				return;
			}

			let data = {
				"biztrip_psn_syspayno" : dma_biztripPsn.get("biztrip_psn_syspayno")
			  ,	"biztrip_ymd" : ica_end_dt.getValue()
			  , "flag" : "dom"
			}

			biz.ccs.pop.open(biz.ccs.pop.TYPE.RCT_ACCNT, data, function(ret) {
				scwin.setAccntRow(row, ret, "ACCNT_USE");
			});
		}
	};







	scwin.setAccntRow = function(row, ret, type){
		if(ret.cls_ymd < ica_end_dt.getValue()){
			com.alert("message.xom.ui.ccs.003"); // 과제기간이 종료된 계정입니다.다른 계정을 선택해주세요.
			return;
		}

		if(!/RAK041|RAK061/.test(ret.accnt_state)){ // RAK041 집행가능, RAK061 미입금집행승인
			com.alert(com.getMsg("message.alert.fin.com.0008", ret.accnt_no, "출장신청"));
			return;
		}		

		dlt_accnt.setCellData(row, "accnt_no", ret.accnt_no);
		dlt_accnt.setCellData(row, "accnt_no_nm", ret.accnt_no_nm);
		dlt_accnt.setCellData(row, "fomat_unit", ret.fomat_unit);
		dlt_accnt.setCellData(row, "prj_no", ret.prj_no);
		dlt_accnt.setCellData(row, "tax_cd_nm", (ret.tax_cd == "FTX001")? "과세" : "비과세");
		grd_accnt.setCellColor(row, "tax_cd_nm", (ret.tax_cd == "FTX001")? "red" : "#333");		

		if(type == "ACCNT_USE"){ // 최근사용계정
			dlt_accnt.setCellData(row, "accnt_respn_psn_syspayno", ret.accnt_respn_psn_syspayno);
			dlt_accnt.setCellData(row, "accnt_rspns_empno", ret.accnt_respn_psn_empno);
			dlt_accnt.setCellData(row, "accnt_rspns_nm", ret.accnt_respn_psn_nm);	
		}else{
			dlt_accnt.setCellData(row, "accnt_respn_psn_syspayno", ret.accnt_rspns);
			dlt_accnt.setCellData(row, "accnt_rspns_empno", ret.accnt_rspns_empno);
			dlt_accnt.setCellData(row, "accnt_rspns_nm", ret.accnt_rspns_nm);		
		}

		if(ret.accnt_no == "PJC22012"){
			dlt_accnt.setCellData(row, "expns_cd", "394");
		}else{
			dlt_accnt.setCellData(row, "expns_cd", "271");
		}		

		// 회계코드 조회
		dma_search.set("accnt_no", ret.accnt_no);
		dma_search.set("expns_cd", dlt_accnt.getCellData(row, "expns_cd"));
		com.execute(sbm_selectExpnsList, {
			successCallback : function(ret){	
				if(ret.responseJSON.elData.fbaItmExpnsVoList.length == 0){
					dlt_accnt.setCellData(row, "accnt_no", "");
					com.alert("해당 계정번호는 사용하실 수 없습니다.");
					return;
				}			

				var data = ret.responseJSON.elData.fbaItmExpnsVoList[0];
				dlt_accnt.setCellData(row, "accnt_cd", data.accnt_cd);				

				// 총액 셋팅
				scwin.setAccntTotExpns();	

				// 예실잔액 체크
				var param = {
					bugt_yr : scwin.data.today.substring(0,4),
					accnt_no : dlt_accnt.getCellData(row, "accnt_no"),
					accnt_cd : data.accnt_cd,
					relat_no : dlt_accnt.getCellData(row, "accnt_rspns_empno")
				};

				com.biz.checkBugtRamt(param, function(ret){
					var bugtAmt = Number(ret.bugt_amt);
					var causeAmt = Number(ret.cause_amt);
					var enfrcAmt = Number(ret.enfrc_amt);
					var reqAmt = Number(dlt_accnt.getCellData(row, "tot_expns"));

					if(/FAF011|FAF021|FAF031/.test(ret.bugt_ctrl_way)){
						if(bugtAmt - causeAmt - enfrcAmt < reqAmt){
							alert("예산이 부족합니다. 관련 계정의 예실대비표 확인 부탁드립니다.");	
						}
					}
				});
			}

		});
	}

	/**
	 * 계정내역 그리드 변경  이벤트
	 */
	scwin.grd_accnt_onviewchange = function(info) {
		var row = info.rowIndex;
		var col = info.colIndex;
		if(grd_accnt.getColumnID(col) == "tot_expns"){
			if(dlt_accnt.getCellData(row, "tot_expns") == 0){
				com.alert("message.xom.ui.ccs.004"); // 출장경비가 0원인 경우, 출장비 : 출장비 없음에 체크하여주시기 바랍니다.
				cbx_no_expns.setValue("Y");
			}
		}else if(grd_accnt.getColumnID(col) == "accnt_no"){
			if(info.newValue.length == 8){
				var data = {
					accnt_no : dlt_accnt.getCellData(row, "accnt_no"),
					cls_ymd : ica_end_dt.getValue()
				};

				data.ref_yn = (dlt_accnt.getCellData(row, "accnt_no") == '')? "Y":"N";

				com.pop.open(com.pop.TYPE.ACNNO, data, function(ret) {
					scwin.setAccntRow(row, ret);
				});
			}
		}
	};

	/**
	 * 결재신청 전 처리
	 */
	scwin.processAppr = function(){
		scwin.data.isApproval = true;

		// 서버사이드 업무로직 유효성 검증 시작		
		var psnArray = dlt_compn.getColData("biztrip_psn_syspayno");
		psnArray.push(dma_biztripPsn.get("biztrip_psn_syspayno"));

		dma_aprCheck.set("req_no", dma_trip.get("req_no"));
		dma_aprCheck.set("biztrip_start_ymd", ica_start_dt.getValue());
		dma_aprCheck.set("biztrip_cls_ymd", ica_end_dt.getValue());
		dma_aprCheck.set("biztrip_psn_syspayno", psnArray.join(","));
		//dma_aprCheck.set("biztrip_psn_syspayno", "20026495,20130576");
		//dma_aprCheck.set("accnt_no", "PIR19670,PNK20180");
		dma_aprCheck.set("goal_clsf", dma_trip.get("biztrip_goal_clsf"));
		dma_aprCheck.set("no_trvel_expns", dma_trip.get("no_trvel_expns"));

		var accntArray = [];
		var expnsCd =  dlt_accnt.getCellData(0, "expns_cd"); // 2023.01.11.비용코드 394 일 때 PJC23012 비참여연구원도 가능하도록(XC22023001FA)
		for(var i=0; i<dlt_accnt.getRowCount(); i++){
			var rowData = dlt_accnt.getRowJSON(i);
			if(rowData.prj_no.trim() != "" && !biz.ccs.exceptAccntMember(rowData.accnt_no, expnsCd)){
				accntArray.push(rowData.accnt_no);
			}
		}		
		dma_aprCheck.set("accnt_no", accntArray.join(","));

		com.execute(sbm_checkApr, {
			successCallback : function(e){
				var data ={};
				if(e.responseJSON.elData.rst_val == "prd_dup"){ // 출장기간 중복여부
					data.type = "PRD_DUP";
					data.rstData = e.responseJSON.elData.prdDupList;
				}else if(e.responseJSON.elData.rst_val == "no_rep"){ // 복명서 작성여부
					data.type = "NO_REP";
					data.rstData = e.responseJSON.elData.prdDupList;
				}else if(e.responseJSON.elData.rst_val == "othr_wrk"){ // 타업종사여부 확인
					data.type = "OTHR_WRK";
					data.rstData = e.responseJSON.elData.prdDupList;
				}else if(e.responseJSON.elData.rst_val == "no_join"){ // 참여연구원 확인
					data.type = "NO_JOIN";
					data.rstData = e.responseJSON.elData.noJoinList;
				}

				// 검증팝업 호출
				if(e.responseJSON.elData.rst_val != "success"){
					biz.ccs.pop.open(biz.ccs.pop.TYPE.APR_CHK, data, function(ret) {
					});
				}else{
					// 출장통계조회
					scwin.getTripCnt();		
				}
			}
		});		
	};

	/**
	 * 결재신청버튼 클릭 이벤트
	 */
	scwin.btn_apr_onclick = function(e) {
		if(!scwin.preSave()) return;

		var taxRows = dlt_accnt.getMatchedIndex("tax_cd_nm", "과세", true);
		if(ibx_biztrip_nght > '0' && taxRows.length > 0){
			com.alert("<font style='color:red'>* 본 출장계정은 과세계정으로, 숙박비는 법인카드로 진행하여 주시기 바랍니다. 개인카드 결제시 '부가세'(환급 대상)를 제외한 숙박비로 실비정산되어야 합니다.</font>", function(ret){
				if(ret){
					scwin.processAppr();
				}
			}, true);			
		}else{
			scwin.processAppr();
		}
	};	

	/**
	 * 출장통계조회
	 */
	scwin.getTripCnt = function(){
		dma_search.set("biztrip_psn_syspayno", dma_biztripPsn.get("biztrip_psn_syspayno"));
		dma_search.set("curr_yy", scwin.data.today.substring(0,4));
		dma_search.set("curr_ymd", scwin.data.today);	
		com.execute(sbm_searchTripPsnCount, {
			successCallback : function(e){
				dlt_tripCnt.setJSON(e.responseJSON.ctrTripCurStnVoList);

				var outSum = 0;
				for(var i=0; i<dlt_tripCnt.getRowCount(); i++){					
					outSum+= Number(dlt_tripCnt.getCellData(i, "ctr_out_region"));
				}

				if(outSum >= 101){
					btn_trip_info.trigger("click");						
				}else{
					// 사후결재 확인
					scwin.confirmPostApr();
				}		
			}
		});
	};

	/**
	 * 사후결재 확인
	 */
	scwin.confirmPostApr = function(){
		var data = {
			req_no : dma_trip.get("req_no"),
			docu_id : "C86",
			syspayno : dma_biztripPsn.get("biztrip_psn_syspayno"),
			action_ymd : dma_trip.get("biztrip_start_ymd")
		};

		com.biz.confirmPostApr(data, function(ret){
			if(ret){
				scwin.sendApr();
			}
		});
	};

	/**
	 * 결재신청(저장 후 결재)
	 */
	scwin.sendApr = function(){
		dma_trip.set("approval", "true");

		scwin.save();
	};

	/**
	 * 출장자정보 변경 이벤트
	 */
	scwin.dma_biztripPsn_onmodelchange = function(info) {
		if(info.key == "biztrip_psn_empno" && info.newValue == ''){			
			dma_biztripPsn.set("biztrip_psn_syspayno", "");
			dma_biztripPsn.set("biztrip_psn_nm", "");
			dma_biztripPsn.set("biztrip_psn_resid_no", "");
			dma_biztripPsn.set("occutyp_cd", "");
			dma_biztripPsn.set("posi_cd", "");
			dma_biztripPsn.set("posi_clsf", "");
			dma_biztripPsn.set("biztrip_dept_nm", "");
			dma_biztripPsn.set("biztrip_psn_dept_cd", "");			
		}else if(info.key == "agent_empno" && info.newValue == ''){
			dma_biztripPsn.set("agent_nm", "");
		}
	};

	/**
	 * 휴일 카운트
	 */
	scwin.selectHoliday = function(){
		if(ica_start_dt.getValue() == "" || ica_end_dt.getValue() == "") return;

		dma_search.set("from_ymd", ica_start_dt.getValue());
		dma_search.set("to_ymd", ica_end_dt.getValue());
		dma_search.set("req_no", dma_trip.get("req_no"));

		com.execute(sbm_selectHoliday, {
			successCallback : function(e){

				if(dlt_holiday.getRowCount() == 0){
					grp_holiday.hide();
				}else{
					grp_holiday.show("");	

					var syspaynos = dlt_holiday.getUnmatchedIndex("biztrip_syspayno", "", true);
					if(syspaynos.length == 0){
						scwin.gridHolidayAppend();
					}

					for(var i=0; i<dlt_holiday.getRowCount(); i++){
						if(dlt_holiday.getCellData(i, "rest_cd") == "CTD010"){
							// 근무구분이 근무일경우 일요일이거나 휴무이면 활성화				
							grd_holiday.setCellDisabled(i, "holiday_alt_day", !(dlt_holiday.getCellData(i, "day_no") == "0" || dlt_holiday.getCellData(i, "off_rmk") != ""));
						}else{
							grd_holiday.setCellDisabled(i, "holiday_alt_day", true);
						}
					}
				}
			}
		});

	};

	/**
	 * 출장시작일 변경 이벤트
	 */
	scwin.ica_start_dt_onviewchange = function(info) {
		scwin.changeBizTripDay();
	};

	/**
	 * 출장종류일 변경 이벤트
	 */
	scwin.ica_end_dt_onviewchange = function(info) {
		if(info.newValue == ""){
			com.alert(com.getMsg("message.xom.co.com.0003", "출장종료일자"));
			ica_end_dt.setValue(info.oldValue);
			return;
		} else {
			if(info.newValue != "" && info.newValue < "20230401") {
				com.alert("개정된 여비규정에 의거, 상향 조정된 국내출장경비 수령은 4월 1일 출장 건부터 적용입니다.\n 이전 출장에 대한 출장경비는 이전 기준으로 수령하여 주시기 바랍니다.");
			}
		}
		scwin.changeBizTripDay(this.getValue());
	};

	/**
	 * 출장일자 변경 이벤트
	 */
	scwin.changeBizTripDay = function(ymd){
		if(dma_trip.get("biztrip_start_ymd") == "" || dma_trip.get("biztrip_cls_ymd") == ""){
			return;
		}		

		// 숙박비 없음 초기화
		cbx_lodg_fee_chk.setValue("");	

		// 박일 계산
		var day = com.diffDate(dma_trip.get("biztrip_start_ymd"), dma_trip.get("biztrip_cls_ymd"));
		dma_trip.set("biztrip_nght", day);
		dma_trip.set("biztrip_day", com.parseInt(day)+1);		

		// 비상근직원의 출장기간 유효성 검증
		// 2022.10.26. 알람창을 띄우고 1박이상 출장신청은 가능하도록 수정
		if(!scwin.checkPeriodPartTimer(ymd)) return;

		// 활동내역 일정변경		
		if(dlt_schd.getRowCount() == 0){
			var data = {
				start_ymd : dma_trip.get("biztrip_start_ymd"),
				cls_ymd : dma_trip.get("biztrip_cls_ymd")
			};

			dlt_schd.setRowJSON(0, data, false);
		}else{
			for(var i=0; i<dlt_schd.getRowCount(); i++){
				dlt_schd.setCellData(i, "start_ymd", dma_trip.get("biztrip_start_ymd"));
				dlt_schd.setCellData(i, "cls_ymd", dma_trip.get("biztrip_cls_ymd"));
			}
		}

		if(com.parseInt(dma_etcFee.get("lodg_fee")) == 0){
			// 기타경비조회
			scwin.getEtcFee();
		}else{
			// 기타경비 계산
			scwin.setTripExpns();				
		}		

		// 휴일 내역 조회
		scwin.selectHoliday();
	};	

	/**
	 * 휴일 그리드 인원별로 추가 로우 생성
	 */
	scwin.gridHolidayAppend = function(){
		var rowCnt = dlt_holiday.getRowCount();
		// 출장자 휴무/휴일 처리
	 	for(var i =0; i<rowCnt; i++){
		 	dlt_holiday.setCellData(i, "biztrip_syspayno", dma_biztripPsn.get("biztrip_psn_syspayno"));
		 	dlt_holiday.setCellData(i, "biztrip_empno", dma_biztripPsn.get("biztrip_psn_empno"));
		 	dlt_holiday.setCellData(i, "biztrip_nm", dma_biztripPsn.get("biztrip_psn_nm"));

		 	if(dlt_holiday.getCellData(i, "day_no") == '0' || dlt_holiday.getCellData(i, "off_rmk") != ""){ // 일요일 이거나 휴일비고가 있으면
			 	grd_holiday.setCellDisabled(i, "holiday_alt_day", !(dlt_holiday.getCellData(i,"rest_cd") == "CTD010")); // CTD010 업무
			} else {
				grd_holiday.setCellDisabled(i, "holiday_alt_day", true);
			}
		}

		// 동반자 휴무/휴일 처리
		var row = rowCnt;
		for(var i=0; i<dlt_compn.getRowCount(); i++){
			if(dlt_compn.getCellData(i, "biztrip_psn_syspayno") == "") continue;

			for(var j=0; j<	rowCnt; j++){				
				var rowData = dlt_holiday.getRowJSON(j);
				rowData.biztrip_syspayno = dlt_compn.getCellData(i, "biztrip_psn_syspayno");
				rowData.biztrip_empno = dlt_compn.getCellData(i, "biztrip_psn_empno");
				rowData.biztrip_nm = dlt_compn.getCellData(i, "biztrip_psn_nm");

				dlt_holiday.setRowJSON(row, rowData, false); // false insert, true 덮어쓰기
				if(dlt_holiday.getCellData(row, "day_no") == '0' || dlt_holiday.getCellData(row, "off_rmk") != ""){ // 일요일 이거나 휴일비고가 있으면
				 	grd_holiday.setCellDisabled(row, "holiday_alt_day", !(dlt_holiday.getCellData(row,"rest_cd") == "CTD010")); // CTD010 업무
				} else {
					grd_holiday.setCellDisabled(row, "holiday_alt_day", true);
				}

				row++;
			}
		}
	};

	/**
	 * 휴일내역 변경 이벤트
	 */
	scwin.grd_holiday_onviewchange = function(info) {
		var row = info.rowIndex;
		var hol_biztrip_day = dlt_holiday.getCellData(row, "biztrip_day");
		if(info.colIndex == grd_holiday.getColumnIndex("rest_cd")){
			if(info.newValue == "CTD010"){
				// 근무구분이 근무일경우 일요일이거나 휴무이면 활성화				
				grd_holiday.setCellDisabled(row, "holiday_alt_day", !(dlt_holiday.getCellData(row, "day_no") == "0" || dlt_holiday.getCellData(row, "off_rmk") != ""));
			}else{
				// 근무구분이 근무가 아니면 대체휴일 제거
				dlt_holiday.setCellData(row, "holiday_alt_day", "");
				grd_holiday.setCellDisabled(row, "holiday_alt_day", true);
			}
		}else if(info.colIndex == grd_holiday.getColumnIndex("holiday_alt_day")){
			debugger;
			var workDays = dlt_ymd.getColData("ymd");
			if(!workDays.some(function(day) {
				return (day == info.newValue);
			})){
				com.alert("message.xom.ui.ccs.008"); // 출장종료일 이후 7일 이내로 선택하세요.
				dlt_holiday.setCellData(row, "holiday_alt_day", "");
			} else {
				var holDay = info.newValue;
//				var today = scwin.data.today;
				// 하기 if문 조건 today(오늘날짜)에서 hol_biztrip_day(휴일 출장 대상일)로 변경
				if(holDay.substr(0,6) != hol_biztrip_day.substr(0,6)) {
					com.alert("message.xom.ui.ccs.030"); // 대체휴일은 발생월 내에서만 지정이 가능합니다. \n추가 사항은 총무보안실에 문의 바랍니다.
					dlt_holiday.setCellData(row, "holiday_alt_day", "");
				}
			}			
		}
	};

	/**
	 * 운임 및 기타경비 재조회
	 */
	scwin.getFareFee = function(){		
		dlt_path.setCellData(0, "posi_clsf", dma_biztripPsn.get("posi_clsf"));

		com.execute(sbm_selectFareFee, {
			successCallback : function(e){	
				dlt_path.setJSON(e.responseJSON.ctrDomPathVoList);
				// 출장경비: 운임 비고 생성
				scwin.makeFareFeeRmk();

				// 기타경비 계산
				scwin.getEtcFee();
			}
		});
	}

	/**
	 * 경로변경 이벤트
	 */	
	scwin.grd_path_oncellclick = function(row,col,colId) {
		if(colId == "chk"){
			grd_path.setCellChecked(row , colId, true); // 체크되는 시점이 이벤트 발생 시점 뒤여서 미리 체크
			var chkArr = grd_path.getCheckedIndex(colId);
			var rCnt = grd_path.getRowCount();

			if (chkArr.length == 1) {
				var chkIdx = chkArr[0];	// 1개만 체크되는 radio특성상 0번째 인덱스만 사용
				if(chkIdx == 0 || chkIdx == (rCnt-1)) {
					com.alert("message.xom.ui.ccs.027"); // 경로 삭제는 출발지와 도착지를 제외한 중간 경로만 삭제 가능합니다.
					grd_path.setCellChecked(row , colId, false);
				} 
			} else if(chkArr.length > 1){
				com.alert("출장경로 [선택]란이 중복 체크되었습니다.");
				for(var i=0; i<rCnt; i++){
					grd_path.setCellChecked(i , colId, false);
				}
			} 
		}
	};

	/**
	 * 경로변경 이벤트
	 */		

	scwin.grd_path_onviewchange = function(info) {
		var row = info.rowIndex;
		var colId = grd_path.getColumnID(info.colIndex);
		var rowCnt = dlt_path.getRowCount();

		// 출장일자 체크
		if(!com.validateData(dma_trip
				, [{ id : "biztrip_start_ymd", mandatory : true},{ id : "biztrip_cls_ymd", mandatory : true}]
				, false
			)){
			dlt_path.setCellData(row, colId, "");
			return;
		};			

		if(colId == "start_region"){
			if(dlt_path.getRowCount() > 1){
				dlt_path.setCellData(rowCnt-1, "arriv_region", info.newValue);
			}

		}else if(colId == "arriv_region"){
			if(row == 0 && info.newValue == dlt_path.getCellData(row, "start_region")){
				var msg = com.getMsg("message.xom.ui.ccs.008") + "\n"
				        + com.getMsg("message.xom.ui.ccs.009") + "\n"
				        + com.getMsg("message.xom.ui.ccs.010") + "\n"
				        + com.getMsg("message.xom.ui.ccs.011") + "\n"
				        + com.getMsg("message.xom.ui.ccs.012") + "\n"
				        + com.getMsg("message.xom.ui.ccs.013") + "\n"
				        + com.getMsg("message.xom.ui.ccs.014") + "\n"
				        + com.getMsg("message.xom.ui.ccs.015");
				com.alert(msg);
			}else{
				if(row +1 == rowCnt){
					var data ={
						start_region : info.newValue,
						arriv_region : dlt_path.getCellData(0, "start_region")	
					};				
					dlt_path.setRowJSON(rowCnt, data, false);				
				}else{
					dlt_path.setCellData(row+1, "start_region", info.newValue);
				}	
			}				
		}		

		// 출발지, 도착지, 직급구분이 변경되었을 경우 금액 재계산
		if(/start_region|arriv_region/.test(colId)){
			var val = (colId == "start_region")? dlt_path.getCellData(row, "arriv_region") : dlt_path.getCellData(row, "start_region");

			// 제주 지역 내의 출장이 아니고 출발지 또는 도착지에 제주가 포함되었다면 출장경로 항공으로 변경			
			sbx_biztrip_way.setValue((info.newValue != val && info.newValue == "FAU059")?"exprss" : "train");

			if(dlt_path.getCellData(row, "start_region") != dlt_path.getCellData(row, "arriv_region")){
				if(info.oldValue != info.newValue && val != ""){
					scwin.getFareFee();
				}
			}else{
				dlt_path.setCellData(row, "rmk", "0(운임없음)");
				dlt_path.setCellData(row, "biztrip_fee", 0);
			}
		}

		// 출장경로 삭제 버튼 컨트롤
		scwin.gridPathView();	
	};

	/**
	 * // 출장경로 삭제 버튼 컨트롤
	 */	
	scwin.gridPathView = function(){
		if(dlt_path.getRowCount() > 2){
			btn_path_del.show("");
		}else{
			if(dlt_path.getRowCount() == 2){
				if(dlt_path.getCellData(0, "start_region") == dlt_path.getCellData(0, "arriv_region")){
					dlt_path.removeRow(1);
				}
			}
			btn_path_del.hide();
		}

		// 출장경로 첫행, 3단계 이상 경로인 경우 마지막 행 삭제 체크 불가 처리
		// 출장경로 마지막 행의 출발지는 수정 불가
		var rowCnt = dlt_path.getRowCount();
		if(rowCnt > 1){
			grd_path.setCellReadOnly(rowCnt-1, "start_region", true);	
		}
	};

	/**
	 * 기타경비 조회
	 */	
	scwin.getEtcFee = function(){
		dma_search.set("posi_clsf", dma_biztripPsn.get("posi_clsf"));
		dma_search.set("start_region", dlt_path.getCellData(dlt_path.getRowCount()-1, "start_region"));

		com.execute(sbm_selectEtcFee, {
			successCallback : function(e){
				// 기타경비 셋팅
				scwin.setTripExpns();
			}
		});
	};

	/**
	 * 출장경비
	 */
	scwin.setTripExpns = function(){
		var psnCnt = dlt_compn.getRowCount() + 1;
		var day = com.parseInt(dma_trip.get("biztrip_day"));
		var ngt = com.parseInt(dma_trip.get("biztrip_nght"));		

		// 출장일자가 없으면 리턴
		if(day == 0) return;

		var ymdFee = Number(dma_etcFee.get("ymd_fee"));
		var lodgFee = Number(dma_etcFee.get("lodg_fee"));
		var mealFee = Number(dma_etcFee.get("meal_fee"));

		// 출장비 없음 체크가 아니라면(출장비 있음)
		if(cbx_no_expns.getValue() != "Y"){
			// 1.일비처리
			dma_trip.set("ymd_fee", ymdFee * psnCnt * day);
			dma_trip.set("ymd_fee_rmk", "= " + com.formatMoney(ymdFee) + "(일비) X " + day + "(일) X " + psnCnt + "(명)");

			// 2. 숙박비 처리: 숙박비 없음 체크가 아니라면(숙박비 있음)
			if(cbx_lodg_fee_chk.getValue() != "Y"){
				dma_trip.set("lodg_fee", (ngt > 0)?(lodgFee * psnCnt * ngt) : 0); // 1박 이상이라면
				dma_trip.set("lodg_fee_rmk", "※ 숙박비 정산 시 실비지급 (상한금액: 서울특별시 100,000원 / 광역시 80,000원 / 그 밖의 지역 70,000원)");
			}

			// 3. 식비 처리: 카드식 처리
			var cardCnt = com.parseInt(dma_trip.get("card_meal"));
			var mealFeeRmk = ("= "+ com.formatMoney(mealFee) +"(식비) X "+ day +"(일) X "+ psnCnt +"명");
			if(cardCnt > 0){
				var exMeal = Math.floor(mealFee * psnCnt * cardCnt / (3*10)) * 10;
				var mea_fee = mealFee * psnCnt * day - exMeal;
				dma_trip.set("meal_fee", (mea_fee < 0)? 0 : mea_fee);
				dma_trip.set("meal_fee_rmk", mealFeeRmk +" - ("+ com.formatMoney(mealFee) +"/3) X "+ cardCnt +"식  X " + psnCnt + "명");

			}else{
				dma_trip.set("meal_fee", Math.floor((mealFee * psnCnt * day) / 10) * 10);
				dma_trip.set("meal_fee_rmk", mealFeeRmk);
			}
		}

		// 총액 재계산 및 계정내역 금액 계산
		scwin.setAccntTotExpns();			
	}

	/**
	 * 총액 재계산 및 계정내역 금액 계산
	 */
	scwin.setAccntTotExpns = function(){
		if(!com.validateData(dma_trip
				, [{ id : "biztrip_start_ymd", mandatory : true},{ id : "biztrip_cls_ymd", mandatory : true}]
				, false
			)){
			return;
		};	

		// 운임 + 일비 + 숙박비 + 식비 + 기타
		var totSum = Number(dma_trip.get("fare_fee")) + Number(dma_trip.get("ymd_fee")) + Number(dma_trip.get("lodg_fee"))
					 + Number(dma_trip.get("meal_fee"));
		dma_trip.set("tot_expns", totSum);
		if(totSum > 0 && dma_trip.get("cu_tot_expns") != totSum){
			spnChgExpnsDesc.show("");
		}else{
			spnChgExpnsDesc.hide();
		}

		var rowCnt = dlt_accnt.getRowCount();
		if(rowCnt > 0){
			var totExpns = Math.round(totSum / rowCnt);
			var totExpnsSum = 0;
			for(var i=0; i < rowCnt; i++){
				if((i+1) == rowCnt){
					totExpns = totSum - totExpnsSum;
				}else{
					totExpnsSum += totExpns;
				}
				dlt_accnt.setCellData(i, "tot_expns", totExpns);
			}
		}else{
			var data = {tot_expns : totSum,
			            cud_type : "C"
			};
			dlt_accnt.setRowJSON(0, data, false);
		}


	};

	/**
	 * 박일 변경 이벤트
	 */
	scwin.ibx_biztrip_nght_onviewchange = function(info) {
		if(ibx_biztrip_nght.getValue().trim() == ''){
			ibx_biztrip_nght.setValue(0);
		}
		if(dma_trip.get("biztrip_start_ymd") != "" && dma_trip.get("biztrip_cls_ymd") != ""){
			var day = com.diffDate(dma_trip.get("biztrip_start_ymd"), dma_trip.get("biztrip_cls_ymd"));
			if(com.parseInt(info.newValue) > com.parseInt(day)){
				com.alert(com.getMsg("message.xom.co.com.0012", "출장일자"));
				ibx_biztrip_nght.setValue(day);
				return;
			}
		}
		scwin.setTripExpns();
	};


