	};

	/**
	 * 카드식 변경 이벤트
	 */
	scwin.spi_card_meal_onviewchange = function(info) {
		var mealCnt = com.parseInt(dma_trip.get("biztrip_day")) * 3;
		if(info.newValue > mealCnt){
			spi_card_meal.setValue(info.oldValue);
			return;
		}
		scwin.setTripExpns();
	};

	/**
	 * 운임,일비
	 */
	scwin.changeFee = function(Obj, oldVal, newVal){
		if(newVal == ""){
			Obj.setValue(0);
		}else{
			if(/ibx_fare_fee|ibx_ymd_fee/.test(Obj.id)){
				if(sbx_car_use.getValue() != "FDO001" && sbx_goal_clsf.getValue() != "FBV150"){ // FBV150 외부강의등
					com.alert("message.xom.ui.ccs.018");
					ibx_fare_fee.setValue(oldVal);
					return;
				}
			}
			scwin.setAccntTotExpns();			
		}
	}

