	/**
	 * 삭제 버튼 이벤트
	 */
	scwin.btn_del_onclick = function(e) {
		var allowAprStateCodes = ["", "XAD005", "XAD010", "XAD013", "XAD015"];
		if(!allowAprStateCodes.some( function(allowAprStateCode, index){
			return (allowAprStateCode == scwin.data.aprState);
		})) return;

		dma_search.set("del_flag", "Y"); // 삭제 전 출장정산서가 작성되었는지 체크

		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function(ret) {
			if (ret) {
				com.file.deleteAll( function(){					
					com.execute(sbm_deleteDomTrip, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								com.moveList("C306",{});							
							});
						}
					});
				});
			}
		});
	};

	/**
	 * 인쇄 버튼 이벤트
	 */
	scwin.btn_print_onclick = function(e) {
		if(dma_trip.get("req_no") == ""){
			com.alert(com.getMsg("message.xom.co.com.0007", "신청번호"));
			return
		}

		var evidItemCd = "F037";
		if(dma_trip.get("req_no").substring(0,3) != "C86"){
			evidItemCd = "C001";
		}
		var node = "req_no="+ dma_trip.get("req_no");
		com.oz.openPopup({
			ozrCode : evidItemCd,      
			param: node
        });
	};

