			// 직급이 변경되면  기타경비 재조회
			scwin.getEtcFee();
		}
	};

	/**
	 * 담당자저장 버튼 클릭 이벤트
	 */
	scwin.btn_mngSave_onclick = function(e) {
		if(com.file.action.getTotalFileCount(upload_A.getID()) < 1){
			alert("첨부파일을 업로드하시기 바랍니다.");
			return;
		}

		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				com.file.transport(function(attachObj) {
					dma_trip.set("attach_file_no", attachObj.upload_A);
					dma_trip.set("validAttachNo", attachObj.upload_A);

					com.execute(sbm_savePostApr, {
						successCallback : function(e){
							alert("변경내역을 저장하였습니다.");
							scwin.search();
						}
					});
				});
			}
		});
	};
