head.script:
    /****************************************************
	 * 화면 설명 : 대외활동신고서
	 * 변경 이력 : 2022-05-20	박영훈	최초 작성
	 *          2023-11-23	이영희	겸직,타업종사,기타활동 추가
	 * serviceId : C680
	 ****************************************************/     
	scwin.openPopFlag = false;
	scwin.today = com.getServerDateTime();
	scwin.sn = com.getLoginInfo("sn");

	/**
	 * 페이지 로딩 이벤트
	 */
	scwin.onpageload = function() {
		// 초기화
		com.file.init(); // 첨부파일 초기화
		com.file.create(upload_A.getID(), {fileKey: 'C680', mode: 'edit'});

		spn_msg.hide();

		grp_reject.hide(); // 반려사유
		btn_print.setDisabled(true);

		// 기간 역전 체크
		com.checkPeriod(ica_st_daytm, ica_ed_daytm);

		dma_search.setJSON(com.getParameter());

		var codeOptions = [ { code : "CCR", compID :"sbx_report_cd"}, // 신고구분
							{ code : "CTR", compID :"sbx_trip_cd"}, // 출장연계
							{ code : "CLA", compID :"sbx_act_clsf"}, // 대외활동구분
							{ code : "CCB", compID :"rdo_activity_cd_001"}, // 활동유형(겸직)
							{ code : "CCE", compID :"rdo_activity_cd_002"}, // 활동유형(타업종사)
							{ code : "CCA", compID :"rdo_activity_cd_003"}, // 활동유형(외부강의)
							{ code : "CCF", compID :"rdo_activity_cd_004"}, // 활동유형(기타활동)
							{ code : "CLB", compID :"rdo_act_shape"}, // 활동형태		
							{ code : "HCN", compID :"sbx_attnce_clsf,grd_accr:attnce_clsf"}, // 참여정도	
							{ code : "HCQ", compID :"sbx_econo_gain_clsf,grd_accr:econo_gain_clsf"}, // 보상구분
							{ code : "CCH", compID :"rad_companion_cd"}, // 반려사유
							];
		com.setCmnCd(codeOptions, function() {	
			if(dma_search.get("req_no") != ""){
				scwin.search();
			}else{
				dma_search.set("yyyymm", scwin.today.substring(0,6));
				dma_search.set("syspayno", scwin.sn);

				biz.ccs.pop.open(biz.ccs.pop.TYPE.CVN_NOTI);
			}
		});
	};

	scwin.onpageunload = function() {

	};

	/**
	 * 활동구분 및 활동유형에 따른 누적데이터 설정
	 */
	scwin.setFormGrpAccr = function(){
		// 활동구분에 따른 셋팅
		if(dma_cvn.get("activity_cd") == ""){
			scwin.setFormByActClsf(2);
		}

		// 활동유형
		var actClsf = sbx_act_clsf.getValue();
		dma_search.set("act_clsf", actClsf);

		var comps = ["grp_activity_cd_001", "grp_activity_cd_002", "grp_activity_cd_003", "grp_activity_cd_004"];
		comps.forEach(function(elt) {
			if(actClsf.substring(3, 6)== elt.substring(16, 19)){
				com.getComponent(elt).show("");	
				//com.getComponent(elt).setDisabled(false);
			}else{
				com.getComponent(elt).hide();
				//com.getComponent(elt).setDisabled(true);
			}
		});

		// 참여정도
		if(actClsf == "CLA003"){
			sbx_attnce_clsf.setDisabled(true);	
			sbx_econo_gain_clsf.setDisabled(true);
			sbx_econo_gain_clsf.setValue("HCQ030");
			spn_period_time.setValue("분");
			ica_st_daytm.setCalendarValueType("yearMonthDateTime");
			ica_ed_daytm.setCalendarValueType("yearMonthDateTime");
		}else{
			sbx_attnce_clsf.setDisabled(false);	
			sbx_econo_gain_clsf.setDisabled(false);
			sbx_econo_gain_clsf.setValue("");
			spn_period_time.setValue("시간");
			ica_st_daytm.setCalendarValueType("yearMonthDate");
			ica_ed_daytm.setCalendarValueType("yearMonthDate");
		}

		if(/CLA001|CLA002/.test(actClsf)){ // 겸직,타업종사
			sbx_trip_cd.setValue("CTR040");
			grp_trip1.hide();
			grp_trip2.hide();
			grp_trip_amt.hide();

			sbx_trip_cd.setDisabled(true);
		}else{
			sbx_trip_cd.setDisabled(false);
			grp_trip1.show("");
			grp_trip2.show("");
			grp_trip_amt.show("");
		}

		if(/CLA001|CLA002/.test(actClsf) || /CCF005|CCF006/.test(dma_cvn.get("activity_cd"))){
			grp_acc.hide();
		}else{	
			grp_acc.show("");		
			if(/CCF001|CCF002|CCF003/.test(dma_cvn.get("activity_cd"))){ // 기타활동(서면,심사,자문,원고료,집필료,자문활동)
				grd_accr.show("");
				grp_accMon.hide();
			}else{
				grd_accr.hide();
				grp_accMon.show("");
			}
		}
	};

	/**
	 * 활동구분에 따른 셋팅
	 */
	scwin.setFormByActClsf = function(flag){
		if(flag == 1){
			if(dma_cvn.get("act_clsf") == "CLA001"){
				rdo_activity_cd_001.setValue(dma_cvn.get("activity_cd"));
			}else if(dma_cvn.get("act_clsf") == "CLA002"){
				rdo_activity_cd_002.setValue(dma_cvn.get("activity_cd"));
			}else if(dma_cvn.get("act_clsf") == "CLA003"){
				rdo_activity_cd_003.setValue(dma_cvn.get("activity_cd"));
			}else if(dma_cvn.get("act_clsf") == "CLA004"){
				rdo_activity_cd_004.setValue(dma_cvn.get("activity_cd"));
			}
		}else{
			if(dma_cvn.get("act_clsf") == "CLA001"){
				dma_cvn.set("activity_cd", rdo_activity_cd_001.getValue());
			}else if(dma_cvn.get("act_clsf") == "CLA002"){
				dma_cvn.set("activity_cd", rdo_activity_cd_002.getValue());
			}else if(dma_cvn.get("act_clsf") == "CLA003"){
				dma_cvn.set("activity_cd", rdo_activity_cd_003.getValue());
			}else if(dma_cvn.get("act_clsf") == "CLA004"){
				dma_cvn.set("activity_cd", rdo_activity_cd_004.getValue());
			}
		}
	};


	/**
	 * 조회
	 */
	scwin.search = function(){
		com.execute(sbm_ctrTripDetail, {
			successCallback : function(e) {
				// 누적 월간횟수 계산위해 필요
				dma_accMon.set("acc_mon_count", dma_cvn.get("acc_mon_count"));
				dma_accMon.set("acc_mon_hour", dma_cvn.get("acc_mon_hour"));

				if (!com.isEmpty(dma_cvn.get("req_no"))) {					
					// 버튼 컨트롤
					com.biz.disabledButtons(dma_cvn.get("apr_state"), dma_cvn.get("req_no"));
					btn_print.setDisabled(false);

					// 누적데이터 설정
					scwin.setFormGrpAccr();

					if(/XAD005|XAD010|XAD013|XAD015/.test(dma_cvn.get("apr_state")) && dma_search.get("req_no") == ""){
						alert("※ [저장] 상태의 기존 신고서가 존재하여 불러오기 하였습니다.");	
						spn_msg.show("");		
						spn_msg.setValue("※ ["+ dma_cvn.get("apr_state_nm") +"] 상태의 기존 신고서가 존재하여 불러오기 하였습니다.")
					}

					if(dma_cvn.get("apr_state") == "XAD015"){
						grp_reject.show("");
					}else{
						grp_reject.hide();
					}

					// 활동구분에 따른 셋팅
					scwin.setFormByActClsf(1);

					if(dma_cvn.get("apr_state") >= "XAD020"){
						// 출장담당자일 경우 첨부파일 업로드 가능하게 기능 추가
						// 1006206 출장담당자, 1006205 총무담당자, 1006273 외부강의담당자
						if (/1006206|1006205|1006273/.test(com.getLoginInfo('roleCode'))) {
							req_tel_no_input.setDisabled(true);
							sbx_trip_cd.setDisabled(true);
							gr1.setDisabled(true);
							gr2.setDisabled(true);
							gr3.setDisabled(true);

							btn_save.setDisabled(false);
						}
					}
					var attachFileNo = dma_cvn.get("attach_file_no");
					if(attachFileNo != ""){
						com.file.create(upload_A.getID(), {fileKey: attachFileNo, mode: 'edit'});
					}


					// 출장정보
					if(dma_cvn.get("trip_cd") != "" && dma_cvn.get("trip_cd") != "CTR040"){
						grp_trip1.show("");
						grp_trip2.show("");		
					}else{
						grp_trip1.hide();
						grp_trip2.hide();
					}

					// 실비 및 총액 구하기
					scwin.getTotalAmt();
				}
			}			
		});

	};

	/**
	 * 총액 및 실비 구하기
	 */
	scwin.getTotalAmt = function() {
		var actualAmt = Number(dma_cvn.get("traffic_amt")) + Number(dma_cvn.get("stay_amt")) + Number(dma_cvn.get("food_amt"));
		ibx_actual_amt.setValue(actualAmt);
		ibx_total_amt.setValue(actualAmt + Number(dma_cvn.get("reward_amt")));
	};	

	/**
	 * 일시 변경시 활동시간 구하기(외부강의등)
	 */
	scwin.changeCalendar = function(obj){
		if(sbx_act_clsf.getValue() != "CLA003") return;

		var sDate = ica_st_daytm.getFormattedValue();
		var eDate = ica_ed_daytm.getFormattedValue();		

		// onload가 아니며 캘린더 모두 값이 있을 때만 실행.
		if (sDate && eDate){
			if (sDate >= eDate) {
				alert("종료일시가 시작일시보다 크거나 같을 수 없습니다.");
				obj.setValue("");
				return;
			}

			if(sDate.substring(0,10) == eDate.substring(0,10)){
				var sDate = new Date(sDate.substring(0,10) + "T" + sDate.substring(11));
				var eDate = new Date(eDate.substring(0,10) + "T" + eDate.substring(11));

				var diffMinCal = Math.abs( eDate - sDate) / 60000;	
				var diffMinIpt = ibx_period_time.getValue();
				if (isNaN(diffMinCal) || isNaN(diffMinIpt)) return;

				ibx_period_time.setValue(diffMinCal);
				scwin.getAccData();
			}
		}
	}

	/**
	 * 저장전 체크
	 */
	scwin.isValid = function(){
		// 활동유형 셋팅
		scwin.setFormByActClsf(2);

		var syspayno = dma_cvn.get("syspayno");
		var writeSyspayno = dma_cvn.get("write_syspayno");

		if ( !(/1006206|1006205|1006273/.test(com.getLoginInfo('roleCode'))) && scwin.sn != syspayno && scwin.sn != writeSyspayno ){
			alert("작성자 또는 신고자가 아니면 수정할 수 없습니다.");
			return false;
		}

		var validGroup = [
				{ id : "act_clsf", mandatory : true},
				{ id : "activity_cd", mandatory : true},
				{ id : "trip_cd", mandatory : true},
				{ id : "req_tel_no", mandatory : true}, // 신고자 연락처
				{ id : "invite_cause", mandatory : true}, // 활동내용
				{ id : "act_shape", mandatory : true}, // 활동형태
				{ id : "invite_org_nm", mandatory : true},				
				{ id : "invite_ceo_nm", mandatory : true},
				{ id : "invite_dept_nm", mandatory : true},
				{ id : "invite_tel_no", mandatory : true},				
				{ id : "subject", mandatory : true},
				{ id : "region", mandatory : true},		
				{ id : "st_daytm", mandatory : true},
				{ id : "ed_daytm", mandatory : true},	
				{ id : "period_minute", mandatory : true},
				{ id : "rmk", mandatory : true}, // 기타조건
			];

		if(sbx_act_clsf.getValue() != "CLA003"){
			validGroup.push({ id : "attnce_clsf", mandatory : true});
		}

		if(rdo_cost_yn.setValue() == ""){
			validGroup.push({ id : "reward_amt", mandatory : true});
		}

		if(!com.validateData(dma_cvn, validGroup, false)) return false;		

		if (/CTR010|CTR020|CTR030/.test(dma_cvn.get("trip_cd")) && "" == trip_req_no.getValue().trim()){
			alert("출장연계가 있는 경우 출장신청정보는 필수 입력 사항입니다.");
			sbx_trip_cd.focus();
			return false;
		}
		if (ibx_period_time.getValue() > 32767){
			alert("외부강의 시간이 허용값을 초과합니다. \n강의일시 입력내용을 확인하십시오.");
			ibx_period_time.focus();
			return false;
		} 

		return true;
	};

	/**
	 * 저장
	 */
	scwin.preSave = function(){
		if(dma_cvn.get("apr_state") == "XAD015"){
			if(dma_cvn.get("companion_rs") == "REC010"){
				dma_cvn.set("delay_yn", "N");
				scwin.save();
			}else{
				var keys = dma_cvn.getModifiedKey();	
				var keyNames = [];
				keys.forEach(function(value, key, array) {
					if(!/acc_mon_count|acc_mon_hour|approval|validAttachNo/.test(value)){
						keyNames.push(dma_cvn.getName(value));
					}
				});	

				if(keyNames.length > 0){
					var data = {
						modify_item : keyNames.join(",")
					};

					biz.ccs.pop.open(biz.ccs.pop.TYPE.CVN_UPT_CAUSE, data, function(retObj) {
						if(retObj){
							dma_cvn.set("modify_item", retObj.modify_item);
							dma_cvn.set("modify_cause", retObj.modify_cause);

							scwin.save();
						}
					});
				}else{
					scwin.save();
				}
			}
		}else{
			scwin.save();
		}
	};

	/**
	 * 저장 실행
	 */
	scwin.save = function(){
		// 월 3회 또는 6시간을 초과하는 경우 허가제(CCR020) 그 외에는 신고제(CCR010)
		if(/CLA003/.test(sbx_act_clsf.getValue())){
			dma_cvn.set("report_cd", (dma_cvn.get("acc_mon_count") > 3)? "CCR020" : "CCR010"); 
		}

		dma_cvn.set("yyyymm", dma_cvn.get("st_daytm").substring(0, 6));

		// dutyCode HAB018 이거나 HAB013 일때 report_cd = CCR030
		// 2023.11.27. CCR030 사용하지 않음
		if(/HAB018|HAB013/.test(com.getLoginInfo('dutyCode'))) {
			dma_cvn.set("report_cd", "CCR030");  //허가제 여부 : Y 전결관련 변수는 CCR010과 같게 (rule_money, rule_type)
		}

		com.file.transport( function( attachObj){
			dma_cvn.set("attach_file_no", attachObj.upload_A);
			dma_cvn.set("validAttachNo", attachObj.upload_A);
			com.execute(sbm_saveCtrTrip, {
				successCallback : function(e) {
					if(scwin.isApproval){
						// 결재화면 이동
						com.movePageMenu("A001", { "reqNo" :  dma_cvn.get("req_no")});
					}else{
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function(){
							// 조회
							scwin.search();
						});
					}
				}
			});
		});		
	};

	/**
	 * 월간누적횟수 및 시간 구하기
	 */
	scwin.getAccData = function(){	
		var startYm = ica_st_daytm.getValue().substring(0,6);

		if(sbx_act_clsf == "CLA003" || dma_cvn.get("activity_cd") == "CCF004"){
			if(dma_search.get("yyyymm") != startYm){

				dma_search.set("req_no", dma_cvn.get("req_no"));
				dma_search.set("yyyymm", startYm);
				dma_search.set("act_clsf", dma_cvn.get("act_clsf"));
				dma_search.set("activity_cd", dma_cvn.get("activity_cd"));

				com.execute(sbm_accYearMonth, {			
					successCallback : function(e) {
						scwin.getAccMonCount();		

						btn_month.trigger("click");
					}
				});
			}else{
				scwin.getAccMonCount();	
			}
		}
	}	

	/**
	 * 월간누적횟수 및 시간 계산
	 */
	scwin.getAccMonCount = function(){
		var acc_mon_hour = Number(dma_accMon.get("acc_mon_hour"));
		var acc_mon_count = WebSquare.util.parseInt(dma_accMon.get("acc_mon_count"), 0);
		if(dma_search.get("req_no") == ""){
			acc_mon_count +1;
		}

		var diffMin = Number(dma_cvn.get("period_minute"));
		//dma_cvn.set("acc_mon_hour", (diffMin/60 + acc_mon_hour).toFixed(2));
		dma_cvn.set("acc_mon_hour", acc_mon_hour.toFixed(2));
		dma_cvn.set("acc_mon_count", acc_mon_count);
	};

	/**
	 * 월간통계 팝업 호출
	 */
	scwin.openMonthPop = function() {	

		var data = {
			syspayno : (dma_cvn.get("req_no") !="")? dma_cvn.get("syspayno") : scwin.sn,
			yyyymm : (dma_cvn.get("req_no") !="")? dma_cvn.get("st_daytm").substring(0,6) : scwin.today.substring(0,6),
			nm : dma_cvn.get("req_psn_nm"),
			act_clsf : sbx_act_clsf.getValue(),
			activity_cd : dma_cvn.get("activity_cd"),
			act_clsf_nm : sbx_act_clsf.getText()
		}

		biz.ccs.pop.open(biz.ccs.pop.TYPE.CVN_RPT, data, function(retObj) {
			scwin.openPopFlag = true;
		});
	}

