	};

	/**
	 * 기관면 검색버튼 클릭 이벤트
	 */
	scwin.btn_vend_onclick = function(e) {		
		com.pop.open( com.pop.TYPE.VEND, "", function(ret){
			dma_cvn.set("org_cd", ret.vend_cd);
			dma_cvn.set("invite_org_nm", ret.vend_abbr);
			dma_cvn.set("invite_ceo_nm", ret.reprs_psn_nm);
		});

	};

	/**
	 * 출장연계 변경 이벤트
	 */
	scwin.sbx_trip_cd_onviewchange = function(info) {
		var trip = this.getValue();
		var type = "";

		if( "CTR040" == trip){
			grp_trip1.hide();
			grp_trip2.hide();
		} else {
			grp_trip1.show("");
			grp_trip2.show("");	

			//출장팝업데이터
			if(trip == "CTR010"){
				type = "DomCvnTrip";
			}else if(trip == "CTR020"){
				type = "LocalCvnTrip";
			}else if(trip == "CTR030"){
				type = "OverCvntrip";
			}

			var data = {ref_yn : "Y" , 
			            flag : type,
			            biztrip_psn_empno : dma_cvn.get("empno"),
			            biztrip_psn_syspayno : dma_cvn.get("syspayno"),
			            biztrip_psn_nm : dma_cvn.get("req_psn_nm"),	
			            act_clsf : dma_cvn.get("act_clsf"),	
			            };

			com.pop.open(com.pop.TYPE.TRIP, data, function(ret){
				dma_cvn.set("trip_req_no", ret.req_no);
				dma_cvn.set("trip_days", ret.biztrip_term);
				dma_cvn.set("trip_goal", ret.biztrip_goal);
				dma_cvn.set("trip_region", ret.biztrip_region_nm);
			});
		}
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 */
	scwin.btn_save_onclick = function(e) {
		dma_cvn.set("approval", "false");		
		scwin.isApproval = false;

		if(!scwin.isValid()) return;

		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				scwin.preSave();
			}
		});		
	};

	/**
	 * 삭제버튼 클릭 이벤트
	 */
	scwin.btn_del_onclick = function(e) {
		//var syspayno = dma_cvn.get("syspayno");
		//var writeSyspayno = dma_cvn.get("write_syspayno");
		//var sn = com.getLoginInfo("sn");
