		});
	};

	/**
	 * 월간통계 버튼 클릭 이벤트
	 */
	scwin.btn_month_onclick = function(e) {
		scwin.openMonthPop();
	};

	/**
	 * 결재신청 버튼 클릭 이벤트
	 */
	scwin.btn_apr_onclick = function(e) {
		if(dma_cvn.get("apr_state") == "XAD015"){
			if(rad_companion_cd.getValue() == ""){
				alert("반려 사유를 선택해주세요.");
				return;
			}

			if(rad_companion_cd.getValue() == "REC040"){
				if(ibx_companion_rs.getValue() == ""){
					alert("반려 사유가 기타일 경우 기타사유를 입력해주세요.");
					return;
				}
			}
		}

		if(!scwin.isValid()) return;

		dma_cvn.set("approval", "true");
		scwin.isApproval = true;

		scwin.preSave();
	};

	/**
	 * 외부강의 시작일시 변경 이벤트
	 */
	scwin.ica_st_daytm_onviewchange = function(info) {
		/**
		* 정승준 사무원 요청시에만 주석 해제
		var curYear = scwin.today.substring(0,4);
		if(info.newValue != "" && info.newValue < curYear){
			alert("올해보다 과거의 날짜는 설정하실 수 없습니다.");
			this.setValue("");
			return;
		}
		*/

		scwin.changeCalendar(this);

	};

