	/**
	* 활동구분 변경 이벤트
	*/
	scwin.sbx_act_clsf_onviewchange = function(info) {
		if(info.newValue != ""){
			scwin.setFormGrpAccr();			

			dma_search.set("req_no", "");
			dma_search.set("syspayno", scwin.sn);			

			dma_cvn.setEmptyValue();
			sbx_act_clsf.setValue(dma_search.get("act_clsf"));

			// 실비 및 총액 구하기
			scwin.getTotalAmt();

			scwin.search();
		}
	};

	/**
	* 경제적보상구분 변경 이벤트
	*/
	scwin.sbx_econo_gain_clsf_onviewchange = function(info) {
		if(info.newValue == "HCQ030" && sbx_act_clsf.getValue() != "CLA003" && rdo_activity_cd_004.getValue() != "CCF004"){
			alert("외부강의 또는 기타활동(법령에 근거하지않은 위원회등의 정부지원활동)일 경우에만 [회당]을 선택할 수 있습니다.");
			sbx_econo_gain_clsf.setValue("");
			return;	
		}
	};

	/**
	* 대가여부 변경 이벤트
	*/
	scwin.rdo_cost_yn_onviewchange = function(info) {
		if(info.newValue != ""){
			ibx_reward_amt.setValue(0);
			ibx_reward_amt.setDisabled(true);
		}
