head.script:

    // 사용자의 권한에 따라 UI컨트롤 구현

	scwin.onpageload = function() {
		if (com.getParameter("syspayno") === undefined) {
			ica_basedate.setValue(com.getServerDateTime('yyyyMM'));
			inp_empno.setValue(com.getLoginInfo("empno"));
			scwin.hum_search();
		}
	};

	scwin.onpageunload = function() {

	};


	scwin.btn_usage_onclick = function(e) {
		if ("none" == grp_usage_layer.getStyleValue("display")) {
			grp_usage_layer.setPosition(btn_usage.getPosition("left")-600, btn_usage.getPosition("top")-120);
			grp_usage_layer.show("");
		} else {
			grp_usage_layer.hide("");
		}
	};


	scwin.btn_hum_search_onclick = function(e) {
		scwin.hum_search("img");
	};

	scwin.searchWorkTime = function() {
		com.execute(sbm_selectAllWorkTimes, {
			successCallback : function(e) {
				if(com.isEmpty(e.responseJSON.elData.cwsWorkTimeBasicInfoVo)){
					dma_commutecd.setEmptyValue();
					com.alert("기준일에 해당하는 데이터가 없습니다.");
				}
				var commute_cd = e.responseJSON.elData.cwsWorkTimeBasicInfoVo.commute_cd;
				var commute_rmk = e.responseJSON.elData.cwsWorkTimeBasicInfoVo.commute_rmk;
				console.log(commute_cd);
				if ("HWT010" ===  commute_cd) {
					grp_smry_stdtime.hide();
					grp_smry_flxtime.show("");
					grp_smry_slttime.hide();
					grp_smry_prttime.hide();
					grp_smry_flxtime.setStyle("margin-top", "0px")

					grp_dtl_flxtime.show("");
					grp_dtl_stdtime.hide();
					grp_dtl_slttime.hide();
					grp_dtl_flxtime.setStyle("margin-top", "0px")
				} else if ("HWT040" === commute_cd) {
					grp_smry_stdtime.hide();
					grp_smry_flxtime.hide();
					grp_smry_prttime.hide();
					grp_smry_slttime.show("");
					grp_smry_slttime.setStyle("margin-top", "0px")

					grp_dtl_slttime.show("");
					grp_dtl_flxtime.hide();
					grp_dtl_stdtime.hide();
					grp_dtl_slttime.setStyle("margin-top", "0px")

					commute_rmk = "시간선택제: 근무시간은 통상근로자의 1/2 (주40시간)으로 비례";
				}
				if ("HWT010" === commute_cd || "HWT020" === commute_cd ) {
					var workdayDiff = dma_workdays.get("work_days") - dma_smry_worktime.get("work_days");
					var coreTimeDiff = scwin.diffTime(dma_workdays.get("target_core_time"), dma_smry_worktime.get("core_times"))
					var flxWorkTimeDiff = scwin.diffTime(dma_workdays.get("target_flex_time"), dma_smry_worktime.get("flex_times"))
					var totlaTimeDiff = scwin.diffTime(dma_workdays.get("target_total_time"), dma_smry_worktime.get("total_times"))

					tbx_remain_flx_work_days.setValue(workdayDiff);
					tbx_remain_core_time.setValue(coreTimeDiff);
					tbx_remain_flx_work_time.setValue(flxWorkTimeDiff);
					tbx_remain_total_time.setValue(totlaTimeDiff);

					tbx_remain_core_time.setStyle("color", (-1 < tbx_remain_core_time.getValue().indexOf("-")) ? "blue" : "red");
					tbx_remain_flx_work_time.setStyle("color", (-1 < tbx_remain_flx_work_time.getValue().indexOf("-")) ? "blue" : "red");
					tbx_remain_total_time.setStyle("color", (-1 < tbx_remain_total_time.getValue().indexOf("-")) ? "blue" : "red");
				}
				console.log(commute_rmk);
				if (!com.isEmpty(commute_rmk)) {
					txt_notice.setValue(commute_rmk);
					grp_notice.show("");
				} else {
					grp_notice.hide();
				}
			}
		});
	}

	scwin.diffTime = function(st, ed) {
		st = st.split(":");
		ed = ed.split(":");
		var sDate = new Date(0, 0, 0, st[0], st[1], 0);
		var eDate = new Date(0, 0, 0, ed[0], ed[1], 0);
		// 마이너스 시간 계산을 위해 절대값 처리
		var diff = Math.abs(eDate.getTime() - sDate.getTime());
		var hh = Math.floor(diff / (1000 * 60 * 60));
		var mm = Math.floor((diff - hh * 1000 * 60 * 60) / (1000 * 60));
		// 24 시간제 처리
		if (0 > hh) hh += 24;
		// 마이너스 부호 처리
		return (0 > eDate.getTime() - sDate.getTime() ? "-" : "+") + (10 > hh ? "0" : "") + hh + ":" + (10 > mm ? "0" : "") + mm;
	};

	scwin.hum_search = function(data, callBack) {
			// 버튼(img)으로 팝업함수 바로 호출 처음 변수에 아무것도 넣지 않으면 최초 서브미션도 실행 안함. 
		data == "img" ? data = { empno : ""} : data = { empno : inp_empno.getValue(), nm : inp_empno.getValue(), work_clsf : "HAG010" };

		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			dma_search.set("syspayno", ret.syspayno);
			var strEmp = ret.nm + " / " + ret.posi_nm + " / " + ret.up_dept_nm + " " + ret.dept_nm;
			tbx_empinfo.setValue(strEmp);
		});
	}
	scwin.inp_empno_onkeypress = function(e) {
		if(event.keyCode == 13){
			scwin.hum_search();
		}
	};

	scwin.grp_body_onmouseup = function(e) {
		var epath = event.composedPath();
		var hasUsage = epath.some(function(epat) {
	    		var compid = epat.id + "";
	   	 		return (compid.includes("grp_usage_layer") || compid.includes("btn_usage"));
			});

		if (!hasUsage) {
			grp_usage_layer.hide("");
		}
	};

	scwin.btn_aggregate_onmouseover = function(e) {
		if (grp_report_guide.getStyleValue("display") === "none") {
			grp_report_guide.setPosition(btn_aggregate.getPosition("left"), btn_aggregate.getPosition("top") - 150);
			grp_report_guide.show("");
		}
	};

	scwin.btn_aggregate_onmouseout = function(e) {
		grp_report_guide.hide("");
	};

	scwin.dma_search_onmodelchange = function(info) {
		if (info.key === "syspayno" && dma_search.get("syspayno") != "") {
			scwin.searchWorkTime();
		}
	};

	scwin.btn_search_onclick = function(e) {
		scwin.searchWorkTime();
	};

	scwin.ica_basedate_onviewchange = function(info) {
		if (com.isEmpty(this.getValue())) {
			com.warn("기준일을 입력해주세요.");
			this.setValue(com.getServerDateTime("yyyyMM"));	
		}
	};
