head.script:
/****************************************************
 * 화면 설명 : 휴가원 증빙자료
 * 변경 이력 : 2022-09-02 이영희 최초생성
 * serviceId : C22A
 ****************************************************/    
 /**
 * 페이지 로딩 이벤트
 */          
scwin.onpageload = function() {
	scwin.param = com.getParameter();
	var sysPayNo = scwin.param.sysPayNo;
	var year = scwin.param.analYr;
	var displayType = scwin.param.displayType;
	if (com.trim(year) == "") year = (new Date()).getFullYear();

	if(displayType == "use"){
		grpOuter.show();  // 기존화면
		grpOuter2.hide(); // 보상공제
		grpOuter3.hide(); // 연차공제
		grpOuter4.hide();
	}else if(displayType == "com"){
		grpOuter.hide();  // 기존화면
		grpOuter2.show(); // 보상공제
		grpOuter3.hide(); // 연차공제
		grpOuter4.hide();
	}else if(displayType == "anal"){
		grpOuter.hide();  // 기존화면
		grpOuter2.hide(); // 보상공제
		grpOuter3.show(); // 연차공제
		grpOuter4.hide();
	}else{
		grpOuter.hide();  // 기존화면
		grpOuter2.hide(); // 보상공제
		grpOuter3.hide(); // 연차공제
		grpOuter4.show();
	}

	dma_cgsAnnualSch.set("syspayno", sysPayNo);
	dma_cgsAnnualSch.set("year", year);
	dma_cgsAnnualSch.set("anal_yr", year);
	com.execute(sbm_selectInfo, {
		successCallback : function(e) {
		}
	});
};

scwin.onpageunload = function() {

};


scwin.btn_holiday_onclick = function(e) {
	if (dlt_cgsLappInfo.getTotalRow() > 6000) {
		com.alert("6000건 이상");
	} else {
		var now = com.getServerDateTime("yyyyMMdd");
		var name = '휴가사용내역_' + now + '.xlsx';

		var option = {
			    fileName : name
		};
		com.downloadGridExcel(grd_holiday, option);
	}
};

/**
 * 닫기 버튼 클릭 이벤트
 */ 
scwin.btn_close_onclick = function(e) {
	com.closePopup();
};
