head.script:
    /****************************************************
 	 * 화면 설명 : 매입전자세금계산서 팝업
 	 * 변경 이력 : 2022-06-02	이영희	최초 작성
 	 ****************************************************/  
 	/**
	 * 페이지 로딩 이벤트
	 */ 
	scwin.onpageload = function() {		
		var param = com.getParameter();
		dma_search.setJSON(param);
		btn_del.hide();
		btn_deleteSlip.hide();
		btn_saveSlip.hide();

		if(param.wrk_clsf == "mnt"){ // 매입계산서(관)에서 넘어왔을 경우에만 보여줌
			grp_slip.show("");
		}else{
			grp_slip.hide();
		}

		if(param.mode == "view"){
			grp_find_file.hide();
			btn_save.hide();
			grp_broker.hide();
			grp_mody.hide();
			grd_vend.hide();

			if(param.issu_id != ""){
				dma_search.set("issu_seqno", param.issu_id);	// 계산서 삭제 시 사용하는 컬럼명 상이한 관계로 추가
				scwin.search();

				if(/1002207/.test(com.getLoginInfo("roleCode"))){	// 세무담당자만 삭제버튼 표출
					btn_del.show("");
				}	
			}
		}else{
			grp_broker.hide();
			grp_msg.hide();
			btn_print.hide();

			dma_bill.set("tax_type_nm", "전자세금계산서");
			dma_bill.set("company_code", "KTX");
			dma_bill.set("sys_chk", "W"); // 시스템 구분
			dma_bill.set("bill_type", "2"); // 매출(1)/매입(2) 구분
			dma_bill.set("stat_code", "01");
			dma_bill.set("recp_cd", "1"); // 정발행(1)/역발행(2) 구분
			dma_bill.set("rst_rcp_yn", "N");
			dma_bill.set("err_cd", "000000");
			dma_bill.set("loadstatus", "0"); // 발행처리상태
			dma_bill.set("asp_sdatetime", com.getServerDateTime("yyyyMMddHHmmss"));
		}
	};

	scwin.onpageunload = function() {

	};	

	/**
	 * 조회
	 */
	scwin.search = function(){
		com.execute(sbm_selectData, {
			successCallback : function(e){
				scwin.setMstr();				

				// 세무담당자에 한하여 삭제, 저장 버튼 퀀한 부여
				if (/1002207/.test(com.getLoginInfo("roleCode"))) {
					btn_saveSlip.show("");

					if (dma_bill.get("unslip_no") != "") {
						btn_deleteSlip.show("");
					}
				} else {
					ibx_unslip_no.setDisabled(true);
				}
			}
		});
	};

	/**
	 * 닫기
	 */
	scwin.btn_close_onclick = function(e) {
		com.closePopup();
	};	

	/**
	 * 계산서 마스터 셋팅
	 */
	scwin.setMstr = function(){		
		spn_selr_corp_no.setValue(dma_bill.get("selr_corp_no").replace(/(\d{3})(\d{2})(\d{5})/, '$1-$2-$3'));
		spn_buyr_corp_no.setValue(dma_bill.get("buyr_corp_no").replace(/(\d{3})(\d{2})(\d{5})/, '$1-$2-$3'));

		if(dma_bill.get("regs_date").length == 8){
			spn_regs_yr.setValue(dma_bill.get("regs_date").substring(2,4));
			spn_regs_mon.setValue(dma_bill.get("regs_date").substring(4,6));
			spn_regs_dd.setValue(dma_bill.get("regs_date").substring(6,8));
		}

		// 공급가액
		var chrgArr = (dma_bill.get("chrg_amt")+"").split("");
		var s = 0;
		for(var i=12 - chrgArr.length; i<12; i++){
			dma_chrg.set("val_" + i, chrgArr[s]);
			s++;
		}

		// 세액
		var taxArr = (dma_bill.get("tax_amt")+"").split("");
		s = 0;
		for(var i=11 - taxArr.length; i<11; i++){
			dma_tax.set("val_" + i, taxArr[s]);
			s++;
		}
	};

	/**
	 * 저장
	 */
	scwin.btn_save_onclick = function(e) {		
		// 필수값 체크
		if (!com.validateData(dma_bill, [{ id : "issu_id", mandatory : true}], false)) return;

		dma_bill.set("pur_con_user_id", com.getLoginInfo("sn"));
		dma_bill.set("pur_con_org_code", com.getLoginInfo("deptCode"));
		dma_bill.set("wrte_psn", com.getLoginInfo("sn"));
		dma_bill.set("wrte_dept", com.getLoginInfo("deptCode"));

		if(dma_bill.get("mody_code") == "02"){ //공급가액 변동
			if(dma_bill.get("bfo_issu_id") == ""){
				com.alert(com.getMsg("message.xom.co.com.0001", "수정 전 일련번호"));
				return;
			}
		}

		var selrCorpNo = dma_bill.get("selr_corp_no").replace(/\-/gi, "");
		if (selrCorpNo == "1198201008") {
			com.alert("message.alert.fin.spm.0003"); // 매출계산서는 등록이 불가합니다.
			return;
		}

		// 2019.09.05 타기관 매입계산서 등록불가
		var buyrCorpNo = dma_bill.get("buyr_corp_no").replace(/\-/gi, "");
		if (buyrCorpNo != "1198201008") {
			com.alert("message.alert.fin.spm.0004"); // 공급받는자가 한국생산기술연구원(119-82-01008)이 아닌 매입계산서는 등록이 불가합니다.
			return;
		}

		var curYear = com.getServerDateTime().substring(0,4);

		if(dma_bill.get("regs_date").substring(0,4) < curYear && dma_bill.get("pur_con_user_id") != "20140580"){
			com.alert(com.getMsg("message.alert.fin.spm.0005", curYear));
			return;
		}

		if(dlt_vend.getRowCount() == 0){
			com.alert("message.alert.fin.spm.0006");

			// 거래처관리 매뉴 오픈			
			com.openTabMenu("F119");
			return;
		}else{
			for(var i=0; i<dlt_vend.getRowCount(); i++){
				if(dlt_vend.getCellData(i, "tran_stop_ymd") != ""){
					com.alert("message.xom.ui.fin.023");
					return;
				}
			}

			dma_bill.set("ech_code", dlt_vend.getCellData(0, "vend_cd"));
		}

		com.execute(sbm_saveData, {
			successCallback : function(e){
				com.alert("message.xom.wq.023", function() {
					var billObj = { dma_bill : dma_bill
									, dlt_smlitm : dlt_smlitm }
					com.closePopup(billObj);
				});
			}			
		});		
	};

	/**
	 * 업로드 버튼 클릭 이벤트
	 */
	scwin.upd_xmlFile_onInputValueChange = function(fileName) {
		dlt_detl.removeAll();
		dlt_detl.reform();

		var strFileName = upd_xmlFile.getValue();
		var ext="";
		console.log("------------- strFileName : " + strFileName);
		if (strFileName && '' != strFileName) {
			ext=strFileName.substring(strFileName.lastIndexOf(".")+1);
			if (ext.toUpperCase() == "XML") {
				upd_xmlFile.changeFormAction("/SvcFINSPMPOPF01.do?callbackFunc=" + $p.getFrameId() + "_scwin.uploadCallback");
				upd_xmlFile.submit();
			} else {
				com.alert("전자세금계산서 XML파일의 확장자는 'XML'이어야 합니다.\n(대소문자 관계없음)");
				//uploadForm.reset();
			}
		}
	};

	/**
	 * 업로드 콜백
	 */
	scwin.uploadCallback = function(rslt) {
//		rslt = rslt.replace(/:\s*[^"0-9.]?-*([0-9.]+)/g, ':"$1"');
		var json = JSON.parse(rslt);
		var data = json.TaxInvoice;

		if(data.ExchangedDocument != undefined) {
			if(data.ExchangedDocument.IssueDateTime != undefined) {
				dma_bill.set("issu_date", data.ExchangedDocument.IssueDateTime.substring(0, 8));
			}
		}

		if(data.TaxInvoiceDocument != undefined){
			dma_bill.set("issu_id", data.TaxInvoiceDocument.IssueID);
			dma_bill.set("issu_seqno", data.TaxInvoiceDocument.IssueID);
			dma_bill.set("regs_date", data.TaxInvoiceDocument.IssueDateTime);

			//세금계산서종류
			if(data.TaxInvoiceDocument.TypeCode != undefined){
				dma_bill.set("tax_type", data.TaxInvoiceDocument.TypeCode);

				if(/03|05/.test(dma_bill.get("tax_type").substring(2,4))){ // 위수탁, 영세율 위수탁
					grp_invoice.hide();
					grp_broker.show("");
				}

				/* 세금계산서 종류(2자리) : 01(세금계산서), 02(수정세금계산서), 03(계산서), 04(수정계산서), 05(영수증)
				   세금계산서 분류(2자리) : 01(일반), 02(영세율), 03(위수탁), 04(수입), 05(영세율위수탁)
				*/
				if(/0101|0103|0104|0201|0203|0204/.test(dma_bill.get("tax_type"))){ // 세금계산서(전자)
					dma_bill.set("evid_cd", "FAH011");	

				}else if(/0102|0105|0202|0205/.test(dma_bill.get("tax_type"))){
					dma_bill.set("evid_cd", "FAH021"); // 세금계산서(영세율)

				}else if(/0301|0303|0304|0401|0403|0404/.test(dma_bill.get("tax_type"))){
					dma_bill.set("evid_cd", "FAH031"); // 계산서
				}
			}

			//영수/청구구분
			if(data.TaxInvoiceDocument.PurposeCode != undefined){
				dma_bill.set("pops_code", data.TaxInvoiceDocument.PurposeCode); // 01 : 영수, 02 : 청구
			}else{
				dma_bill.set("pops_code", "01");
			}	

			// 수정코드
			if(data.TaxInvoiceDocument.AmendmentStatusCode != undefined) {
				dma_bill.set("mody_code", data.TaxInvoiceDocument.AmendmentStatusCode);
			}		
			dma_bill.set("pops_nm", (dma_bill.get("pops_code") == "01")?"영수":"청구");

			if(data.TaxInvoiceDocument.DescriptionText != undefined) {
				var descriptionTextArray = [];				
				if(typeof(data.TaxInvoiceDocument.DescriptionText.length) == "undefined") {
					descriptionTextArray[0] = data.TaxInvoiceDocument.DescriptionText;
				}else {
					descriptionTextArray.push(data.TaxInvoiceDocument.DescriptionText);
				}

				dma_bill.set("note1", descriptionTextArray.join(" ").trim());
			}

			// 수입세금계산서 정보
			if(data.TaxInvoiceDocument.ReferencedImportDocument != undefined) {
				if(data.TaxInvoiceDocument.ReferencedImportDocument.ID != undefined) {
					dma_bill.set("impt_no", data.TaxInvoiceDocument.ReferencedImportDocument.ID);
				}

				if(data.TaxInvoiceDocument.ReferencedImportDocument.ItemQuantity != undefined) {
					dma_bill.set("item_quant", data.TaxInvoiceDocument.ReferencedImportDocument.ItemQuantity);
				}

				if(data.TaxInvoiceDocument.ReferencedImportDocument.AcceptablePeriod != undefined) {
					if(data.TaxInvoiceDocument.ReferencedImportDocument.AcceptablePeriod.StartDateTime != undefined) {
						dma_bill.set("acep_stat_date", data.TaxInvoiceDocument.ReferencedImportDocument.AcceptablePeriod.StartDateTime);
					}

					if(data.TaxInvoiceDocument.ReferencedImportDocument.AcceptablePeriod.EndDateTime != undefined) {
						dma_bill.set("acep_end_date", data.TaxInvoiceDocument.ReferencedImportDocument.AcceptablePeriod.EndDateTime);
					}
				}
			}

			// 거래정보
			if(data.TaxInvoiceTradeSettlement != undefined){
				// 공급가액 및 세액
				if(data.TaxInvoiceTradeSettlement.SpecifiedMonetarySummation != undefined){
					var amtData = data.TaxInvoiceTradeSettlement.SpecifiedMonetarySummation;
					// 세액
					if(amtData.TaxTotalAmount != undefined){
						dma_bill.set("tax_amt", amtData.TaxTotalAmount);
					}else{
						dma_bill.set("tax_amt", 0);
					}
					// 공급가액
					if(amtData.ChargeTotalAmount != undefined){
						dma_bill.set("chrg_amt", amtData.ChargeTotalAmount);
					}

					// 합계금액
					if(amtData.GrandTotalAmount != undefined){
						dma_bill.set("totl_amt", amtData.GrandTotalAmount);
					}
				}

				// 공급자정보
				if(data.TaxInvoiceTradeSettlement.InvoicerParty != null) {
					var supData = data.TaxInvoiceTradeSettlement.InvoicerParty;

					// 공급자 사업자등록번호
					dma_bill.set("selr_corp_no", supData.ID);
					// 공급자 상호
					dma_bill.set("selr_corp_nm", supData.NameText);	
					// 공급업체의 업태
					dma_bill.set("selr_buss_cons", supData.TypeCode);	
					// 공급업체의 업종
					dma_bill.set("selr_buss_type", supData.ClassificationCode);	
					//종사업장 정보
					if(supData.SpecifiedOrganization != undefined && supData.SpecifiedOrganization.TaxRegistrationID != undefined){
						dma_bill.set("selr_code", supData.SpecifiedOrganization.TaxRegistrationID); // 종사업장 식별코드		
					}
					// 공급자 대표자 정보
					dma_bill.set("selr_ceo", supData.SpecifiedPerson.NameText);	
					// 공급자 주소
					dma_bill.set("selr_addr", supData.SpecifiedAddress.LineOneText);	

					// 담당자 정보
					if(supData.DefinedContact != undefined){
						if(supData.DefinedContact.DepartmentNameText != undefined){
							dma_bill.set("selr_chrg_post", supData.DefinedContact.DepartmentNameText);
						}
						if(supData.DefinedContact.PersonNameText != undefined){
							dma_bill.set("selr_chrg_nm", supData.DefinedContact.PersonNameText);
						}
						if(supData.DefinedContact.TelephoneCommunication != undefined){
							dma_bill.set("selr_chrg_tel", supData.DefinedContact.TelephoneCommunication);
						}
						if(supData.DefinedContact.URICommunication != undefined){
							dma_bill.set("selr_chrg_email", supData.DefinedContact.URICommunication);	
						}
					}		
				}

				if(data.TaxInvoiceTradeSettlement.InvoiceeParty != null) {
					var buyrData = data.TaxInvoiceTradeSettlement.InvoiceeParty;
					// 사업자등록번호
					dma_bill.set("buyr_corp_no", buyrData.ID);	
					// 상호
					dma_bill.set("buyr_corp_nm", buyrData.NameText);	
					// 업태
					dma_bill.set("buyr_buss_cons", buyrData.TypeCode);	
					// 업종
					dma_bill.set("buyr_buss_type", buyrData.ClassificationCode);	
					// 종사업장 정보
					dma_bill.set("buyr_gb", buyrData.SpecifiedOrganization.BusinessTypeCode);	// 공급받는자 구분코드
					dma_bill.set("buyr_code", buyrData.SpecifiedOrganization.TaxRegistrationID);	// 공급받는자 종사업장 식별코드				

					// 대표자 정보
					dma_bill.set("buyr_ceo", buyrData.SpecifiedPerson.NameText);	
					// 주소
					dma_bill.set("buyr_addr", buyrData.SpecifiedAddress.LineOneText);	
					// 담당자1 정보
					if(buyrData.PrimaryDefinedContact != undefined){
						if(buyrData.PrimaryDefinedContact.DepartmentNameText != null && buyrData.PrimaryDefinedContact.DepartmentNameText != undefined){
							dma_bill.set("buyr_chrg_post1", buyrData.PrimaryDefinedContact.DepartmentNameText);	
						}
						if(buyrData.PrimaryDefinedContact.PersonNameText != undefined){
							dma_bill.set("buyr_chrg_nm1", buyrData.PrimaryDefinedContact.PersonNameText);
						}
						if(buyrData.PrimaryDefinedContact.TelephoneCommunication != undefined){
							dma_bill.set("buyr_chrg_tel1", buyrData.PrimaryDefinedContact.TelephoneCommunication);
						}
						if(buyrData.PrimaryDefinedContact.URICommunication != undefined){
							dma_bill.set("buyr_chrg_email1", buyrData.PrimaryDefinedContact.URICommunication);
						}
					}

					// 담당자2 정보
					if(buyrData.SecondaryDefinedContact != undefined){
						if(buyrData.SecondaryDefinedContact.DepartmentNameText != undefined){
							dma_bill.set("buyr_chrg_post2", buyrData.SecondaryDefinedContact.DepartmentNameText);	
						}

						if(buyrData.SecondaryDefinedContact.PersonNameText != undefined){
							dma_bill.set("buyr_chrg_nm2", buyrData.SecondaryDefinedContact.PersonNameText);
						}
						if(buyrData.SecondaryDefinedContact.TelephoneCommunication != undefined){
							dma_bill.set("buyr_chrg_tel2", buyrData.SecondaryDefinedContact.TelephoneCommunication);
						}
						if(buyrData.SecondaryDefinedContact.URICommunication != undefined){
							dma_bill.set("buyr_chrg_email2", buyrData.SecondaryDefinedContact.URICommunication);
						}	
					}			
				}

				// 수탁자 정보
				if(data.TaxInvoiceTradeSettlement !=undefined && data.TaxInvoiceTradeSettlement.BrokerParty != undefined) {
					var brokerData = data.TaxInvoiceTradeSettlement.BrokerParty;
					// 사업자등록번호
					if(brokerData.ID != undefined){ 
						dma_bill.set("brok_corp_no", brokerData.ID);
					}
					// 업체명
					if(brokerData.NameText != undefined){ 
						dma_bill.set("brok_corp_nm", brokerData.NameText);
					}
					// 업태
					if(brokerData.TypeCode != undefined){ 
						dma_bill.set("brok_buss_cons", brokerData.TypeCode);
					}
					// 업종
					if(brokerData.ClassificationCode != undefined){ 
						dma_bill.set("brok_buss_type", brokerData.ClassificationCode);
					}
					// 종사업장 정보
					if(brokerData.SpecifiedOrganization != undefined){ 
						// 수탁자 종사업장 식별코드
						if(brokerData.SpecifiedOrganization.TaxRegistrationID != undefined){ 
							dma_bill.set("brok_code", brokerData.SpecifiedOrganization.TaxRegistrationID);
						}
					}
					// 대표자 정보
					if(brokerData.SpecifiedPerson != undefined){ 
						if(brokerData.SpecifiedPerson.NameText != undefined){ 
							dma_bill.set("brok_ceo", brokerData.SpecifiedPerson.NameText);
						}
					}
					// 주소
					if(brokerData.SpecifiedAddress != undefined){ 
						if(brokerData.SpecifiedAddress.LineOneText != undefined){ 
							dma_bill.set("brok_addr", brokerData.SpecifiedAddress.LineOneText);
						}
					}
					// 담당자 정보
					if(brokerData.DefinedContact != undefined){ 
						// 담당부서명
						if(brokerData.DefinedContact.DepartmentNameText != undefined){ 
							dma_bill.set("brok_chrg_post", brokerData.DefinedContact.DepartmentNameText);
						}
						// 담당자명
						if(brokerData.DefinedContact.PersonNameText != undefined){ 
							dma_bill.set("brok_chrg_nm", brokerData.DefinedContact.PersonNameText);
						}
						// 담당자전화번호
						if(brokerData.DefinedContact.TelephoneCommunication != undefined){ 
							dma_bill.set("brok_chrg_tel", brokerData.DefinedContact.TelephoneCommunication);
						}
						// 담당자이메일
						if(brokerData.DefinedContact.URICommunication != undefined){ 
							dma_bill.set("brok_chrg_email", brokerData.DefinedContact.URICommunication);
						}
					}
				}

				// 결제방법
				if(data.TaxInvoiceTradeSettlement.SpecifiedPaymentMeans != undefined) {
					// 현금
					var meanData = data.TaxInvoiceTradeSettlement.SpecifiedPaymentMeans;
					if(meanData.TypeCode != undefined){
						// 현금
						if(meanData.TypeCode == "10"){
							dma_bill.set("pymt_type1", meanData.TypeCode);
							dma_bill.set("pamt_amt1", meanData.PaidAmount);
						// 수표
						}else if(meanData.TypeCode == "20"){
							dma_bill.set("pymt_type2", meanData.TypeCode);
							dma_bill.set("pamt_amt2", meanData.PaidAmount);
						// 어음
						}else if(meanData.TypeCode == "30"){
							dma_bill.set("pymt_type3", meanData.TypeCode);
							dma_bill.set("pamt_amt3", meanData.PaidAmount);
						// 외상
						}else if(meanData.TypeCode == "40"){
							dma_bill.set("pymt_type4", meanData.TypeCode);
							dma_bill.set("pamt_amt4", meanData.PaidAmount);
						}
					}
				}
			}	

			// 상품정보
			if(data.TaxInvoiceTradeLineItem != undefined) {
				var itemArr = [];
				if(typeof(data.TaxInvoiceTradeLineItem.length) == "undefined") {
					itemArr[0] = data.TaxInvoiceTradeLineItem;
				}else {
					itemArr = data.TaxInvoiceTradeLineItem;
				}

				var row =0;
				itemArr.forEach(function(el,index){
					var rowData ={};

					// 일련번호
					if(el.SequenceNumeric != undefined){
						rowData.seq_no = el.SequenceNumeric;
					}
					// 공급년월일
					if(el.PurchaseExpiryDateTime != undefined){
						rowData.buy_date = el.PurchaseExpiryDateTime;
						if(rowData.buy_date.length == 8){
							rowData.buy_mon = rowData.buy_date.substring(4,6);
							rowData.buy_day = rowData.buy_date.substring(6,8);
						}else if(rowData.buy_date.length == 4){
							rowData.buy_mon = rowData.buy_date.substring(0,2);
							rowData.buy_day = rowData.buy_date.substring(2,4);
						}
					}
					// 품목명
					if(el.NameText != undefined){
						rowData.item_nm = el.NameText;
					}
					// 규격
					if(el.InformationText != undefined){
						rowData.item_infm = el.InformationText;

						if (rowData.item_infm == "") {
							rowData.item_unit_nm = "식";
						}
					} else {
						rowData.item_unit_nm = "식";					
					}
					// 비고
					if(el.DescriptionText != undefined){
						rowData.item_desp = el.DescriptionText;
					}
					// 수량
					if(el.ChargeableUnitQuantity != undefined){
						rowData.item_qunt = el.ChargeableUnitQuantity;
						rowData.item_qty = el.ChargeableUnitQuantity;
					}else{
						rowData.item_qunt = 0;
						rowData.item_qty = 0;
					}
					// 공급가액
					if(el.InvoiceAmount != undefined){
						rowData.item_amt = el.InvoiceAmount;
					}
					// 세액
					if(el.TotalTax != undefined && el.TotalTax.CalculatedAmount != undefined){
						rowData.item_tax = el.TotalTax.CalculatedAmount;
						rowData.tax_amt = el.TotalTax.CalculatedAmount;
					}else{
						rowData.item_tax = 0;
						rowData.tax_amt = 0;
					}					
					// 단가					
					if(el.UnitPrice != undefined && el.UnitPrice.UnitAmount != undefined){
						rowData.unit_prce = WebSquare.util.parseInt(el.UnitPrice.UnitAmount);
						rowData.item_unit_price = WebSquare.util.parseInt(el.UnitPrice.UnitAmount);
					}else{
						rowData.unit_prce = 0;
						rowData.item_unit_price = 0;
					}
					// 소액물품검수조서에서 총액
					if(rowData.item_amt != undefined) {
						rowData.totl_amt = Number(rowData.item_amt) + Number(rowData.item_tax);
					}

					dlt_detl.insertRow(row);
					dlt_detl.setRowJSON(row, rowData, true);

					// 2023.12.06 소액물품검수조서 내 항목들 자동세팅을 위해 추가
					dlt_smlitm.insertRow(row);
					dlt_smlitm.setRowJSON(row, rowData, true);
					row++;
				});
			}		


			// 계산서 정보 마스터 셋팅
			scwin.setMstr();

			// 거래선 정보 조회
			if(dma_bill.get("selr_corp_no") != ""){
				dma_search.set("bsns_psn_regst_no", dma_bill.get("selr_corp_no"));
				com.execute(sbm_selectVend, {
					successCallback : function(e){
						for(var i=0; i<dlt_vend.getRowCount(); i++){
							if(dlt_vend.getCellData(i, "tran_stop_ymd") == ""){
								grd_vend.setColumnVisible("tran_stop_cause", false);
							}
						}
					}
				});
			}			
		}
	}

	/**
	 * 수정전 일련번호 검색
	 */
	scwin.btn_bill_onclick = function(e) {
		if(dma_bill.get("selr_corp_no") == ""){
			com.alert(com.getMsg("message.xom.co.com.0001", "공급자사업자번호"));
			return;
		}

		var data = {
			bsns_psn_regst_no : dma_bill.get("selr_corp_no"),
			inqr_clsf : "tax_bill"
		}

		biz.fin.pop.open(biz.fin.pop.TYPE.BILL, data, function(ret) {
			dma_bill.set("bfo_issu_id", ret.issu_id);
		});
	};

	scwin.printHtml = '';

	scwin.btn_print_onclick = function(e) {
		var html = grpTaxInfo.render.innerHTML;
		scwin.printHtml = html;

		var url = "/ui/com/pop/COM_Z019_00P.xml";
		var option = { type: "browserPopup", width: 1350, height: 1480, id: "printPop", popupName: "프린트 팝업"};
		var data = { type : 'print', parentID : scwin.$w.id};
		com.openPopup(url, option, data);
	};

	/**
	* 결의번호 업데이트
	*/
	scwin.btn_saveSlip_onclick = function(e) {
		if(ibx_unslip_no.getValue() == ""){
			alert("결의번호를 입력하세요");
			return;	
		}

		com.execute(sbm_saveUnslipNo, {
			successCallback : function(e){
				com.closePopup({});
			}
		});
	};

	/**
	* 삭제버튼 클릭이벤트
	*/
	scwin.btn_del_onclick = function(e) {
		if(ibx_unslip_no.getValue() != ""){
			alert("해당 " + dma_bill.get("tax_type_nm") + "로 결의가 진행되어 삭제할 수 없습니다.");
			return;
		}

		com.confirm("message.xom.wq.022"/*삭제하시겠습니까?*/, function(ret){
			if(ret){
				com.execute(sbm_deleteData, {			
					successCallback : function(e){
						com.alert("message.xom.wq.023", function() {
							com.closePopup({issu_seqno :""});
						});
					}
				});
			}
		});
	};

	/**
	* 결의번호 삭제버튼 클릭이벤트
	*/
	scwin.btn_deleteSlip_onclick = function(e) {
		com.confirm("message.xom.wq.022"/*삭제하시겠습니까?*/, function(ret){
			if(ret){
				com.execute(sbm_deleteUnslipNo, {
					successCallback : function(e){
						com.closePopup({});
					}
				});
			}
		});
	};
