     * 조달청 전송 버튼 클릭 이벤트
     */
	scwin.btn_send_onclick = function(e) {

	};
	
    /**
     * 확정 버튼 클릭 이벤트
     */	
	scwin.btn_confirm_onclick = function(e) {
		
		//필수값체크 실행
		if (!scwin.preSave()) {
			return false;
		}
	
		//확정 로직
		com.confirm("확정 하시겠습니까?", function(ret){
			if (ret) {
				
				//저장로직 실행
				scwin.save("approval");
			}
		});

	};
	
    /**
     * 결재신청 버튼 클릭 이벤트
     */	
	scwin.btn_apr_onclick = function(e) {
		
		var eval_state = dma_info.get("eval_state");
		
		//필수값체크 실행
		if (!scwin.preSave()) {
			return false;
		}
		
		//구매 담당자 조회(manager)
		dma_search.set("role_type", "PUR");
		dma_search.set("syspayno", dma_info.get("sys_pay_no"));
		//dma_search.set("syspayno", dma_info.get("charger_cd"));
		//dma_search.set("syspayno", dma_info.get("assist_charger_cd"));
		
		//결재신청시 manager 세팅하기 위함
		com.execute(sbm_selectDeptToManager, {
			successCallback : function(e) {

				dma_info.set("manager_id", e.responseJSON.elData.syspayno);
		
				//"PRD050"협조심사중 "PRD060"협조심사결재중
				if (eval_state == "PRD050" || eval_state == "PRD060") {
					
					//결재신청 로직
					com.confirm("결재신청 하시겠습니까?", function(ret){
						if (ret) {
							
							//저장로직 실행
							scwin.save("apr");
						}
					});
					
					
				} else {
					com.alert("결재신청은 저장 이후 가능합니다.");
					return;
				}
			}
		});	
		

	};
	
    /**
     * 접수 버튼 클릭 이벤트
     */	
	scwin.btn_new_onclick = function(e) {

		//추가 필요 데이터 세팅		
		dma_info.set("ui_id"			, "P385");
		dma_info.set("eval_state"		, "PRD040");	//PRD040 협조심사대기
		
		//접수(=결재승인 로직)
		com.confirm("접수 하시겠습니까?", function(ret) { 		
			if(ret) {
				com.execute(sbm_aprAgr, {
					successCallback : function(e) {
						com.alert("접수 완료 되었습니다.");
						//com.openTabMenu("A004", {});
					}
				});
			}
		});
	};
	
    /**
     * 저장, 결재신청 전 필수값체크
     */
	scwin.preSave = function() {

		var eval_state = dma_info.get("eval_state");
		
		//기술평가목록이 없는 경우
		if(grid1.getRowCount() <= 0){
			alert("심사평가 업체를 등록하여 주십시오.");
			return;
		}
	    
		if (eval_state == "PRD040" || eval_state == "PRD050") {	//협조요청 후에는 평가 점수 필수
		
			for (var i = 0; i < grid1.getRowCount(); i++) {
				//기술평가점수가 없는 경우
				if (dlt_list.getCellData( i , "tot_score" ) == "") {
					com.alert("적심사평가점수를 입력하여 주십시오." );
					return;
				}
				
				//적합여부가 없는 경우
				if (dlt_list.getCellData( i , "succ_yn").trim() == "") {
					com.alert("적합여부를 선택하여 주십시오." );
					return;
				}
			}
			
	    }
		
		return true;
	};
	
    /**
     * 저장 버튼 클릭 이벤트
     */		
	scwin.btn_save_onclick = function(e) {

		//필수값체크 실행
		if (!scwin.preSave()) {
			return false;
		}
		
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret) {
			if (ret) {
				
				//저장로직 실행
				scwin.save("save");
				
			}
		});
	};
	
    /**
     * 저장, 수정, 결재신청 로직
     */	
	scwin.save = function(saveType) {

		var eval_state = dma_info.get("eval_state");
		
		//수정
		if (saveType == "save") {
		
			//"PRD010"협조심사대기 상태
			if (eval_state == "PRD040") {

				com.file.transport( function(attachObj){

					//추가 데이터 세팅
					dma_info.set("eval_state"					, dma_info.get("eval_state"));			//현재 진행상태
					dma_info.set("ui_id"						, "P449");								//serviceId
					dma_info.set("eval_attach_file_no"			, attachObj.upload_A);					//첨부파일(하단)
					dma_info.set("eval_assist_attach_file_no"	, attachObj.upload_B);					//첨부파일(상단)
					
					//dlt_list에 new_odr 추가해주기 위함
					for(var i = 0; i < dlt_list.getRowCount(); i++){
						dlt_list.setCellData(i, "bid_noti_no"	, dma_info.get("bid_noti_no"));
						dlt_list.setCellData(i, "chg_no"		, dma_info.get("chg_no"));
						dlt_list.setCellData(i, "eval_clsf_cd"	, dma_info.get("eval_clsf_cd"));
						dlt_list.setCellData(i, "new_odr" 		, i+1);
						dlt_list.setCellData(i, "odr" 			, i+1);
						dlt_list.setCellData(i, "eval_state" 	, eval_state);
					}
					
					//수정
					com.execute(sbm_updateData, {
						successCallback : function(e) { // 성공 콜백					
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								
								//재조회
								scwin.search();
								
							});		
						}
					});
					
				});
				
				
			//"PRD050"협조심사중 상태 -> 수정
			} else if (eval_state == "PRD050") {

				com.file.transport( function(attachObj){

					//추가 데이터 세팅
					dma_info.set("eval_state"					, dma_info.get("eval_state"));			//현재 진행상태
					dma_info.set("ui_id"						, "P385");								//serviceId
					
					dma_info.set("eval_attach_file_no"			, attachObj.upload_A);					//첨부파일(하단)
					dma_info.set("eval_assist_attach_file_no"	, attachObj.upload_B);					//첨부파일(상단)
					dma_info.set("tech_eval_assist_doc_no"		, dma_info.get("tech_eval_assist_doc_no"));		//협조내용결재 문서번호
					
					//dlt_list에 new_odr 추가해주기 위함
					for(var i = 0; i < dlt_list.getRowCount(); i++){
						dlt_list.setCellData(i, "bid_noti_no"	, dma_info.get("bid_noti_no"));
						dlt_list.setCellData(i, "chg_no"		, dma_info.get("chg_no"));
						dlt_list.setCellData(i, "eval_clsf_cd"	, dma_info.get("eval_clsf_cd"));
						dlt_list.setCellData(i, "new_odr" 		, i+1);
						dlt_list.setCellData(i, "odr" 			, i+1);
						dlt_list.setCellData(i, "eval_state" 	, eval_state);
					}
					
					//수정
					com.execute(sbm_updateData, {
						successCallback : function(e) { // 성공 콜백					
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								
								//재조회
								scwin.search();
								
							});		
						}
					});
					
				});
				
				
			//"PRD070"심사확정 상태 -> 수정불가alert
			} else if (eval_state == "PRD070") {
				com.alert("결재신청건은 수정할 수 없습니다.");
				return;
			}

			
		//결재신청
		} else if (saveType == "apr") {
			//스냅샷 결재신청
			
			com.file.transport( function( attachObj){
					
				//추가 데이터 세팅
				dma_info.set("approval"						, true); 		//결재여부
				
				dma_info.set("eval_state"					, "PRD060");	//기존 "PRD050"협조심사중 -> "PRD060"협조심사결재중으로 변경
				dma_info.set("ui_id"						, "P385");		//serviceId
				
				dma_info.set("eval_attach_file_no"			, attachObj.upload_A);					//첨부파일(하단)
				dma_info.set("eval_assist_attach_file_no"	, attachObj.upload_B);					//첨부파일(상단)
				
				//결재신청
				com.execute(sbm_aprData, {
					successCallback : function(e) { // 성공 콜백					
							
							com.alert("!!! 결재신청 확인");
							// 결재화면 이동
							//com.movePageMenu("A001", { "reqNo" :  dma_dtl.get("req_no")});
	
					}
				});
				
			});
		
		
		//확정
		} else if (saveType == "approval") {
			
			com.alert("!!!확정로직");
		
		
		
		}
		
	};
