head.script:
  	/****************************************************
 	 * 화면 설명 : 발주계획등록
 	 * 변경 이력 : 2023-08-07	이영희	수정 작성
 	 * 서비스아이디 : P302
 	 ****************************************************/  
    scwin.data = {};

	 /*
     * 페이지 열기
     */ 
	scwin.onpageload = function() {	
		var param = com.getParameter();
		if(param.param4 == "apr"){
			if(param.req_no == undefined){
				choicepop.show();
			}
		}

		// 초기화
		debugger;
		spn_mdy_text.hide();
		spn_renoti_text.hide();
		spn_cntst_prod.hide();
		$("._pq_eval_yn").wq("setStyle", "display", "none");
		$(".delvrDate").wq("setStyle", "display", "none"); // 납품일자
		$(".rpa_yn").wq("setStyle", "display", "none"); // RPA 자동등록
		$(".esti_online").wq("setStyle", "display", "none");
		grp_bid_expl.hide(); // 입찰설명회
		sbx_join_method.hide(); // 공동수급협정서 구성방식
		grp_loc_limit.hide();
		grp_type_limit.hide();

		var minObj = document.querySelectorAll('.min');
		minObj.forEach(function(o){
			o.value ="00";
		});	

		// 견적요청서 리포트 그룹
		grp_estmt_attach.hide();

		// 첨부파일 컴포넌트 초기화
		com.file.init();		

		// 공통코드
		// 1) 투찰제한-지역제한 참가지역 조회(G2B 공통코드)
	    dma_search.set("upper_cd", "GU7");
		com.execute(sbm_selectG2bComCode, {
			successCallback : function(e){
				dlt_g2bCd.setJSON(e.responseJSON.epuComCodeG2bVoList);				
			}
		});

		// 공통코드 세팅
	    var codeOptions = [ 
		    { code : "PRB", compID :"sbx_bid_clsf_cd"}, // 공고진행방법
		    { code : "PRE", compID :"sbx_ord_helper"} // 발주도우미
	    ];
	    com.setCmnCd(codeOptions, function(){});		

		ta_ozText.setValue( "가. 「국가를당사자로하는계약에관한법률시행령」제12조에 의거 경쟁입찰의 참가자격을 갖춘 자" + "\n"
							+ "     및 제76조의 규정에 의한 부정당업자로 입찰참여 제한을 받지 않은 자" + "\n"
							+ "나. 「중소기업제품 구매촉진 및 판로지원에 관한 법률」제4조 제2항 및 동법 시행령 제2조의2 제1항" + "\n"
							+ "     제1호에 따른 소기업 또는 소상공인으로서 「중·소기업·소상공인 및 장애인기업 확인요령」에" + "\n"
							+ "     따라 발급된 소기업·소상공인 확인서를 소지한 업체" + "\n"
							+ "     (필요시 수요기관에서 확인 예정)"
							);	// 견적제출자격 문구 세팅	

		dma_search.setJSON(param);		

		if(param.ord_flag != "show"){		
			// G2B ID 조회
			dma_search.set("syspayno", com.getLoginInfo("sn"));

			com.execute(sbm_selectG2bId, {
				successCallback : function(e){
					scwin.data.G2bRegstId = e.responseJSON.g2b_regst_id;
				}
			});
		}

		if(param.param4 == "apr"){
			if(param.req_no != undefined && param.req_no != ""){
				var paramArr = param.req_no.split("-");
				dma_search.set("bid_ord_no", paramArr[0]);
				dma_search.set("chg_no", paramArr[1]);

				if(param.serviceId == "P302"){
					scwin.data.pur_grp_cd = "EGF010";	
				}else if(param.serviceId == "P345"){
					scwin.data.pur_grp_cd = "EGF020";	
				}else if(param.serviceId == "P303"){
					scwin.data.pur_grp_cd = "EGF030";	
				}

				dma_search.set("pur_clsf_cd", scwin.data.pur_grp_cd);
				// 화면초기화
				scwin.setFormInit(scwin.data.pur_grp_cd);
			}
		}else{				
			scwin.data.pur_grp_cd = param.pur_clsf_cd;
			scwin.data.ord_flag = param.ord_flag; // show 조회, reg 등록

			// 재공고시 pur_clsf_cd 가 넘어오지 않아서 셋팅
			if(param.pur_clsf_cd == undefined){
				if(param.serviceId == "P302"){
					scwin.data.pur_grp_cd = "EGF010";	
				}else if(param.serviceId == "P345"){
					scwin.data.pur_grp_cd = "EGF020";	
				}else if(param.serviceId == "P303"){
					scwin.data.pur_grp_cd = "EGF030";	
				}
			}

			scwin.setFormInit(scwin.data.pur_grp_cd);
		}			
	};

	scwin.onpageunload = function() {

	};

	/*
	 * 화면 유형 선택 버튼 클릭 이벤트
	 */
	scwin.getOrdType = function(clsf){
		choicepop.hide();	

		if(clsf == "EGF010"){	
			scwin.data.ord_flag = "reg";
			scwin.data.pur_grp_cd = clsf;

			scwin.setFormInit(clsf);
		}else{
			var data ={
				ord_flag : "reg",
				pur_clsf_cd : clsf,
			};

			var serviceId = "";
			if(clsf == "EGF020"){ // 용역
				serviceId = "P345";
			}else if(clsf == "EGF030"){ // 공사
				serviceId = "P303";
			}		

			com.moveList(serviceId, data);
		}
	};

	// 화면 초기화(구매,용역,공사,외자에 따른 화면 셋팅)
	scwin.setFormInit = function(clsf){	
		// 지체상금율
		if(clsf == "EGF010"){
			var data=[
				{delay_rate_nm : "물품제조구매 : 1천분의 0.75", delay_rate : "0.075"},
				{delay_rate_nm : "물품의 제조구매(단, 계약이후 설계승인이 필요한 설계 및 제조) : 1천분의 0.5", delay_rate : "0.050"},
				{delay_rate_nm : "물품수리가공대여 : 1천분의 1.25", delay_rate : "0.125"},
				{delay_rate_nm : "(구)물품의 제조 및 구매 : 1000분의 1.5", delay_rate : "0.150"},
			];	

			dlt_rate.setJSON(data);
		}else if(clsf == "EGF020"){
			var data=[
				{delay_rate_nm : "물품수리가공대여,용역 : 1천분의 1.25", delay_rate : "0.125"},
				{delay_rate_nm : "(구)물품의 수리, 가공 및 용역 : 1000분의 2.5", delay_rate : "0.250"}
			];	

			dlt_rate.setJSON(data);
		}else if(clsf == "EGF030"){
			var data=[
				{delay_rate_nm : "공사 : 1000분의 0.5", delay_rate : "0.050"}
			];	

			dlt_rate.setJSON(data);
		}

		if(clsf == "EGF030"){
			var data=[
				{cd : "1", cd_nm : "없음"},
				{cd : "2", cd_nm : "현장설명회 개최"},	
				{cd : "3", cd_nm : "현장설명회 의무"},	
			];	

			dlt_bid_expl.setJSON(data);

			spn_join_mem_loc_limit_yn.hide(); // 공사일경우 공동수급체 구성원 지역제한적용여부 필수아님
		}else{
			var data=[
				{cd : "1", cd_nm : "없음"},
				{cd : "2", cd_nm : "입찰설명회 개최"},	
				{cd : "3", cd_nm : "입찰설명회 의무"},	
			];	

			dlt_bid_expl.setJSON(data);			
		}

		var serviceObj = document.querySelectorAll('.SERVICE_ORD');
		var goodObj = document.querySelectorAll('.GOODS_ORD');
		var constObj = document.querySelectorAll('.CONST_ORD');
		switch(clsf){
			case "EGF010" :	
				serviceObj.forEach(function(o){
					o.style.display ="none";
				});	

				constObj.forEach(function(o){
					o.style.display ="none";
				});	
				break;
			case "EGF020" :	
				goodObj.forEach(function(o){
					o.style.display ="none";
				});	

				constObj.forEach(function(o){
					o.style.display ="none";
				});	
				break;
			case "EGF030" :
				sbx_bid_succ_method.hide(); // 계약방법(2) 숨김처리
				grp_service_type.hide(); 
				spn_bid_expl.setValue("현장설명회 여부");
				spn_bid_expl_loc.setValue("현장설명 장소");
				spn_bid_expl_ymd.setValue("현장설명일시");
				spn_budget_amt.setValue(spn_budget_amt.getValue() + "(부가가치세불포함)");
				rdo_multi_esti_pr_yn.setValue('2');
				rdo_multi_esti_pr_yn.trigger("viewchange");
				grp_ord_helper.hide(); // 발주도우미

				goodObj.forEach(function(o){
					o.style.display ="none";
				});	

				$(".group44_srv").wq("setStyle", "display", "none");

				break;
		}		

		// 초기 G2b 공통코드 셋팅
		scwin.getG2bComCode();		
	};

	/*
     * 초기 G2b 공통코드 셋팅
     */
	scwin.getG2bComCode = function(){
		if(scwin.data.pur_grp_cd == "EGF010"){
			com.biz.getG2bComCd({upper_cd:"L17", cd:"100", compID : "sbx_contrct_method"}, function(){
				sbx_contrct_method.trigger("viewchange");
			}); // 계약방법

			com.biz.getG2bComCd({upper_cd:"Z47", cd:"", compID : "sbx_succ_type" }); // 낙찰방법
		}else if(scwin.data.pur_grp_cd == "EGF020"){
			com.biz.getG2bComCd({upper_cd:"L17", str_index : 3, str_length : 2, cd:"00", compID : "sbx_contrct_method" }, function(){
				sbx_contrct_method.trigger("viewchange");
			}); // 일반용역일 때 계약방법

			com.biz.getG2bComCd({upper_cd:"Z46", cd:"", compID : "sbx_succ_type" }); // 낙찰방법
		}else if(scwin.data.pur_grp_cd == "EGF030"){
			// 계약방법(1)
			com.biz.getG2bComCd({upper_cd:"S01", compID : "sbx_contrct_method"}, function(){
				sbx_contrct_method.trigger("viewchange");
			}); 
			com.biz.getG2bComCd({upper_cd:"B22", cd:"", compID : "sbx_pq_eval_rcp_type", dataListId : "dlt_pq_rcp_type" }); // PQ심사신청서 접수방식
			com.biz.getG2bComCd({upper_cd:"S14", cd:"", compID : "sbx_succ_type" }); // 낙찰방법
		}

		com.biz.getG2bComCd({upper_cd:"B22", cd:"", compID : "sbx_join_possible_yn"}); // 공동수급협정서접수 및 구성방식
		com.biz.getG2bComCd({upper_cd:"Z44", cd:"", compID : "sbx_join_method" }); // 공동수급협정서접수 및 구성방식
		com.biz.getG2bComCd({upper_cd:"S93", cd:"", compID : "sbx_bid_succ_type"}, function(){ //입찰방식
			sbx_bid_succ_type.setValue("01");

			// 초기 작업
			scwin.setInitWork();
		});
	};

	/*
     * 초기 작업
     */
	scwin.setInitWork = function(){
		if(dma_search.get("bid_ord_no") != ""){ // 발주계획 상세보기(show)와 재공고(modify)
			// 발주내역 조회
			scwin.search();
		}else{
			dma_ord.set("chg_no", "00");
			dma_ord.set("cud_type", "C");
			// 작성자 초기 세팅
			dma_ord.set("ord_psn_nm", com.getLoginInfo('cn'));
			dma_ord.set("inout_psn_clsf_cd", "1");
			dma_ord.set("pur_clsf_cd", scwin.data.pur_grp_cd);
			dma_ord.set("multi_esti_pr_yn", "2");

			rdo_pq_eval_yn.setValue("N");

			if(scwin.data.pur_grp_cd == "EGF020"){
				sbx_service_type_cd.setValue("3"); // 일반용역	
				scwin.setSuccType();
			}else if(scwin.data.pur_grp_cd == "EGF030"){
				rdo_rebid_possible_yn.setValue("N"); // 재입찰가능여부
			}

			// 날자/시간 정보 기본 세팅
			var today = com.getServerDateTime();
			ica_noti_ymd.setValue(today); // 공고일자
			scwin.setBidDefVal();

			sbx_join_method.hide(); // 공동수급협정서 접수 및 구성방식
			//sbx_delay_amt_rate.setValue("0.150"); // 지체상금율
			scwin.setExeGuar(); // 하자보증 초기값 설정

			// 첨부파일
			com.file.create(upload_A.getID(), {fileKey: 'P302', mode: 'edit'}, function(){
				com.file.create(upload_B.getID(), {fileKey: 'P302', mode: 'edit'});	
			});

			if (com.getParameter("frompur") == "Y" || WebSquare.net.getParameter("frompur") == "Y"){ // 구매관리 또는 계약관리에서 조회
				var purReqNo = (com.getParameter("pur_req_no") == undefined)? WebSquare.net.getParameter("pur_req_no") : com.getParameter("pur_req_no");
				dma_search.set('pur_req_no', purReqNo); // 구매요구번호

				com.execute(sbm_selectPurItem, {
					successCallback : function(e){
						dlt_item.setJSON(e.responseJSON.epuOrdReqItemVoList);
						btn_item_add.setDisabled(true);
						btn_item_del.setDisabled(true);						

						scwin.setStylePurItem("new");
					}
				});
			}			
		}
	};

	/*
     * 하자보증 초기값 설정
     */
	scwin.setExeGuar = function(){
		if (!/modify|renoti/.test(scwin.data.ord_flag)){
			if(scwin.data.pur_grp_cd == "EGF010"){
				ibx_exe_guar_rate.setValue("3"); // 하자보증율
			}else if(scwin.data.pur_grp_cd == "EGF020"){
				ibx_exe_guar_rate.setValue("2"); // 하자보증율
			}else if(scwin.data.pur_grp_cd == "EGF030"){
				ibx_exe_guar_rate.setValue("10"); // 하자보증율
			}
			ibx_exe_guar_period.setValue("12"); // 하자보증기간
		}
	};

	/*
     * 조회
     */
	scwin.search = function(){
		com.execute(sbm_selectData, {
			successCallback : function(e){
			}
		});
	};

	/*
     * 조회 후 폼셋팅
     */
	scwin.setForm = function() {	
		// 버튼 제어
		com.biz.disabledButtons(dma_ord.get("apr_state"), dma_ord.get("elec_docu_no"));

		// 결재코드 XAD010 - 작성중, XAD013 - 회수, XAD015 - 반려, XAD100 - 결재완료
	    if(dma_ord.get("apr_state") >= "XAD020"){
			grp_btn_item.hide(); // 발주품목 추가,삭제 버튼

			// 입찰공고가 존재하지 않으면 발주계획 삭제 버튼을 보여준다.
			var bid_ord_no_ck = dma_ord.get("bid_noti_no")
			if( bid_ord_no_ck == "" && dma_ord.get("apr_state") != "XAD070") {
	      		btn_del.show("");
			}
			if( dma_ord.get("apr_state") == "XAD100" ) {
				if (scwin.data.ord_flag == "modify" || scwin.data.ord_flag == "renoti"){
					btn_save.setDisabled(false);
				}
			}
	    }	    

		scwin.setFormBidClsf(); // 공고진행방법 변경에 따른 폼 셋팅 
		// 구매요구내역
		scwin.setStylePurItem("");			

	    // 수의(총액) / 수의(총액)소액수의 인 경우 납품기한, 납품일자 입력
		if (dma_ord.get("bid_succ_method") == "4101" || dma_ord.get("contrct_method") == "42050") {
			$(".delvrDate").wq("setStyle", "display", "");
			$(".rpa_yn").wq("setStyle", "display", "");
		} else {
			$(".delvrDate").wq("setStyle", "display", "none");
			$(".rpa_yn").wq("setStyle", "display", "none");

			ica_delvr_ymd.setValue("");
			rdo_rpa_yn.setValue("N");
		}

		// 지명경쟁일 경우 
		if(/30000|3100|3200|37/.test(dma_ord.get("contrct_method")) || /06|09/.test(dma_ord.get("bid_succ_type"))){ // 06 전자시담, 09 전자시담(다자간)		
			grp_nomi_vend.show(); // 지명업체
		}else{
			dlt_bid_vend.removeAll(); 
			dlt_bid_vend.reform();
			grp_nomi_vend.hide();
		}	


	    // 견적요청서 - 견적제출자격 / 수의계약체결제한여부확인서
	    if(dma_ord.get("apr_state") >= "XAD005"){
		    // 공사 42050 수의(총액)소액수의
			if (dma_ord.get("bid_succ_method") == "4101" || dma_ord.get("contrct_method") == "42050" || sbx_ord_helper.getValue() == "PRE030") { // 소액수의 또는 견적요청
	        	grp_estmt_attach.show("");	        	
			}else {
	        	grp_estmt_attach.hide();
			}
	    }
	};

	/*
	* 날자/시간 정보 기본 세팅(공고일자변경 후 호출)
	*/
	scwin.setBidDefVal = function() {
		if(ica_noti_ymd.getValue() == "") return;

		// 입찰서 접수 개시일시
		ica_bid_start_ymd.setValue(scwin.afterThisDate(ica_noti_ymd.getValue(), 1));
		ibx_bid_start_hour.setValue("09");

		var baseDay = ""; 
		var baseHour = 0;
		if (/1107|2107|3107/.test(sbx_bid_succ_method.getValue())) { // 협상에 의한 계약일 경우 40일(공사는 적용안됨)
			rdo_rebid_possible_yn.setValue("N"); // 재입찰가능여부

			var rebidObj = document.querySelectorAll('.rebid_possible');
			rebidObj.forEach(function(o){
				o.style.display ="none";
			});				

			baseDay = (rdo_emer_bid_yn.getValue() == "N")? 40 : 10;  // 긴급 시 10일		

			ibx_bid_end_hour.setValue("16"); // 입찰서 접수마감일시			
			ibx_open_hour.setValue("17"); // 개찰일시		
			// 입찰참가등록마감일시
			if(scwin.data.pur_grp_cd == "EGF010"){	
				ibx_bid_apply_end_hour.setValue("18");
			}else if(scwin.data.pur_grp_cd == "EGF020"){
				ibx_bid_apply_end_hour.setValue("16");
				ibx_join_fin_hour.setValue("18"); // 공동수급협정서 접수 마감일시
				ibx_join_fin_min.setValue("00"); 
			}

			ibx_bid_gurnt_fin_hour.setValue("18"); // 입찰보증서접수마감일시			
		} else {
			// 재입찰가능여부
			var rebidObj = document.querySelectorAll('.rebid_possible');
			rebidObj.forEach(function(o){
				o.style.display ="";
			});	

			baseDay = (rdo_emer_bid_yn.getValue() == "N")? 7 : 5;  // 기본 7일, 긴급 시 5일				

			if(scwin.data.pur_grp_cd == "EGF030"){
				ibx_bid_end_hour.setValue("16"); // 입찰서 접수마감일시
				ibx_open_hour.setValue("17"); // 개찰일시
			}else{
				ibx_bid_end_hour.setValue("12"); // 입찰서 접수마감일시
				ibx_open_hour.setValue("13"); 
			}
			// 입찰참가등록마감일시
			ibx_bid_apply_end_hour.setValue("18");
			if(scwin.data.pur_grp_cd == "EGF020"){
				ibx_join_fin_hour.setValue("12"); // 공동수급협정서 접수 마감일시
			}
			ibx_bid_gurnt_fin_hour.setValue("18"); // 입찰보증서접수마감일시			
		}

		ica_bid_end_ymd.setValue( scwin.afterThisDate(ica_noti_ymd.getValue(), baseDay + 1)); // 입찰서 접수마감일자
		ica_open_ymd.setValue( scwin.afterThisDate(ica_noti_ymd.getValue(), baseDay + 1)); // 개찰일자		
		ica_bid_apply_end_ymd.setValue(scwin.afterThisDate(ica_noti_ymd.getValue(), baseDay)); // 입찰참가등록마감일자
		if(scwin.data.pur_grp_cd == "EGF020"){	
			ica_join_agr_fin_ymd.setValue(scwin.afterThisDate(ica_noti_ymd.getValue(), baseDay + 1)); // 공동수급협정서 접수 마감일자
		}
		ica_bid_gurnt_fin_ymd.setValue( scwin.afterThisDate(ica_noti_ymd.getValue(), baseDay)); // 입찰보증서접수마감일자

	};

	/*
	* 계약방법, 입찰방식에 따른 폼 셋팅	
	*/ 
	scwin.setFormVendByConMethod = function() {
		// 지명경쟁일 경우 (3200 지명(단가) - 일반용역, 37 지명경쟁 - 기술용역
		// 30000 공사 지명경쟁
		if( /30000|3100|3200|37/.test(sbx_contrct_method.getValue())|| /06|09/.test(sbx_bid_succ_type.getValue())){ // 06 전자시담, 09 전자시담(다자간)		
			grp_nomi_vend.show(); // 지명업체
		}else{
			dlt_bid_vend.removeAll(); 
			dlt_bid_vend.reform();
			grp_nomi_vend.hide();
		}		
	};	

	/*
	* 공고진행방법 변경에 따른 폼 셋팅 
	*/
	scwin.setFormBidClsf = function() {		
		scwin.data.checkBidSucc = "N";

		switch(sbx_bid_clsf_cd.getValue()){
			case "PRB010" : // 연계입찰
				spn_bid_msg.show();
				scwin.data.checkBidSucc = "N";
				break;
			case "PRB020" : // 중앙조달의뢰
			case "PRB030" : // 나라장터 쇼핑몰
				scwin.data.checkBidSucc = "Y-PRB";
				spn_bid_msg.hide();
				break;
			case "PRB040" : // 비연계
			    // 4201 수의(단가)소액수의 (용역)
				if(/4101|4201/.test(sbx_bid_succ_method.getValue()) && sbx_bid_succ_type.getValue() == "00"){ // 소액수의-직찰일 경우 
					scwin.data.checkBidSucc = "Y";
				}
				spn_bid_msg.hide();
				break;
			case "PRB060" : // 견적요청일 경우
				scwin.data.checkBidSucc = "Y";
				spn_bid_msg.hide();
				break;
		}	

		// checkBidSucc 에 따른 그룹 셋팅
		if(scwin.data.checkBidSucc == "Y"){ 
			grp_contrct.show(); // 계약및 입찰방식
			$(".group44").wq("setStyle", "display", "none"); // 낙찰방법, PQ심사여부 입찰설명회 여부(장소포함) 등
			if(scwin.data.pur_grp_cd == "EGF020"){
				$(".group44_srv").wq("setStyle", "display", "none");
			}
			grp_bid_prcs.hide(); // 입찰집행
			grp_bid_limit.hide(); // 투찰제한 일반
			grp_loc_limit.hide(); // 투찰제한 - 지역제한
			grp_type_limit.hide(); // 투찰제한 - 업종제한

			if(scwin.data.pur_grp_cd == "EGF030"){
				grp_const_clsf.hide();	
			}
		}else if(scwin.data.checkBidSucc == "Y-PRB"){
			grp_contrct.hide();
			grp_bid_prcs.hide(); // 입찰집행
			grp_bid_limit.hide(); // 투찰제한 일반
			grp_loc_limit.hide(); // 투찰제한 - 지역제한
			grp_type_limit.hide(); // 투찰제한 - 업종제한	

			if(scwin.data.pur_grp_cd == "EGF030"){
				grp_const_clsf.hide();	
			}
		}else{
			grp_contrct.show(); // 계약 및 입찰방식
			$(".group44").wq("setStyle", "display", "table-row");  // 낙찰방법, PQ심사여부 입찰설명회 여부(장소포함) 등			
			if(scwin.data.pur_grp_cd == "EGF020"){
				$(".group44_srv").wq("setStyle", "display", "table-row");
			}
			grp_bid_prcs.show();
			grp_bid_limit.show();

			if(scwin.data.pur_grp_cd == "EGF030"){
				grp_const_clsf.show();	
			}
		}	
	};	

	/*
     * 입찰서 접수마감일시 변경 후 날짜 셋팅
     */
	scwin.chgTender = function(clsf){
		// 입찰서접수개시일시
		var endYmd = ica_bid_end_ymd.getValue();
		var endHour = ibx_bid_end_hour.getValue();

		var tmpYmd = scwin.afterThisDate(endYmd, -1);		
		var tmpHour = endHour * 1 + 1;

		// 개찰일자
		ica_open_ymd.setValue(endYmd);
		ibx_open_hour.setValue(tmpHour);
		ibx_open_min.setValue(ibx_bid_end_min.getValue());

		// 입찰참가등록마감일시
		ica_bid_apply_end_ymd.setValue(tmpYmd);
		ibx_bid_apply_end_hour.setValue("18");
		ibx_bid_apply_end_min.setValue(ibx_bid_end_min.getValue());

		// 보증서접수마감일시
		ica_bid_gurnt_fin_ymd.setValue(tmpYmd);	
		ibx_bid_gurnt_fin_hour.setValue("18");
		ibx_bid_gurnt_fin_min.setValue(ibx_bid_end_min.getValue());
	};

	/*
     * 구매요구내역 그리드 셋팅 및 구매요구내역 합계금액 구하기
     */
	scwin.setStylePurItem = function(flag){
		var reqAmt = 0;
		var addTax = 0;
		var incidntlExpns = 0;
		var reqAmtSum = 0;

		for(var i=0; i<dlt_item.getRowCount(); i++){
			grd_item.setCellBackgroundColor(i, "item_cd", "#fefea6 !important");
			grd_item.setCellBackgroundColor(i, "delvr_demnd_daycnt", "#fefea6 !important");
			grd_item.setCellBackgroundColor(i, "delvr_plc_nm", "#fefea6 !important");
			grd_item.setCellColor(i, "cntst_prod_yn", "red");

			if(flag == "new"){
				grd_item.setCellReadOnly(i, "chk", true);
				var tempStr = "계약일로부터 " + dlt_item.getCellData( i , "delvr_demnd_daycnt" ) + "일 이내";
				dlt_item.setCellData( i , "delvr_demnd_daycnt",  tempStr);

				dlt_item.setCellData(i, "cud_type", "C");
			}

			reqAmt += Number(dlt_item.getCellData( i , "req_amt" ));
			addTax += Number(dlt_item.getCellData( i , "req_time_addtax_extexpns" ));
			incidntlExpns += Number(dlt_item.getCellData( i , "incidntl_expns" ));
			reqAmtSum += Number(dlt_item.getCellData( i , "req_amt_sum" ));

			if(dlt_item.getCellData(i, "cntst_prod_yn") == "Y"){
				spn_cntst_prod.show("");	
			}
		}

		ibx_reqAmtSum.setValue(reqAmt); // 소 계(추정금액)
		ibx_addTaxSum.setValue(addTax); // 부가세
		ibx_incidntlExpns.setValue(incidntlExpns); // 부대비
		ibx_totlReqAmt.setValue(reqAmtSum); // 총합

		if(flag == "new_add" || flag == "new"){
			ibx_assign_amount.setValue(Math.round(reqAmtSum - incidntlExpns)); // 배정예산
			ibx_budget_amt.setValue(Math.round(reqAmt)); // 추정가격

			if (WebSquare.util.getNumber(ibx_assign_amount.getValue()) < 22000000) { // 품목 합이 2,200만원 미만이면 비예가
				rdo_multi_esti_pr_yn.setValue( "0" );
			}
			if (!/show|modify|renoti/.test(scwin.data.ord_flag)){
				ibx_bid_title.setValue( dlt_item.getCellData(0, "item_nm"));
			}
		}
	};

	/*
	* 날짜 더하기(기준일자, 더할 날짜)
	*/
	scwin.afterThisDate = function(stdYmd, dd) {		
		return com.addDate(stdYmd, dd);
	};		

	/*
     * 전문 계약 방법 세팅 (A- 최저가 입찰, B-기술가격 분리 동시, C-협상에 의한 계약, D-적격심사)
     */
	scwin.setContrctType = function(){
		var contrctType = "";

		if(scwin.data.pur_grp_cd == "EGF010"){
			switch(sbx_bid_succ_method.getValue()){
				case "1100": contracType = "A"; break; // 일반(총액)
				case "1102": contracType = "B"; break; // 일반(총액)규격가격동시
				case "1107": contracType = "C"; break; // 일반(총액)협상에의한계약
	//			case "1200": contracType = "A"; break; // 일반(단가)
	//			case "1202": contracType = "B"; break; // 일반(단가)규격가격동시
	//			case "1207": contracType = "C"; break; // 일반(단가)협상에의한계약
				case "2100": contracType = "A"; break; // 제한(총액)
				case "2102": contracType = "B"; break; // 제한(총액)규격가격동시
				case "2107": contracType = "C"; break; // 제한(총액)협상에의한계약
				case "2114": contracType = "B"; break; // 제한(총액) P.Q
		//		case "2200": contracType = "A"; break; // 제한(단가)
		//		case "2202": contracType = "B"; break; // 제한(단가)규격가격동시
		//		case "2207": contracType = "C"; break; // 제한(단가)협상에의한계약
		//		case "2214": contracType = "B"; break; // 제한(단가) P.Q
				case "3100": contracType = "A"; break; // 지명(총액)
				case "3102": contracType = "B"; break; // 지명(총액)규격가격동시
				case "3107": contracType = "C"; break; // 지명(총액)협상에의한계약
				case "3114": contracType = "B"; break; // 지명(총액) P.Q
		//		case "3200": contracType = "A"; break; // 지명(단가)
		//		case "3207": contracType = "C"; break; // 지명(단가)협상에의한계약
		//		case "3214": contracType = "B"; break; // 지명(단가) P.Q
				case "4100": contracType = "A"; break; // 수의(총액)
				case "4101": contracType = "A"; break; // 수의(총액)소액수의
				case "4106": contracType = "A"; break; // 수의(총액)일반수의
				case "4124": contracType = "A"; break; // 수의(총액)소액-견적입찰
		//		case "4200": contracType = "A"; break; // 수의(단가)
		//		case "4201": contracType = "A"; break; // 수의(단가)소액수의
		//		case "4206": contracType = "A"; break; // 수의(단가)일반수의
				default: contracType = "A"; break;
			}
		} else if(scwin.data.pur_grp_cd == "EGF020"){
			if(sbx_service_type_cd.getValue() == "2"){ // 기술용역
				switch( sbx_succ_type.getValue()){
					case "1" : contracType = "A"; break; // 제한최저
					case "1-1" : contracType = "A"; break; // 수의(견적제출)
					case "21" : contracType = "A"; break; // 최저가낙찰제
					case "3-4" : contracType = "D"; break; // 추정가격 5억미만 고시금액이상(건축사법에 따른 설계 5억미만 1억이상)
					case "7-7" : contracType = "D"; break; // 추정가격 고시금액 이상 5억미만 건설사업관리(TP심사포함)
					case "8" : contracType = "D"; break; // 추정가격 5억이상10억미만 건설사업관리(TP심사포함)
					default : contracType = "A"; break;
				}
			}else{ // 일반용역
				switch(sbx_bid_succ_method.getValue()){
					case "1100" : contracType = "A"; break;	// 일반(총액)
					case "1102" : contracType = "B"; break;	// 일반(총액)규격가격동시
					case "1107" : contracType = "C"; break;	// 일반(총액)협상에의한계약
					case "1200" : contracType = "A"; break;	// 일반(단가)
					case "1202" : contracType = "B"; break;	// 일반(단가)규격가격동시
					case "1207" : contracType = "C"; break;	// 일반(단가)협상에의한계약
					case "2100" : contracType = "A"; break;	// 제한(총액)
					case "2102" : contracType = "B"; break;	// 제한(총액)규격가격동시
					case "2107" : contracType = "C"; break;	// 제한(총액)협상에의한계약
					case "2114" : contracType = "B"; break;	// 제한(총액) P.Q
					case "2200" : contracType = "A"; break;	// 제한(단가)
					case "2202" : contracType = "B"; break;	// 제한(단가)규격가격동시
					case "2207" : contracType = "C"; break;	// 제한(단가)협상에의한계약
					case "2214" : contracType = "B"; break;	// 제한(단가) P.Q
					case "3100" : contracType = "A"; break;	// 지명(총액)
					case "3102" : contracType = "B"; break;	// 지명(총액)규격가격동시
					case "3107" : contracType = "C"; break;	// 지명(총액)협상에의한계약
					case "3114" : contracType = "B"; break;	// 지명(총액) P.Q
					case "3200" : contracType = "A"; break;	// 지명(단가)
					case "3207" : contracType = "C"; break;	// 지명(단가)협상에의한계약
					case "3214" : contracType = "B"; break;	// 지명(단가) P.Q
					case "4100" : contracType = "A"; break;	// 수의(총액)
					case "4101" : contracType = "A"; break;	// 수의(총액)소액수의
					case "4106" : contracType = "A"; break;	// 수의(총액)일반수의
					case "4200" : contracType = "A"; break;	// 수의(단가)
					case "4201" : contracType = "A"; break;	// 수의(단가)소액수의
					case "4206" : contracType = "A"; break;	// 수의(단가)일반수의					
					default : contracType = "A"; break;
				}
			}
		}else if(scwin.data.pur_grp_cd == "EGF030"){ // 공사
			switch( sbx_succ_type.getValue()){
				case "10" : contracType = "A"; break;
				case "11-11" : contracType = "D"; break; // 추정가격 8천만원미만(전기통신,소방시설,문화재 등)
				case "11-21" : contracType = "D"; break; // 추정가격 1억원미만(전문)
				case "11-31" : contracType = "D"; break; // 추정가격 2억원미만(일반)
				case "13-11" : contracType = "D"; break; // 추정가격 3억원미만 8천만원이상(전기통신,소방시설,문화재 등)
				case "13-21" : contracType = "D"; break; // 추정가격 3억원미만 1억원이상(전문)
				case "13-31" : contracType = "D";; break; // 추정가격 3억원미만 2억원이상(일반)
				case "15-1" : contracType = "D"; break; // 추정가격 10억원미만 3억원이상(일반)
				case "17-21" : contracType = "D"; break; // 추정가격 50억원미만 3억원이상(전문,전기통신,소방시설,문화재 등)
				case "17-3" : contracType = "D"; break; // 추정가격 50억원미만 10억원이상(일반)
				case "18-1" : contracType = "D"; break; // 추정가격 100억원미만 50억원이상
				case "19-2" : contracType = "D"; break; // 추정가격 300억원미만 100억원이상
				case "19-3" : contracType = "D"; break; // 추정가격 300억미만 200억이상(PQ대상)
				case "20" : contracType = "A"; break;
				case "60" : contracType = "A"; break;
				case "70" : contracType = "A"; break;				
				default : contracType = "A"; break;
			}
		}

		return contracType;
	};	

	/*
     * 일시구하기
     */
	scwin.getBidYmd = function(clsf){
		var ymd = "";
		switch(clsf){
			case "expl" :
				if(ica_bid_expl_ymd.getValue() != ""){
					ymd = ica_bid_expl_ymd.getValue() + com.lpad(ibx_bid_expl_hour.getValue(), 2, "0") + com.lpad(ibx_bid_expl_min.getValue(), 2, "0");
				}
				break;
			case "start" : 
				ymd = (ica_bid_start_ymd.getValue() == "")? "": (ica_bid_start_ymd.getValue() + com.lpad(ibx_bid_start_hour.getValue(), 2, "0") + com.lpad(ibx_bid_start_min.getValue(), 2, "0"));
				break;
			case "end" : // 입찰서접수마감일시
				ymd = (ica_bid_end_ymd.getValue() == "")? "": (ica_bid_end_ymd.getValue() + com.lpad(ibx_bid_end_hour.getValue(), 2, "0") + com.lpad(ibx_bid_end_min.getValue(), 2, "0"));
				break;
			case "open" :
				ymd = (ica_open_ymd.getValue() == "")?"": (ica_open_ymd.getValue() + com.lpad(ibx_open_hour.getValue(), 2, "0") + com.lpad(ibx_open_min.getValue(), 2, "0"));
				break;
			case "apply" : // 입찰참가등록마감일시
				if(ica_bid_apply_end_ymd.getValue() != ""){
					ymd = ica_bid_apply_end_ymd.getValue() + com.lpad(ibx_bid_apply_end_hour.getValue(), 2, "0") + com.lpad(ibx_bid_apply_end_min.getValue(), 2, "0");
				}
				break;
			case "gurnt" : // 입찰보증서접수마감일시
				if(ica_bid_gurnt_fin_ymd.getValue() != ""){
					ymd = ica_bid_gurnt_fin_ymd.getValue() + com.lpad(ibx_bid_gurnt_fin_hour.getValue(), 2, "0") + com.lpad(ibx_bid_gurnt_fin_min.getValue(), 2, "0");
				}
				break;
			case "agr" : // 공동수급협정서 접수마감일시
				if(ica_join_agr_fin_ymd.getValue() != ""){
					ymd = ica_join_agr_fin_ymd.getValue() + com.lpad(ibx_join_fin_hour.getValue(), 2, "0") + com.lpad(ibx_join_fin_min.getValue(), 2, "0");
				}
				break;
			case "last" : // 공고마감일시
				if(ica_noti_last_ymd.getValue() != ""){
					ymd = ica_noti_last_ymd.getValue() + com.lpad(ibx_noti_last_hour.getValue(), 2, "0") + com.lpad(ibx_noti_last_min.getValue(), 2, "0");
				}
				break;
			case "pq" : // PQ심사신청서 접수기한
				if(ica_pq_eval_rcp_end_ymd.getValue() != ""){
					ymd = ica_pq_eval_rcp_end_ymd.getValue() + com.lpad(ibx_pq_eval_rcp_end_hour.getValue(), 2, "0") + com.lpad(ibx_pq_eval_rcp_end_min.getValue(), 2, "0");
				}
				break;
		}

		return ymd;
	};

	/*
     * 날짜 셋팅
     */ 
	scwin.setBidYmd = function(){
		// 입찰설명회 일시
		dma_ord.set("bid_expl_ymd", scwin.getBidYmd("expl"));
		// 입찰서접수개시일시
		dma_ord.set("bid_start_ymd", scwin.getBidYmd("start"));
		// 입찰서접수 마감일시
		dma_ord.set("bid_end_ymd", scwin.getBidYmd("end"));
		// 개찰일시
		dma_ord.set("open_ymd", scwin.getBidYmd("open"));
		// 입찰참가등록 마감일시
		dma_ord.set("bid_apply_end_ymd", scwin.getBidYmd("apply"));
		// 입찰보증서접수 마감일시
		dma_ord.set("bid_gurnt_fin_ymd", scwin.getBidYmd("gurnt"));
		// 공동수급협정서 접수마감일시
		dma_ord.set("join_agr_fin_ymd", scwin.getBidYmd("agr"));
		// PQ심사신청서 접수기한
		dma_ord.set("pq_eval_rcp_end_ymd", scwin.getBidYmd("pq"));
	};

	/*
     * 비연계 소액수의 직찰이 아닐 경우 체크
     */
	scwin.checkBidYmd = function(){		
		if(ica_bid_start_ymd.getValue() == "" || ibx_bid_start_hour.getValue() == "" || ibx_bid_start_min.getValue() == ""){
			alert("입찰서접수개시일시를 입력해 주십시요.");
			return false;
		}

		if(ica_bid_end_ymd.getValue() == "" || ibx_bid_end_hour.getValue() == "" || ibx_bid_end_min.getValue() == ""){
			alert("입찰서접수마감일시를 입력해 주십시요.");
			return false;
		}

		if(ica_open_ymd.getValue() == "" || ibx_open_hour.getValue() == "" || ibx_open_min.getValue() == ""){
			alert("개찰일시를 입력해 주십시요.");
			return false;
		}

		if(ica_bid_apply_end_ymd.getValue() == "" || ibx_bid_apply_end_hour.getValue() == "" || ibx_bid_apply_end_min.getValue() == ""){
			alert("입찰참가신청마감일시를 입력해 주십시요.");
			return false;
		}	

		if(ica_bid_gurnt_fin_ymd.getValue() == "" || ibx_bid_gurnt_fin_hour.getValue() == "" || ibx_bid_gurnt_fin_min.getValue() == ""){
			alert("입찰보증서접수마감일시를 입력해 주십시요.");
			return false;
		}

		if(sbx_bid_expl_yn.getValue() == "2" || sbx_bid_expl_yn.getValue() == "3"){ // 입찰설명회
			var msg = (scwin.data.pur_grp_cd == "EGF030")? "현장설명회" : "입찰설명회";
			if(ica_bid_expl_ymd.getValue() == "" || ibx_bid_expl_hour.getValue() == "" || ibx_bid_expl_min.getValue() == ""){
				alert( msg + " 일시를 입력해 주십시요.");
				return false;
			}

			if(ica_bid_expl_ymd.getValue() < ica_noti_ymd.getValue()){
				alert(msg + "날짜는 공고일이후여야 합니다.");
				return false;
			}			
		}

		// 공동수급협정서
		var bidEndYmd = scwin.getBidYmd("end"); // 입찰서접수마감일시
		if(sbx_join_possible_yn.getValue() != "N"){			
			if(ica_join_agr_fin_ymd.getValue() == "" || ibx_join_fin_hour.getValue() == "" || ibx_join_fin_min.getValue() == ""){
				alert("공동수급협정서 접수마감일시를 입력해 주십시요.");
				return false;
			}

			// 공동수급협정서 유효성 체크
			if (bidEndYmd  < scwin.getBidYmd("agr") ) {
				alert("입찰서접수마감일시는 공동수급협정서 접수마감일시 이후여야 합니다.");
				return false;
			}
		}

		var openYmd = scwin.getBidYmd("open"); // 개찰일시
		var bidStartYmd = scwin.getBidYmd("start"); // 입찰서접수개시일시
		var bidApplyEndYmd = scwin.getBidYmd("apply"); //  입찰참가등록마감 일시

		if (openYmd  <  bidApplyEndYmd) {
			alert("개찰일시는 입찰참가등록마감 일시  이후여야 합니다.");
			return false;
		}

		if ( openYmd <  bidStartYmd) {
			alert("개찰일시는 입찰서접수개시일시 이후여야 합니다.");
			return false;
		}

		if (bidEndYmd < bidStartYmd) {
			alert("개찰일시는 입찰서접수개시일시 이후여야 합니다.");
			return false;
		}

		if( bidApplyEndYmd < bidStartYmd ) {
			alert("입찰참가등록마감일시는 입찰서접수개시일시  이후여야 합니다.");
			return false;
		}

		if(bidStartYmd < ica_noti_ymd.getValue()){
			alert("입찰서접수개시일은 공고일자 이후여야 합니다.");
			return false;
		}

		if(openYmd < ica_noti_ymd.getValue()){
			alert("개찰일자는 공고일자 이후여야 합니다.");
			return false;
		}

		if(scwin.data.pur_grp_cd == "EGF020"){
			if(bidEndYmd < bidApplyEndYmd){
				alert("입찰서접수마감일시는 입찰참가등록마감일시 이후여야 합니다.");
				return false;
			}
		}

		return true;		
	};

	/*
     * 저장전 체크
     */ 
	scwin.pre_save = function(){
		// 전문 계약 방법 세팅 (A- 최저가 입찰, B-기술가격 분리 동시, C-협상에 의한 계약, D-적격심사)
		dma_ord.set("contract_type_cd", scwin.setContrctType());	

		// 낙찰자 선정방법
		dma_ord.set("bid_succ_method", sbx_bid_succ_method.getValue());
		// 낙찰방법
		dma_ord.set("succ_type", sbx_succ_type.getValue());		
		// 공고마감일시
		if(ica_noti_last_ymd.getValue() != ""){
			dma_ord.set("noti_last_ymd", scwin.getBidYmd("last"));
		}	

		if(dlt_item.getRowCount() == 0){
			alert("구매의뢰내역을 추가해 주십시요.");
			return false;
		}

		if(!com.validateData(dlt_item,[
			{ id : "item_cd", mandatory : true}
		], false)) return false;	

		var validData =[
			{ id : "bid_clsf_cd", mandatory : true},			
			{ id : "budget_amt", valFunc : function(value){
					if(com.isEmpty(value) || WebSquare.util.parseInt(value, 0) == 0){
						return "추정가격을 입력하세요";
					}
				}
			},
			{ id : "assign_amount", mandatory : true}, // 배정예산
			{ id : "exe_guar_rate", mandatory : true}, // 하자보증율
			{ id : "exe_guar_period", mandatory : true}, // 하자보증기간
			{ id : "delay_amt_rate", mandatory : true}, // 지체상금율
			{ id : "rebid_possible_yn", mandatory : true}
		];

		if(scwin.data.pur_grp_cd == "EGF020"){
			validData.push({ id : "service_type_cd", mandatory : true});
		}

		if(sbx_bid_clsf_cd.getValue() == "PRB010"){ // 연계입찰
			validData.push({ id : "bid_title", mandatory : true});
			validData.push({ id : "noti_ymd", mandatory : true});
			validData.push({ id : "noti_last_ymd", mandatory : true});
		}

		if(rdo_multi_esti_pr_yn.getValue() == "2"){ // 복수예가
			validData.push({ id : "desing_amt", mandatory : true}); // 기초금액
		}else if(rdo_multi_esti_pr_yn.getValue() == "1"){
			validData.push({ id : "esti_online_yn", mandatory : true});
		}

		 // 중앙조달, 나라장터쇼핑몰(Y-PRB) 일 경우 '계약 및 입찰방식', '입찰진행' 입력안함
		if(!/PRB020|PRB030/.test(sbx_bid_clsf_cd.getValue())){
			validData.push({ id : "contrct_method", mandatory : true});
			validData.push({ id : "bid_succ_type", mandatory : true}); // 입찰방식

			if (sbx_bid_succ_type.getValue() == "00" ) { // 직찰
				if (sbx_bid_clsf_cd.getValue() != "PRB040" ) {
					alert("직찰일 경우에는 비연계로 진행 됩니다.");
					sbx_bid_clsf_cd.setValue( "PRB040" );
				}
			}

			if(rdo_pq_eval_yn.getValue() == "Y"){
				validData.push({ id : "pq_score", mandatory : true});
			}

			if(scwin.data.checkBidSucc != "Y"){ // 비연계 소액수의 직찰이 아닐 경우
				validData.push({ id : "join_possible_yn", mandatory : true}); // 공동수급협성서 접수 및 구성방식
				validData.push({ id : "limit_loc", mandatory : true}); // 지역제한
				validData.push({ id : "limit_category", mandatory : true});
				validData.push({ id : "same_suc_lot_program_yn", mandatory : true}); // 동가입찰 낙찰자 자동추첨프로그램

				if(scwin.data.pur_grp_cd == "EGF010"){
					validData.push({ id : "item_limit_yn", mandatory : true}); // 물품분류제한여부
				}

				if(scwin.data.pur_grp_cd == "EGF030"){
					validData.push({ id : "his_bid_yn", mandatory : true}); // 내역입찰여부
				}else{
					validData.push({ id : "join_mem_loc_limit_yn", mandatory : true}); // 공동수급체 구성원 지역제한적용여부
				}

				// 공동수급협정서
				if(sbx_join_possible_yn.getValue() != "N"){
					validData.push({ id : "join_method", mandatory : true}); // 공동수급방법			
				}

				if(sbx_bid_expl_yn.getValue() == "2" || sbx_bid_expl_yn.getValue() == "3"){ // 입찰설명회
					validData.push({ id : "bid_expl_loc", mandatory : true});							
				}
				// 날짜 체크
				if(!scwin.checkBidYmd()) return false;		

				if(sbx_limit_loc.getValue() == "Y"){
					if(dlt_limit_loc.getRowCount() == 0){
						alert("지역제한 데이터가 1건 이상이여야 합니다.");
						return false;
					}
				}	

				if(sbx_limit_category.getValue() == "Y"){
					if(dlt_limit_ctg.getRowCount() == 0){
						alert("업종제한 데이터가 1건 이상이여야 합니다.");
						return false;
					}
				}
			} // 비연계 소액수의 직찰이 아닐 경우 종료

			if(sbx_bid_succ_method.getValue() == "4101"){ // 물품, 용역
				validData.push({ id : "delvr_ymd", mandatory : true});
			}else if(sbx_contrct_method.getValue() == "42050"){ // 공사
				validData.push({ id : "delvr_ymd", mandatory : true});
			}			
		} // PRB020|PRB030 아닌 경우 종료		

		if(!com.validateData(dma_ord, validData, false)) return false;	

		return true;	
	};

	/*
     * 저장
     */ 
	scwin.save = function(){
		if(scwin.data.ord_flag == "renoti"){			
			dma_ord.set("rebid_ord_no", scwin.data.bid_ord_no);
			dma_ord.set("rebid_no", scwin.data.chg_no);

			dma_ord.set("chg_no", "00");
		}else{
			dma_ord.set("rebid_ord_no", "");
			dma_ord.set("rebid_no", "");
		}

		dma_ord.set("check_bid_succ", scwin.data.checkBidSucc);
		dma_ord.set("req_addtax", ibx_addTaxSum.getValue());

		// 날짜셋팅
		scwin.setBidYmd();

		com.file.transport( function( attachObj){
			dma_ord.set("estipr_attach_file_no", attachObj.upload_A); // 원가계산 근거 첨부파일 번호
			dma_ord.set("attach_file_no", attachObj.upload_B);
			var attachList = [];
			if(attachObj.upload_A != "") attachList.push(attachObj.upload_A);
			if(attachObj.upload_B != "") attachList.push(attachObj.upload_B)
			dma_ord.set("validAttachNo", attachList.join(","));

			com.execute(sbm_saveData, {
				successCallback : function(e) { // 성공 콜백
					if(dma_ord.get("approval") == "true"){
						// 결재화면 이동
						com.movePageMenu("A001", { "reqNo" :  dma_ord.get("elec_docu_no")});
					}else{
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							scwin.data.saveFlag = true;
							scwin.data.ord_flag = "";
							scwin.search();
						});	
					}				
				}
			});
		});
	};

	/*
     * 구매품목데이터 중복여부 체크
     */
	scwin.checkDupData = function(rowData){
		for(var j=0; j < dlt_item.getRowCount(); j++){
			if(dlt_item.getCellData(j, "pur_req_no") == rowData.pur_req_no && dlt_item.getCellData(j,"pur_req_item_odr") == rowData.pur_req_item_odr){
				alert(rowData.item_nm + "의 구매요구건은 이미 구매의뢰내역에 추가 되었습니다.");
				return false;
			}			
		}

		return true;
	};

	/*
     * 지명업체 중복여부 체크
     */
	scwin.checkVendAdd = function(data){
		for(var j=0; j < dlt_bid_vend.getRowCount(); j++){
			if(dlt_bid_vend.getCellData(j, "bsns_psn_regst_no") == rowData.bsns_psn_regst_no){
				alert(ret.vend_nm + "는 이미 지명업체에 추가 되었습니다.");
				return false;
			}			
		}

		return true;
	};

	/*
     * 발주도우미 선택시 입찰서접수개시일자 변경 후 날짜변경
     */
	scwin.setYmdByBidStart = function(){
		var ordHelper = sbx_ord_helper.getValue();
		var bidStartYmd = ica_bid_start_ymd.getValue();
		switch(ordHelper){
			case "PRE040" :  // 협상에 의한 계약
				ica_bid_end_ymd.setValue( scwin.afterThisDate(bidStartYmd, 11)); // 입찰서 접수마감일자
				ica_open_ymd.setValue( ica_bid_apply_end_ymd.getValue()); // 개찰일자
				ica_bid_apply_end_ymd.setValue(scwin.afterThisDate(ica_bid_apply_end_ymd.getValue(), -1)); // 입찰참가등록마감일자
				ica_bid_gurnt_fin_ymd.setValue( scwin.afterThisDate(ica_bid_apply_end_ymd.getValue(), -1)); // 입찰보증서접수마감일자

				ibx_open_hour.setValue(com.lpad(ibx_bid_start_hour.getValue(), 2, "0"));
				ibx_bid_apply_end_hour.setValue("18");
				ibx_bid_gurnt_fin_hour.setValue("18");

			case "PRE050" : // 규격가격동시
			case "PRE060" : // 적격심사
				ica_bid_end_ymd.setValue( scwin.afterThisDate(bidStartYmd, 8)); // 입찰서 접수마감일자
				ica_open_ymd.setValue( ica_bid_apply_end_ymd.getValue()); // 개찰일자
				ica_bid_apply_end_ymd.setValue(scwin.afterThisDate(ica_bid_apply_end_ymd.getValue(), -1)); // 입찰참가등록마감일자
				ica_bid_gurnt_fin_ymd.setValue( scwin.afterThisDate(ica_bid_apply_end_ymd.getValue(), -1)); // 입찰보증서접수마감일자

				ibx_open_hour.setValue(com.lpad(ibx_bid_start_hour.getValue(), 2, "0"));
				ibx_bid_apply_end_hour.setValue("18");
				ibx_bid_gurnt_fin_hour.setValue("18");
			case "PRE010" : // 특정구매
				ica_bid_end_ymd.setValue(bidStartYmd); // 입찰서 접수마감일자
				ica_open_ymd.setValue( ica_bid_apply_end_ymd.getValue()); // 개찰일자
				ica_bid_apply_end_ymd.setValue(bidStartYmd); // 입찰참가등록마감일자
				ica_bid_gurnt_fin_ymd.setValue(bidStartYmd); // 입찰보증서접수마감일자

				ibx_open_hour.setValue(com.lpad(ibx_bid_start_hour.getValue(), 2, "0"));

				ibx_bid_apply_end_hour.setValue(com.lpad(ibx_bid_start_hour.getValue(), 2, "0"));
				ibx_bid_apply_end_min.setValue(com.lpad(ibx_bid_start_min.getValue(), 2, "0"));

				ibx_bid_gurnt_fin_hour.setValue(com.lpad(ibx_bid_start_hour.getValue(), 2, "0"));
				ibx_bid_gurnt_fin_min.setValue(com.lpad(ibx_bid_start_min.getValue(), 2, "0"));
		}
	};

	/*
     * 조회후 날짜 셋팅
     */
	scwin.setFormYmd = function(){
		let temp = '';

		temp = dma_ord.get("bid_expl_ymd").trim();  // 입찰설명회 일시
	    if(temp != null && temp != "" && temp.length == 12){
	      ica_bid_expl_ymd.setValue( scwin.dateToText(temp, "Date") );
	      ibx_bid_expl_hour.setValue( scwin.dateToText(temp, "Hour") );
	      ibx_bid_expl_min.setValue( scwin.dateToText(temp, "Min") );
	    }

	    temp = dma_ord.get("bid_apply_end_ymd").trim(); // 참가신청서 제출기간 종료일시
	    if(temp != null && temp != "" && temp.length == 12){
	      ica_bid_apply_end_ymd.setValue( scwin.dateToText(temp, "Date") );
	      ibx_bid_apply_end_hour.setValue( scwin.dateToText(temp, "Hour") );
	      ibx_bid_apply_end_min.setValue( scwin.dateToText(temp, "Min") );
	    }

	    temp = dma_ord.get("bid_gurnt_fin_ymd").trim(); // 입찰보증서접수마감일시
	    if(temp != null && temp != "" && temp.length == 12){
	      ica_bid_gurnt_fin_ymd.setValue( scwin.dateToText(temp, "Date") );
	      ibx_bid_gurnt_fin_hour.setValue( scwin.dateToText(temp, "Hour") );
	      ibx_bid_gurnt_fin_min.setValue( scwin.dateToText(temp, "Min") );
	    }

	    temp = dma_ord.get("bid_start_ymd").trim(); // 입찰서 제출기간 시작일시
	    if(temp != null && temp != "" && temp.length == 12){
	      ica_bid_start_ymd.setValue( scwin.dateToText(temp, "Date") );
	      ibx_bid_start_hour.setValue( scwin.dateToText(temp, "Hour") );
	      ibx_bid_start_min.setValue( scwin.dateToText(temp, "Min") );
	    }

	    temp = dma_ord.get("bid_end_ymd").trim(); // 입찰서 제출기간 종료일시
	    if(temp != null && temp != "" && temp.length == 12){
	      ica_bid_end_ymd.setValue( scwin.dateToText(temp, "Date") );
	      ibx_bid_end_hour.setValue( scwin.dateToText(temp, "Hour") );
	      ibx_bid_end_min.setValue( scwin.dateToText(temp, "Min") );
	    }

	    temp = dma_ord.get("open_ymd").trim();  // 개찰일
	    if(temp.length == 12 ){
	      ica_open_ymd.setValue( scwin.dateToText(temp, "Date") );
	      ibx_open_hour.setValue( scwin.dateToText(temp, "Hour") );
	      ibx_open_min.setValue( scwin.dateToText(temp, "Min") );
	    }

	    // 공동수급협정서 접수마감일시 
	    temp = dma_ord.get("join_agr_fin_ymd").trim();  
	    if( temp != "" && temp != null && temp.length == 12){
	      ica_join_agr_fin_ymd.setValue( scwin.dateToText(temp, "Date") );
	      ibx_join_fin_hour.setValue( scwin.dateToText(temp, "Hour") );
	      ibx_join_fin_min.setValue( scwin.dateToText(temp, "Min") );
	    }

	    //공고마감일시
	    temp = dma_ord.get("noti_last_ymd").trim(); // 입찰설명회 일시
	    if(temp != null && temp != "" && temp.length == 12){
	      ica_noti_last_ymd.setValue( scwin.dateToText(temp, "Date") );
	      ibx_noti_last_hour.setValue( scwin.dateToText(temp, "Hour") );
	      ibx_noti_last_min.setValue( scwin.dateToText(temp, "Min") );
	    }

	    if(scwin.data.pur_grp_cd == "EGF020"){
		    // PQ심사신청서 접수기한	    
		    temp = dma_ord.get("pq_eval_rcp_end_ymd").trim(); 
		    if(temp != null && temp != "" && temp.length == 12){
		      ica_pq_eval_rcp_end_ymd.setValue( scwin.dateToText(temp, "Date") );
		      ibx_pq_eval_rcp_end_hour.setValue( scwin.dateToText(temp, "Hour") );
		      ibx_pq_eval_rcp_endmin.setValue( scwin.dateToText(temp, "Min") );
		    }
		}
	};

	/*
     * 발주계획(용역)일 경우 낙찰방법 셋팅
     */
	scwin.setSuccType = function(){
		if(sbx_service_type_cd.getValue() == "2"){ // 기술용역
			if(rdo_succ_pivot.getValue() == "1"){ // 조달청 기준
				com.biz.getG2bComCd({upper_cd:"Z10", compID : "sbx_succ_type" }); 
			}else{
				com.biz.getG2bComCd({upper_cd:"ET2", compID : "sbx_succ_type" }); 
			}
		}else{ // 일반용역
			if(rdo_succ_pivot.getValue() == "1"){ // 조달청 기준
				com.biz.getG2bComCd({upper_cd:"Z46", compID : "sbx_succ_type" }); 
			}else{
				com.biz.getG2bComCd({upper_cd:"ET2", compID : "sbx_succ_type" }, function(){
					dlt_cmnCd_ET2.setRowJSON(2, {cd : "13", cd_nm : "협상에 의한 낙찰제"});
				}); 
			}
		}
	};

	/*
