		}
	};	

	/*
	* 저장 버튼 클릭 이벤트
	*/
	scwin.btn_save_onclick = function(e) {		
		if(!scwin.pre_save()) return;		

		var str = "";
		var ContracTypeNm = "";
		// 단일예가일 경우만 예가 온라인 여부 표기
		if(rdo_multi_esti_pr_yn.getValue() == "1"){
			str = "예가방식 : " + rdo_multi_esti_pr_yn.getText() + "\n"+
				  "예가 온라인 여부 : " + rdo_esti_online_yn.getText() + "\n";
		}else{
			str = "예가방식 : " + rdo_multi_esti_pr_yn.getText() + "\n";
		}

		// 전문 계약 방법 세팅 (A- 최저가 입찰, B-기술가격 분리 동시, C-협상에 의한 계약, D-적격심사)
		switch(dma_ord.get("contract_type_cd")){
			case "A": ContracTypeNm = "최저가 입찰"; break;
			case "B": ContracTypeNm = "기술가격 분리 동시"; break;
			case "C": ContracTypeNm = "협상에 의한 계약"; break;
			case "D": ContracTypeNm = "적격심사"; break;
			default : ContracTypeNm = "최저가 입찰"; break;
		}

		var type = "";
		switch(scwin.data.pur_grp_cd){
			case "EGF010" : type="(물품)"; break;	
			case "EGF020" : type="(용역)"; break;	
			case "EGF030" : type="(공사)"; break;	
		}

		var msg = type + "발주계획 내역을 다시 확인하시기 바랍니다. \n\n"+
					"공고진행방법 : " + sbx_bid_clsf_cd.getText() + "\n"+
					"공고명 : " + ibx_bid_title.getValue() + "\n"+
					"입찰공고일 : "+ com.formatDate(ica_noti_ymd.getValue(), "dash") +"\n"+
					"배정예산 : " + com.formatNumber(ibx_assign_amount.getValue()) + "\n"+
					"추정가격 : " + com.formatNumber(ibx_budget_amt.getValue()) + "\n" + str;
		if (scwin.data.checkBidSucc != "Y-PRB"){
			msg += "계약진행방법 : " + ContracTypeNm + "\n";
			if(sbx_service_type_cd.getValue() == "2" || scwin.data.pur_grp_cd == "EGF030"){
				msg += "계약방법 : " + sbx_contrct_method.getText() + "\n";
			}else{
				msg += "계약방법 : " + sbx_contrct_method.getText() +" / "+sbx_bid_succ_method.getText() + "\n";
			}

			msg += "입찰방식 : " + sbx_bid_succ_type.getText() + "\n" ;
		}
		if(scwin.data.checkBidSucc == "N"){
			msg += "낙찰방법 : " + sbx_succ_type.getText() + "\n" +
					"지역제한여부 : " + sbx_limit_loc.getText() + "\n"+
					"업종제한여부 : " + sbx_limit_category.getText() + "\n"+
					"입찰서 접수개시일시 : "+com.formatDate(ica_bid_start_ymd.getValue(), "dash")+" "+ibx_bid_start_hour.getValue()+":"+ibx_bid_start_min.getValue()+"\n"+
					"입찰서 접수마감일시 : "+com.formatDate(ica_bid_end_ymd.getValue(), "dash")+" "+ibx_bid_end_hour.getValue()+":"+ibx_bid_end_min.getValue()+"\n"+
					"개찰일시 : "+com.formatDate(ica_open_ymd.getValue(), "dash") +" "+ibx_open_hour.getValue()+":"+ibx_open_min.getValue()+"\n";
		}

		com.confirm(msg + "\n" + "진행하시겠습니까?", function(ret){
			if(ret){
				scwin.save();
			}
		});		
	};

	/*
	* 입찰설명회여부 viewchange 이벤트
	*/
	scwin.sbx_bid_expl_yn_onviewchange = function(info) {		
		if (sbx_bid_expl_yn.getValue() == "1" || sbx_bid_expl_yn.getValue() == "") {
			ibx_bid_expl_loc.setValue("");
			ica_bid_expl_ymd.setValue("");
			ibx_bid_expl_hour.setValue("");
			ibx_bid_expl_min.setValue("");			
		}
	};

	/*
	* 입찰설명회여부 change 이벤트
	*/
	scwin.sbx_bid_expl_yn_onchange = function(info) {
		if (sbx_bid_expl_yn.getValue() == "1" || sbx_bid_expl_yn.getValue() == "") {			
			grp_bid_expl.hide();
		}else{			
			grp_bid_expl.show("");
		}
	};


	/*
	* 단일예가 온라인여부 viewchange 이벤트
	*/
	scwin.rdo_esti_online_yn_onviewchange = function(info) {
		if (this.getValue() == "N") {
			alert("예정가격 조서를 오프라인으로 선택했습니다.");
		}
	};

	/*
