	/*
	* 견적요청서 사용구분 변경 이벤트
	*/
	scwin.rdo_oz_chk_onviewchange = function(info) {
		if(this.getValue() == "1"){
			ta_ozText.setDisabled(false);
		}else{
			ta_ozText.setDisabled(true);
		}
	};

	/*
     * 결재신청 버튼 클릭 이벤트
     */
	scwin.btn_apr_onclick = function(e) {
		if(dma_ord.get("bid_ord_no") == ""){
			alert("발주계획 작성이 아직 되지 않았습니다.");
			return;
		}

		dma_apr.set("bid_clsf_cd", sbx_bid_clsf_cd.getText());
		dma_apr.set("contrct_method", sbx_contrct_method.getText()); // 계약방법(1)
		dma_apr.set("bid_succ_method", sbx_bid_succ_method.getText()); // 계약방법(2)
		dma_apr.set("bid_succ_type", sbx_bid_succ_type.getText()); // 입찰방식
		dma_apr.set("rebid_possible_yn", rdo_rebid_possible_yn.getText()); // 재입찰가능여부
		dma_apr.set("succ_type", sbx_succ_type.getText()); // 낙찰방법
		dma_apr.set("join_possible_yn", sbx_join_possible_yn.getText()); // 공동수급협정서 접수 및 구성방식
		dma_apr.set("join_method", sbx_join_method.getText()); // 공동수급협정서 접수 및 구성방식(2)
		dma_apr.set("same_suc_lot_program_yn", rdo_same_suc_lot_program_yn.getText()); // 동가입찰 낙찰자 자동추첨프로그램	
		dma_apr.set("limit_loc", sbx_limit_loc.getText());
		dma_apr.set("tender_yn", rdo_tender_yn.getText());
		dma_apr.set("limit_category", sbx_limit_category.getText());
		dma_apr.set("item_limit_yn", sbx_item_limit_yn.getText()); // 물품분류제한여부	
		dma_apr.set("join_mem_loc_limit_yn", sbx_join_mem_loc_limit_yn.getText()); // 공동수급체 구성원 지역제한적용여부	
		dma_apr.set("multi_esti_pr_yn", rdo_multi_esti_pr_yn.getText());
		dma_apr.set("emer_bid_yn", rdo_emer_bid_yn.getText()); // 긴급입찰여부
		dma_apr.set("pq_eval_yn", rdo_pq_eval_yn.getText()); // PQ심사여부
		dma_apr.set("bid_expl_yn", sbx_bid_expl_yn.getText()); // 입찰설명회 여부
		if(sbx_pq_eval_rcp_type.getValue() != ""){
			dma_apr.set("pq_eval_rcp_type", sbx_pq_eval_rcp_type.getText()); // PQ심사신청서 접수방식
		}
		if(scwin.data.pur_grp_cd == "EGF030"){
			dma_apr.set("his_bid_yn", sbx_his_bid_yn.getText()); // 내역입찰여부
			dma_apr.set("fit_eval_judg", sbx_fit_eval_judg.getText()); // 적격심사평가기준
			dma_apr.set("bid_part_limit_yn", sbx_bid_part_limit_yn.getText()); // 입찰참가제한여부
		}


		dma_ord.set("approval", "true");

		if(!scwin.pre_save()) return;

		scwin.save();		
	};

	/*
     * 견적요청서 버튼 클릭 이벤트
     */
	scwin.btn_print_01_onclick = function(e) {
		if(rdo_oz_chk.getValue() == "1" && ta_ozText.getValue() == ""){
			com.alert("수정할 문구를 작성되어 있지 않습니다.");
			return;
		}

		// /epu/ord/EpuOrdPlanGoods.ozr
		var node = "BidOrdNo="+spn_bid_ord_no.getValue()
				 +"&ChgNo="+spn_chg_no.getValue()
				 +"&UseTextChk="+rdo_oz_chk.getValue()
				 +"&UseInsertText="+ta_ozText.getValue();

		// com.oz.open 사용
		com.oz.open({ // reportArr
			ozrCode: "P033",    
			param: node			
		}, { // option
			filename : "견적요청서.pdf"
		});	 
	};

	/*
     * 수의계약체결제한여부확인서 버튼 클릭 이벤트
     */
	scwin.btn_print_02_onclick = function(e) {
		// EpuOrdPlanConfirmation.ozr
		var node = "BidOrdNo="+spn_bid_ord_no.getValue()
					+"&ChgNo="+spn_chg_no.getValue()
					+"&UseTextChk="+rdo_oz_chk.getValue()
					+"&UseInsertText="+ta_ozText.getValue()
					+"&ordNm="+ibx_bid_title.getValue();
		// com.oz.open 사용
		com.oz.open({ // reportArr
			ozrCode: "P034",    
			param: node			
		}, { // option
			filename : "수의계약체결제한여부확인서.pdf"
		});
	};

	/*
     * 입찰방식 변경 이벤트
     */	
	scwin.sbx_bid_succ_type_onchange = function(info) {
		scwin.setFormVendByConMethod();

		if(/4101|4201/.test(sbx_bid_succ_method.getValue()) && sbx_bid_succ_type.getValue() == "00"){ // 소액수의-직찰일 경우 
			scwin.setFormBidClsf();
		}
	};

	/*
     * 발주품목추가 버튼 클릭 이벤트
     */
	scwin.btn_item_add_onclick = function(e) {		
		var data={
			pur_grp_cd : dma_ord.get("pur_clsf_cd"),
			inout_psn_clsf_cd : dma_ord.get("inout_psn_clsf_cd")
		};

		biz.epu.pop.open(biz.epu.pop.TYPE.EPU_REQ_ADD, data, function(ret){
			var rowCnt = dlt_item.getRowCount();
			var cnt = 0;
			for(var i=0; i<ret.length; i++){
				var rowData = ret[i];
				rowData.delvr_demnd_daycnt = "계약일로부터 " + rowData.delvr_demnd_daycnt + "일 이내";
				rowData.cud_type = "C";
				if(rowCnt == 0){
					dlt_item.setRowJSON(i, rowData);
				}else{
					if(scwin.checkDupData(rowData)){
						dlt_item.setRowJSON(rowCnt+cnt, rowData);
						cnt++;
					}
				}
			}

			scwin.setStylePurItem("new_add");		

			scwin.setExeGuar(); // 하자보증 초기값 설정
		});
	};


	/*
    * 낙찰방법 변경 이벤트
    */
	scwin.sbx_succ_type_onviewchange = function(info) {
		ibx_succ_min_rate.setDisabled(false);

		if(rdo_succ_pivot.getValue() == "1"){ // 조달청기준		
			var val = scwin.getSuccMinRate(info.newValue);

			ibx_succ_min_rate.setValue(val);
		}else{
			ibx_succ_min_rate.setValue("");

			if(scwin.data.pur_grp_cd == "EGF010"){
				if(info.newValue == "1"){
					ibx_succ_min_rate.setDisabled(true);
				}
			}else if(scwin.data.pur_grp_cd == "EGF020"){ // 용역인경우
				if(info.newValue == "1"){
					ibx_succ_min_rate.setDisabled(true);
				}else if(info.newValue == "21"){ 
					ibx_succ_min_rate.setDisabled(false);
				}
			}else if(scwin.data.pur_grp_cd == "EGF030"){ // 공사인경우
				if(info.newValue == "20"){ // 20 최저가낙찰제 
					ibx_succ_min_rate.setValue(87.745);
				}
			}
		}
	};

	/*
    * 지명업체 추가 버튼 클릭 이벤트
    */
	scwin.btn_vend_add_onclick = function(e) {
		biz.epu.pop.open(biz.epu.pop.TYPE.EPU_BID_G2B, {}, function(ret) {
			var rowCnt = dlt_bid_vend.getRowCount();
			var cnt = 0;
			for(var i=0; i<ret.length; i++){
				var rowData = ret[i];
				rowData.cud_type = "C";
				if(rowCnt == 0){
					dlt_bid_vend.setRowJSON(i, rowData);
				}else{
					if(scwin.checkVendAdd(rowData)){
						dlt_bid_vend.setRowJSON(rowCnt+cnt, rowData);
						cnt++;
					}
				}
			}
		});
	};

	/*
    * 지명업체 삭제 버튼 클릭 이벤트
    */
	scwin.btn_vend_del_onclick = function(e) {
		var row = grd_vend.getFocusedRowIndex();
		if (row < 0) {
			alert("삭제할 행을 선택하여 주십시오.");
			return;
		}

		dlt_bid_vend.removeRow(row);
	};

